INSERT INTO `pages` (`id`, `author_id`, `summary`, `title`, `content`, `created_at`, `updated_at`, `deleted_at`, `slug`) VALUES
(2, 9, 'Unimart là nền tảng thương mại điện tử hàng đầu tại Đông Nam Á và Đài Loan.', 'Giới thiệu', '<p class=\"topic-intro-parag\">Unimart l&agrave; nền tảng thương mại điện tử h&agrave;ng đầu tại Đ&ocirc;ng Nam &Aacute; v&agrave; Đ&agrave;i Loan.</p>\r\n<p class=\"topic-intro-parag\">Ra mắt năm 2015, nền tảng thương mại Shopee được x&acirc;y dựng nhằm cung cấp cho người d&ugrave;ng những trải nghiệm dễ d&agrave;ng, an to&agrave;n v&agrave; nhanh ch&oacute;ng khi mua sắm trực tuyến th&ocirc;ng qua hệ thống hỗ trợ thanh to&aacute;n v&agrave; vận h&agrave;nh vững mạnh.</p>\r\n<p class=\"topic-intro-parag\">Ch&uacute;ng t&ocirc;i c&oacute; niềm tin mạnh mẽ rằng trải nghiệm mua sắm trực tuyến phải đơn giản, dễ d&agrave;ng v&agrave; mang đến cảm x&uacute;c vui th&iacute;ch. Niềm tin n&agrave;y truyền cảm hứng v&agrave; th&uacute;c đẩy ch&uacute;ng t&ocirc;i mỗi ng&agrave;y tại Shopee.</p>', '2022-07-12 23:55:36', '2022-08-05 00:27:19', NULL, 'gioi-thieu'),
(4, 16, 'Shopee rất trân trọng các bạn nhân viên và cố gắng hỗ trợ tốt nhất cho họ. Chúng tôi liên tục tạo cơ hội cho nhân viên phát triển và nâng cao bản thân. Chúng tôi cũng cung cấp một môi trường làm việc nhiệt huyết để thúc đẩy văn hóa làm việc vui vẻ và hợp tác trong Shopee! Quan trọng nhất, chúng tôi đảm bảo rằng nhân viên của chúng tôi có sức khỏe tốt, được chăm sóc tốt.', 'Tại sao chọn Unimart', '<h3 class=\"h3\">Tại sao n&ecirc;n gia nhập Shopee?</h3>\r\n<p>Shopee rất tr&acirc;n trọng c&aacute;c bạn nh&acirc;n vi&ecirc;n v&agrave; cố gắng hỗ trợ tốt nhất cho họ. Ch&uacute;ng t&ocirc;i li&ecirc;n tục tạo cơ hội cho nh&acirc;n vi&ecirc;n ph&aacute;t triển v&agrave; n&acirc;ng cao bản th&acirc;n. Ch&uacute;ng t&ocirc;i cũng cung cấp một m&ocirc;i trường l&agrave;m việc nhiệt huyết để th&uacute;c đẩy văn h&oacute;a l&agrave;m việc vui vẻ v&agrave; hợp t&aacute;c trong Shopee! Quan trọng nhất, ch&uacute;ng t&ocirc;i đảm bảo rằng nh&acirc;n vi&ecirc;n của ch&uacute;ng t&ocirc;i c&oacute; sức khỏe tốt, được chăm s&oacute;c tốt.</p>\r\n<h3 class=\"main-title h3\">Cơ hội học tập v&agrave; ph&aacute;t triển</h3>\r\n<p>Shopee Academy tự h&agrave;o khi hợp t&aacute;c chặt chẽ với tất cả c&aacute;c nh&agrave; l&atilde;nh đạo, c&aacute;c nh&oacute;m v&agrave; c&aacute; nh&acirc;n trong Shopee để c&ugrave;ng tạo ra c&aacute;c giải ph&aacute;p học tập ph&ugrave; hợp với họ. Ch&uacute;ng t&ocirc;i muốn nh&acirc;n vi&ecirc;n ph&aacute;t triển th&agrave;nh phi&ecirc;n bản tốt hơn của mỗi người<br />Ch&uacute;ng t&ocirc;i mong muốn trở th&agrave;nh phi&ecirc;n bản tốt hơn của ch&iacute;nh m&igrave;nh. Ch&uacute;ng t&ocirc;i sẽ c&oacute; những lớp đ&agrave;o tạo chức năng v&agrave; l&atilde;nh đạo để phục vụ cho c&aacute;c nh&oacute;m nh&acirc;n vi&ecirc;n kh&aacute;c nhau.</p>\r\n<h3 class=\"main-title h3\">Sự kiện gắn kết nh&acirc;n vi&ecirc;n</h3>\r\n<p>Tại Shopee, ch&uacute;ng t&ocirc;i l&agrave;m việc hết sức, chơi hết m&igrave;nh. Xuy&ecirc;n suốt trong năm, ch&uacute;ng t&ocirc;i kỷ niệm c&aacute;c sự kiện quan trọng v&agrave; tổ chức c&aacute;c hoạt động nội bộ vui nhộn để giữ kết nối v&agrave; tương t&aacute;c với nh&acirc;n vi&ecirc;n của m&igrave;nh. H&atilde;y đến v&agrave; chung vui với Shopee nh&eacute;!</p>\r\n<p>&nbsp;</p>', '2022-07-13 00:25:03', '2022-08-05 00:27:19', NULL, 'tai-sao-chon-unimart'),
(5, 17, 'Thông tin tuyển dụng', 'Tuyển dụng', '<p><img src=\"https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/c3f6761d-b43b-0100-ccc4-0014a02421bd.jpg\" alt=\"\" width=\"1140\" height=\"1211\" /></p>\r\n<p><img src=\"https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/824fdc84-24e0-0200-3cea-0014a0246e72.jpg\" alt=\"\" width=\"1140\" height=\"587\" /></p>\r\n<p><img src=\"https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/a38811da-4a88-0300-fc14-0014a0246e74.jpg\" alt=\"\" width=\"1140\" height=\"508\" /></p>\r\n<p><img src=\"https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/d3e5d692-102b-0400-f7ec-0014a0246e76 (1).jpg\" alt=\"\" width=\"1140\" height=\"593\" /></p>\r\n<p><img src=\"https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/a534df47-a476-0500-7e91-0014a0246e78.jpg\" alt=\"\" width=\"1140\" height=\"387\" /></p>', '2022-07-13 00:37:41', '2022-08-05 00:27:19', NULL, 'tuyen-dung');

