INSERT INTO `products` (`id`, `user_id`, `cat_id`, `name`, `thumbnail`, `content`, `summary`, `description`, `qty`, `price`, `old_price`, `created_at`, `updated_at`, `deleted_at`, `slug`, `num_views`, `num_purchases`, `tags`) VALUES
(5, 9, 27, 'Motorola One Fusion Plus', 'https://cdn.tgdd.vn/Products/Images/42/223292/motorola-one-fusion-600x600.jpg', '<p>Th&ocirc;ng tin sản phẩm Motorola One Fusion Plus&nbsp;</p>\r\n<h3 class=\"a-spacing-mini\">Motorola One Fusion</h3>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://m.media-amazon.com/images/S/aplus-media/sota/4cad5e1d-30d9-4666-9d90-daf8ae5a31c2.__CR184,142,458,458_PT0_SX300_V1___.jpg\" alt=\"\" width=\"300\" height=\"300\" /></p>\r\n<p id=\"tw-target-text\" class=\"tw-data-text tw-text-large tw-ta\" dir=\"ltr\" data-placeholder=\"Bản dịch\"><span class=\"Y2IQFc\" lang=\"vi\">Điện thoại th&ocirc;ng minh Motorola One Fusion đi k&egrave;m với m&agrave;n h&igrave;nh 6,5 inch full HD. Điều n&agrave;y mang lại trải nghiệm h&igrave;nh ảnh r&otilde; r&agrave;ng v&agrave; sống động tr&ecirc;n điện thoại của bạn. Camera sau 64 MP + 8 MP + 5 MP + 2 MP đi k&egrave;m đ&egrave;n flash. N&oacute; cũng đi k&egrave;m với một camera trước 16 MP rất hữu &iacute;ch để ghi lại những kỷ niệm m&agrave; bạn c&oacute; thể lưu giữ với ch&iacute;nh m&igrave;nh. Điện thoại cũng đi k&egrave;m với bộ nhớ trong 128 GB m&agrave; bạn c&oacute; thể sử dụng để lưu trữ c&aacute;c tệp v&agrave; t&agrave;i liệu đa phương tiện của m&igrave;nh.</span></p>\r\n<h3 class=\"a-spacing-mini\">Vi&ecirc;n pin dung lượng l&ecirc;n đến 5000 mAh</h3>\r\n<p style=\"text-align: center;\"><img src=\"https://m.media-amazon.com/images/S/aplus-media/sota/af148301-22d1-4d20-9b69-d4ccfc060fa3.__CR0,41,1688,1688_PT0_SX300_V1___.jpg\" alt=\"\" width=\"300\" height=\"300\" /></p>\r\n<p class=\"tw-data-text tw-text-large tw-ta\" dir=\"ltr\" data-placeholder=\"Bản dịch\">Điện thoại th&ocirc;ng minh Motorola One Fusion đi k&egrave;m với pin 5000 mAh cho thời gian chờ l&acirc;u hơn. Gọi nhiều hơn, nhấp nhiều hơn v&agrave; chơi nhiều hơn với thời lượng pin k&eacute;o d&agrave;i cho điện thoại th&ocirc;ng minh của bạn.</p>\r\n<h3 class=\"a-spacing-mini\">Được trang bị phần cứng mạnh mẽ</h3>\r\n<p style=\"text-align: center;\"><img src=\"https://m.media-amazon.com/images/S/aplus-media/sota/38f7768d-40ce-476d-8df9-b616cafa9b70.__CR261,304,1246,1246_PT0_SX300_V1___.jpg\" alt=\"\" width=\"300\" height=\"300\" /></p>\r\n<p class=\"tw-data-text tw-text-large tw-ta\" dir=\"ltr\" data-placeholder=\"Bản dịch\">Được trang bị bộ vi xử l&yacute; Snapdragon 730G v&agrave; RAM 4 GB, điện thoại th&ocirc;ng minh Motorola One Macro mang đến sức mạnh xử l&yacute; mạnh mẽ hơn khi bạn tham gia v&agrave;o c&aacute;c tr&ograve; chơi trực quan cường độ cao hoặc di chuyển giữa c&aacute;c ứng dụng tr&ecirc;n điện thoại của m&igrave;nh.</p>', 'Thông tin sản phẩm Motorola One Fusion Plus', 'Màn hình: IPS LCD6.5\"Full HD+\r\nHệ điều hành: Android 10\r\nCamera sau: Chính 64 MP & Phụ 8 MP, 5 MP, 2 MP\r\nCamera trước: 16 MP\r\nChip: Snapdragon 730\r\nRAM: 6 GB\r\nBộ nhớ trong: 128 GB', 100, 15000000, 17000000, '2022-07-23 03:14:13', '2022-08-05 14:26:16', NULL, 'motorola-one-fusion-plus', 24, 3, 'Sản phẩm,Điện thoại, Điện thoại tầm trung,motorola,Snapdragon 730G'),
(6, 9, 13, 'Samsung Galaxy S8 plus', 'https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus-hh-600x600-600x600.jpg', '<h3>Trải nghiệm m&agrave;n h&igrave;nh lớn, chứ kh&ocirc;ng phải l&agrave; một thiết bị k&iacute;ch thước lớn</h3>\r\n<p>Năm nay, c&aacute;c nh&agrave; sản xuất H&agrave;n Quốc như LG v&agrave; Samsung đ&atilde; đi ti&ecirc;n phong với m&agrave;n h&igrave;nh tỉ lệ 18:9 hay 18,5:9, tối ưu hết mức viền m&agrave;n h&igrave;nh. V&agrave; <a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng điện thoại tại Thegioididong.com\">điện thoại</a>&nbsp;S8 Plus cũng vậy, người d&ugrave;ng sẽ c&oacute; một trải nghiệm m&agrave;n h&igrave;nh lớn chứ kh&ocirc;ng phải l&agrave; một thiết bị k&iacute;ch thước lớn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus-man-hinh.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh của điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus-man-hinh.jpg\" alt=\"M&agrave;n h&igrave;nh của điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus-man-hinh.jpg\" /></a></p>\r\n<p>Kh&ocirc;ng những mặt trước bo cong m&agrave; mặt k&iacute;nh sau cũng mềm mại, &ocirc;m gọn trong b&agrave;n tay. Cảm gi&aacute;c cầm nắm S8 Plus thật sự rất dễ chịu, dường như kh&ocirc;ng thấy ch&uacute;t cấn n&agrave;o ngay cả với chi tiết nhỏ nhất. Tuy nhi&ecirc;n, k&iacute;ch thước 6.2 inch khiến cho việc d&ugrave;ng v&acirc;n tay của m&igrave;nh kh&aacute; l&agrave; kh&oacute; khăn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0203_1280x720-800-resize.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Mặt lưng của điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0203_1280x720-800-resize.jpg\" alt=\"Mặt lưng của điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0203_1280x720-800-resize.jpg\" /></a></p>\r\n<p>Trong qu&aacute; tr&igrave;nh sử dụng, đ&ocirc;i khi m&igrave;nh hay ấn nhầm n&uacute;t volume v&agrave; Bixby trong những ng&agrave;y đầu, do Samsung thiết kế vị tr&iacute; kh&aacute; gần nhau.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_1839_1280x720-800-resize.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế của điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_1839_1280x720-800-resize.jpg\" alt=\"Thiết kế của điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_1839_1280x720-800-resize.jpg\" /></a></p>\r\n<p>&nbsp;</p>\r\n<div class=\"ratingbox\">\r\n<div class=\"item\">\r\n<p><strong>❝</strong>Tuyệt vời qu&aacute; Samsung ơi!!! Mua m&aacute;y n&agrave;y về lũ bạn trầm trồ qu&aacute; trời. M&aacute;y đẹp, sang chảnh, camera qu&aacute; ngon, bảo mật mống mắt rất nhạy, c&oacute; điều hơi dễ tuột khỏi tay nhưng m&igrave;nh đ&atilde; mua th&ecirc;m ốp nhựa dẻo n&ecirc;n chẳng sao cả.<strong>❞</strong></p>\r\n<label class=\"line\"></label><span class=\"info\"><strong>Ngọc D&acirc;n</strong> - Q1, Tp HCM<label class=\"sttB\">Đ&atilde; mua tại Thế giới di động</label></span></div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<h3><a title=\"Tham khảo điện thoại m&agrave;n h&igrave;nh tr&agrave;n viền tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd?f=man-hinh-tran-vien\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo điện thoại m&agrave;n h&igrave;nh tr&agrave;n viền tại Thegioididong.com\">M&agrave;n h&igrave;nh v&ocirc; cực</a>, trải nghiệm thật đ&atilde;</h3>\r\n<p>Galaxy S8 Plus được trang bị m&agrave;n h&igrave;nh 6.2 inch độ ph&acirc;n giải 2K+, tỉ lệ 18,5:9 tr&ecirc;n tấm nền Super AMOLED. Với nhiều c&ocirc;ng nghệ đi k&egrave;m đặc biệt l&agrave; HDR10 khiến cho g&oacute;c nh&igrave;n rộng, độ s&aacute;ng cao, m&agrave;u đen s&acirc;u c&ugrave;ng tương phản m&agrave;u sắc đỉnh, kh&ocirc;ng c&oacute; g&igrave; để ph&agrave;n n&agrave;n về m&agrave;n h&igrave;nh tr&ecirc;n S8 Plus.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0206_1280x720-800-resize.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh của điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0206_1280x720-800-resize.jpg\" alt=\"M&agrave;n h&igrave;nh của điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0206_1280x720-800-resize.jpg\" /></a></p>\r\n<p>Việc thay thế n&uacute;t home vật l&yacute; bằng ph&iacute;m điều hướng m&agrave;n h&igrave;nh tr&ecirc;n S8 Plus m&igrave;nh thấy b&igrave;nh thường kh&ocirc;ng g&acirc;y kh&oacute; &nbsp;khăn g&igrave; trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0207_1280x720-800-resize.jpg\"><img class=\" lazyloaded\" title=\"Ph&iacute;m điều hướng tr&ecirc;n điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0207_1280x720-800-resize.jpg\" alt=\"Ph&iacute;m điều hướng tr&ecirc;n điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0207_1280x720-800-resize.jpg\" /></a></p>\r\n<p>Nếu ngại m&agrave;n h&igrave;nh to kh&oacute; thao t&aacute;c ngo&agrave;i đường, kh&ocirc;ng sao, Galaxy S8 Plus vẫn hỗ trợ t&iacute;nh năng m&agrave;n h&igrave;nh nhỏ để thao t&aacute;c một tay.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0214_1280x720-800-resize.jpg\"><img class=\" lazyloaded\" title=\"Giao diện điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0214_1280x720-800-resize.jpg\" alt=\"Giao diện điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0214_1280x720-800-resize.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, Samsung cũng cho ph&eacute;p t&ugrave;y biến thanh điều hướng với việc thay đổi vị tr&iacute;, m&agrave;u sắc cũng như tốc độ phản hồi n&uacute;t home.</p>\r\n<p>&nbsp;</p>\r\n<div class=\"ratingbox\">\r\n<div class=\"item\">\r\n<p><strong>❝</strong>M&aacute;y tuyệt đẹp cả giao diện ngoại thất lẫn nội thất. Chụp ảnh selfie th&igrave; khỏi b&agrave;n :) Đẹp theo kiểu ch&acirc;n thật chứ ko ảo. Pin x&agrave;i theo m&igrave;nh c&ograve;n kh&aacute; hạn chế nhưng c&ocirc;ng nghệ sạc nhanh đ&uacute;ng l&agrave; sạc cực k&igrave; nhanh.&nbsp;<strong>❞</strong></p>\r\n<label class=\"line\"></label><span class=\"info\"><strong>Nguyễn Quang Tr&iacute;</strong> - Q.9, TP.HCM<label class=\"sttB\">Đ&atilde; mua tại Thế giới di động</label></span></div>\r\n</div>\r\n<p>&nbsp;</p>\r\n<h3>Camera cải thiện, nhưng Samsung kh&ocirc;ng hề n&oacute;i ra</h3>\r\n<p>Nếu nh&igrave;n sơ qua, Galaxy S8 hay S8 Plus đều kh&ocirc;ng qu&aacute; kh&aacute;c biệt với người tiền nhiệm S7. Vẫn l&agrave; độ ph&acirc;n giải 12 MP, khẩu độ F/1.7, c&ocirc;ng nghệ lấy n&eacute;t Dual-pixel qu&aacute; nhanh qu&aacute; nguy hiểm c&ugrave;ng k&iacute;ch thước cảm biến lớn 1.4 micromet.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0218_1280x720-800-resize.jpg\"><img class=\" lazyloaded\" title=\"Giao diện camera tr&ecirc;n điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0218_1280x720-800-resize.jpg\" alt=\"Giao diện camera tr&ecirc;n điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Files/2017/05/01/978135/img_0218_1280x720-800-resize.jpg\" /></a></p>\r\n<p>Tuy nhi&ecirc;n, sự n&acirc;ng cấp của Samsung kh&aacute; &acirc;m thầm v&agrave; chủ yếu l&agrave; chất lượng thấu k&iacute;nh. M&igrave;nh rất h&agrave;i l&ograve;ng về camera tr&ecirc;n Galaxy S8 Plus, với m&igrave;nh n&oacute; xứng đ&aacute;ng l&agrave; một chiếc camera-phone auto xếp h&agrave;ng top cao.</p>\r\n<p>C&aacute;c bạn c&oacute; thể xem một v&agrave;i h&igrave;nh ảnh m&igrave;nh chụp từ Galaxy S8 Plus:</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus2-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus2-1.jpg\" alt=\"Ảnh chụp từ điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus2-1.jpg\" /></a><em>Ảnh cận cảnh trong ph&ograve;ng</em></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus3-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus3-1.jpg\" alt=\"Ảnh chụp từ điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus3-1.jpg\" /></a><em>M&agrave;u sắc gần s&aacute;t với thực tế</em></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus1-2.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ điện thoại Samsung Galaxy S8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus1-2.jpg\" alt=\"Ảnh chụp từ điện thoại Samsung Galaxy S8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/91131/samsung-galaxy-s8-plus1-2.jpg\" /></a></p>\r\n<p>Thuật to&aacute;n xử l&yacute; trong nhiều t&igrave;nh huống đều tương đồng người tiền nhiệm Galaxy S7, tuy nhi&ecirc;n sự kh&aacute;c nhau đến từ chế độ HDR mang lại một bức ảnh c&oacute; dải nhạy s&aacute;ng tuyệt vời. Chưa kể việc flare t&iacute;m hay thấy khi chụp c&oacute; tia đ&egrave;n tr&ecirc;n camera Galaxy S7 hay d&ograve;ng A 2017, đ&atilde; ho&agrave;n to&agrave;n được khắc phục tr&ecirc;n cảm biến S5K2L2 của Galaxy S8 Plus.</p>', 'Galaxy S8 và Galaxy S8 Plus hiện đang là chuẩn mực smartphone về thiết kế trong tương lai. Sau nhiều ngày được sử dụng, mình xin chia sẻ những cảm nhận chi tiết nhất về chiếc Galaxy S8 Plus - chiếc điện thoại Samsung đang có doanh số đặt hàng khủng nhất hiện tại.', 'Màn hình: Super AMOLED6.2\"Quad HD+ (2K+)\r\nHệ điều hành: Android 7.0 (Nougat)\r\nCamera sau: 12 MP\r\nCamera trước: 8 MP\r\nChip: Exynos 8895\r\nRAM: 4 GB\r\nBộ nhớ trong: 64 GB', 100, 20000000, 21000000, '2022-07-24 21:14:21', '2022-08-05 14:17:02', NULL, 'samsung-galaxy-s8-plus', 7, 0, 'Sản phẩm,samsung galaxy s8,Điện thoại sam sung, s8'),
(7, 18, 8, 'Iphone 8 plus', 'https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-hh-600x600-600x600.jpg', '<h3>Thiết kế từ <a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại thiết kế kim loại v&agrave; k&iacute;nh\" href=\"https://www.thegioididong.com/dtdd?g=kim-loai-va-kinh-cuong-luc\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng điện thoại thiết kế kim loại v&agrave; k&iacute;nh\">k&iacute;nh v&agrave; kim loại</a></h3>\r\n<p><a title=\"Tham khảo c&aacute;c d&ograve;ng smartphone tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng smartphone tại Thegioididong.com\">Smartphone</a>&nbsp;iPhone 8 Plus&nbsp;giữ nguy&ecirc;n ho&agrave;n to&agrave;n những đường n&eacute;t thiết kế đ&atilde; ho&agrave;n thiện từ thế hệ trước nhưng sử dụng phong c&aacute;ch 2 mặt k&iacute;nh cường lực kết hợp bộ khung kim loại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h7-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" /></a></p>\r\n<p>Mặt lưng k&iacute;nh bo cong 2.5D thời thượng, khung kim loại được gia cố cứng c&aacute;p, bền bỉ hơn với 7 lớp xử l&yacute; m&agrave;u sơn, hạn chế bong tr&oacute;c.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h9.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h9.jpg\" alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h9.jpg\" /></a></p>\r\n<p>Thay đổi n&agrave;y đồng thời loại bỏ chi tiết đường anten thừa ở mặt lưng như c&aacute;c thế hệ trước, mang đến vẻ đẹp ho&agrave;n hảo hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h0.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h0.jpg\" alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h0.jpg\" /></a></p>\r\n<h3>Camera k&eacute;p chuy&ecirc;n nghiệp</h3>\r\n<p>Kh&ocirc;ng c&oacute; sự thay đổi trong th&ocirc;ng số camera (vẫn l&agrave; cụm camera k&eacute;p c&ugrave;ng độ ph&acirc;n giải 12 MP so với chiếc điện thoại IPhone 7 Plus),&nbsp;chiếc&nbsp;<a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại Apple iPhone tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng điện thoại Apple iPhone tại Thegioididong.com\">điện thoại iPhone</a>&nbsp;n&agrave;y chủ yếu được tập trung n&acirc;ng cấp về ống k&iacute;nh, cảm biến, vi xử l&yacute; h&igrave;nh ảnh để n&acirc;ng cao chất lượng ảnh chụp.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Ảnh chụp bằng camera k&eacute;p tr&ecirc;n điện thoại iPhone 8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-1.jpg\" alt=\"Ảnh chụp bằng camera k&eacute;p tr&ecirc;n điện thoại iPhone 8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-1.jpg\" /></a></p>\r\n<p>Khả năng zoom quang 2X kh&ocirc;ng thay đổi chất lượng cũng như x&oacute;a ph&ocirc;ng ch&acirc;n dung vẫn được t&iacute;ch hợp tr&ecirc;n iPhone 8 Plus c&ugrave;ng với&nbsp;<a title=\"T&igrave;m hiểu t&iacute;nh năng PORTRAIT LIGHTING\" href=\"https://www.thegioididong.com/tin-tuc/post-1029635\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu t&iacute;nh năng PORTRAIT LIGHTING\">Portrait Lighting</a>, t&iacute;nh năng chụp ảnh s&acirc;n khấu ho&agrave;n to&agrave;n mới.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h5-1.jpg\"><img class=\" lazyloaded\" title=\"Camera tr&ecirc;n điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h5-1.jpg\" alt=\"Camera tr&ecirc;n điện thoại iPhone 8 Plus 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h5-1.jpg\" /></a></p>\r\n<p>Camera trước vẫn l&agrave; độ ph&acirc;n giải 7 MP c&ugrave;ng t&iacute;nh năng chụp ch&acirc;n dung &aacute;nh s&aacute;ng s&acirc;n khấu đặc biệt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-2.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp bằng camera trước tr&ecirc;n điện thoại Iphone 8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-2.jpg\" alt=\"Ảnh chụp bằng camera trước tr&ecirc;n điện thoại Iphone 8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-2.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Đ&aacute;nh gi&aacute; camera iPhone 8 Plus\" href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-camera-apple-iphone-8-plus-1192730\" target=\"_blank\" rel=\"noopener\" type=\"Đ&aacute;nh gi&aacute; camera iPhone 8 Plus\">Đ&aacute;nh gi&aacute; camera iPhone 8 Plus: Vẫn rất tốt d&ugrave; chỉ 2 camera</a></p>\r\n<h3>Chip Apple A11 c&ugrave;ng với thực tế ảo AR</h3>\r\n<p>Một trong những điểm nhất quan trọng nhất ở thế hệ <a title=\"Tham khảo điện thoại iPhone đang kinh doanh tại TopZone thương hiệu của Thế Giới Di Động\" href=\"https://www.topzone.vn/iphone\" target=\"_blank\" rel=\"noopener\">Apple iPhone</a> 8 Plus ch&iacute;nh l&agrave; sức mạnh phần cứng, con chip <a title=\"T&igrave;m hiểu chip Apple A11 tr&ecirc;n điện thoại iPhone\" href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-a11-bionic-chip-co-nhieu-thanh-phan-apple-tu-trong-nhat-tu-truoc-den-nay-1021653\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu chip Apple A11 tr&ecirc;n điện thoại iPhone\">Apple A11</a>&nbsp;như tr&ecirc;n <a title=\"Chi tiết điện thoại iPhone X\" href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" rel=\"noopener\" type=\"Chi tiết điện thoại iPhone X\">iPhone X</a>&nbsp;cho hiệu năng vượt trội hơn rất nhiều so với trước đ&acirc;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-3.jpg\"><img class=\" lazyloaded\" title=\"Điểm Antutu Benchmark tr&ecirc;n iPhone 8 Plus\" src=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-3.jpg\" alt=\"Điểm Antutu Benchmark tr&ecirc;n iPhone 8 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114110/iphone-8-plus-tgdd-3.jpg\" /></a></p>\r\n<p>Vi xử l&yacute; Apple A11 c&ograve;n đặc biệt tối ưu cho 3D v&agrave; thực tế ảo tăng cường AR, một trải nghiệm ho&agrave;n to&agrave;n mới mẻ tr&ecirc;n smartphone.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\"><img class=\" lazyloaded\" title=\"T&iacute;nh năng thực tế ảo tr&ecirc;n điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\" alt=\"T&iacute;nh năng thực tế ảo tr&ecirc;n điện thoại iPhone 8 Plus 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h3.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh rộng ho&agrave;n hảo</h3>\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh vẫn l&agrave; 5.5 inch, giữ nguy&ecirc;n độ ph&acirc;n giải Retina HD sắc n&eacute;t nhưng thế hệ iPhone 8 Plus lại c&oacute; th&ecirc;m những cải tiến đ&aacute;ng gi&aacute; hơn, về độ phủ m&agrave;u lớn hiển thị m&agrave;u sắc ch&iacute;nh x&aacute;c hay c&ocirc;ng nghệ <a title=\"T&igrave;m hiểu c&ocirc;ng ngh&ecirc; True Tone\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu c&ocirc;ng ngh&ecirc; True Tone\">True Tone</a> từ iPad Pro 2017.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\" alt=\"M&agrave;n h&igrave;nh điện thoại iPhone 8 Plus 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb2-1.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Đ&aacute;nh gi&aacute; chi tiết iPhone 8 Plus\" href=\"https://www.thegioididong.com/tin-tuc/danh-gia-iphone-8-plus-sau-mot-thoi-gian-su-dung-1192073\" target=\"_blank\" rel=\"noopener\" type=\"Đ&aacute;nh gi&aacute; chi tiết iPhone 8 Plus\">(2019) Đ&aacute;nh gi&aacute; chi tiết iPhone 8 Plus: Thiết kế ho&agrave;i niệm, cấu h&igrave;nh vẫn \'ngon\' để chiến game \'mượt\'</a></p>\r\n<h3>Hỗ trợ sạc nhanh, sạc kh&ocirc;ng d&acirc;y</h3>\r\n<p>Nhờ thiết kế mặt lưng k&iacute;nh m&agrave; iPhone 8 Plus đ&atilde; c&oacute; thể t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ <a title=\"T&igrave;m hiểu c&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y\" href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-sac-khong-day-761328\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu c&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y\">sạc kh&ocirc;ng d&acirc;y</a> ti&ecirc;n tiến cho sử dụng an to&agrave;n v&agrave; thuận tiện hơn rất nhiều.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, một n&acirc;ng cấp phải kể đến nữa l&agrave; khả năng hỗ trợ sạc nhanh đạt 50% chỉ sau 30 ph&uacute;t, tuy nhi&ecirc;n bộ phụ kiện theo k&egrave;m m&aacute;y chưa bao gồm sạc nhanh.</p>', 'Thừa hưởng những thiết kế đã đạt đến độ chuẩn mực, thế hệ iPhone 8 Plus thay đổi phong cách bóng bẩy hơn và bổ sung hàng loạt tính năng cao cấp cho trải nghiệm sử dụng vô cùng tuyệt vời.', 'Màn hình: LED-backlit IPS LCD5.5\"Retina HD\r\nHệ điều hành: iOS 13\r\nCamera sau: 2 camera 12 MP\r\nCamera trước: 7 MP\r\nChip: Apple A11 Bionic\r\nRAM: 3 GB\r\nBộ nhớ trong: 64 GB\r\nSIM: 1 Nano SIMHỗ trợ 4G', 100, 30000000, 100000000, '2022-07-24 21:23:23', '2022-08-05 05:08:21', NULL, 'iphone-8-plus', 0, 0, 'Sản phẩm,Iphone 8,Siêu phẩm,Iphone,Điện thoại'),
(8, 9, 28, 'Samsung Galaxy A5', 'https://cdn.tgdd.vn/Products/Images/42/196963/samsung-galaxy-a50-055920-045942-600x600.jpg', '<p style=\"text-align: justify;\"><strong style=\"font-size: 13px; line-height: 1.6;\">Thiết kế nguy&ecirc;n khối đẹp mắt</strong></p>\r\n<p style=\"text-align: justify;\">Galaxy A5 c&oacute; thiết kế đặc trưng của d&ograve;ng A khi c&aacute;c cạnh được cắt kim cương bắt s&aacute;ng v&agrave; l&agrave;m vu&ocirc;ng vức hơn tạo sự kh&aacute;c lạ, tuy nhi&ecirc;n khi cầm l&acirc;u m&aacute;y bạn sẽ thấy đau tay khi bị cấn.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-14.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Tổng thể của Galaxy A5 kh&aacute; vu&ocirc;ng vức\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-14.jpg\" alt=\"Tổng thể của Galaxy A5 kh&aacute; vu&ocirc;ng vức\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-14.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>M&aacute;y được ho&agrave;n thiện bằng kim loại, sử dụng kh&aacute; m&aacute;t tay</em></p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-11.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&aacute;y c&oacute; độ mỏng chỉ 6.7 mm n&ecirc;n phần viền camera bị lồi ra ngo&agrave;i đ&ocirc;i ch&uacute;t\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-11.jpg\" alt=\"M&aacute;y c&oacute; độ mỏng chỉ 6.7 mm n&ecirc;n phần viền camera bị lồi ra ngo&agrave;i đ&ocirc;i ch&uacute;t\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-11.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>M&aacute;y c&oacute; độ mỏng chỉ 6.7 mm n&ecirc;n phần viền camera bị lồi ra ngo&agrave;i đ&ocirc;i ch&uacute;t</em></p>\r\n<p style=\"text-align: justify;\"><strong>M&agrave;n h&igrave;nh Super AMOLED cho g&oacute;c nh&igrave;n rộng</strong></p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-15.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&aacute;y c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh 5 inch kh&aacute; rộng r&atilde;i để bạn sử dụng m&aacute;y hơn, c&aacute;c ph&iacute;m điều hướng được đặt ngo&agrave;i m&agrave;n h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-15.jpg\" alt=\"M&aacute;y c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh 5 inch kh&aacute; rộng r&atilde;i để bạn sử dụng m&aacute;y hơn, c&aacute;c ph&iacute;m điều hướng được đặt ngo&agrave;i m&agrave;n h&igrave;nh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-15.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>M&aacute;y c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh 5 inch kh&aacute; rộng r&atilde;i để bạn sử dụng m&aacute;y hơn, c&aacute;c ph&iacute;m điều hướng được đặt ngo&agrave;i m&agrave;n h&igrave;nh</em></p>\r\n<p style=\"text-align: center;\">[gallerycompare img1=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-6-2.jpg\" img2=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-7-2.jpg\"]</p>\r\n<p style=\"text-align: center;\"><em>Đặc biệt, m&aacute;y sử dụng m&agrave;n h&igrave;nh <a title=\"T&igrave;m hiểu m&agrave;n h&igrave;nh Super AMOLED\" href=\"https://www.thegioididong.com/tin-tuc/loai-man-hinh-tft-lcd-amoled-la-gi--592346#superamoled\" target=\"_blank\" rel=\"noopener\">Super AMOLED</a> tăng khả năng hiển thị m&agrave;u sắc rất tốt, g&oacute;c nh&igrave;n xung quanh rộng hơn, kh&ocirc;ng bị mất m&agrave;u</em></p>\r\n<p style=\"text-align: justify;\">Với m&agrave;n h&igrave;nh 5 inch, nếu bạn thấy kh&aacute; kh&oacute; để d&ugrave;ng bằng 1 tay th&igrave; c&oacute; thể t&ugrave;y chỉnh thu nhỏ m&agrave;n h&igrave;nh để sử dụng dễ d&agrave;ng hơn.</p>\r\n<p style=\"text-align: justify;\"><strong>Cấu h&igrave;nh kh&aacute; ổn</strong></p>\r\n<p style=\"text-align: justify;\">M&aacute;y sử dụng chip <a title=\"T&igrave;m hiểu chip Snapdragon 410\" href=\"https://www.thegioididong.com/tin-tuc/vi-xu-ly-di-dong-snapdragon-410-610-801-805-808-81-599368#snapdragon410\" target=\"_blank\" rel=\"noopener\">Snapdragon 410</a>, 4 nh&acirc;n tốc độ 1.2 GHz, RAM 2 GB, bộ nhớ trong 16 GB v&agrave; khả năng mở rộng thẻ nhớ l&ecirc;n 64 GB.</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div class=\"article__content__video-container\"><iframe id=\"138522127\" title=\"YouTube video player\" src=\"https://www.youtube.com/embed/ZFV5q_4Q_Nk?rel=0&amp;enablejsapi=1&amp;origin=https%3A%2F%2Fwww.thegioididong.com\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\" data-gtm-yt-inspected-1070012_61=\"true\" data-gtm-yt-inspected-1070012_79=\"true\" data-gtm-yt-inspected-1070012_187=\"true\" data-gtm-yt-inspected-10=\"true\"></iframe></div>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\"><em>Kh&ocirc;ng qu&aacute; xuất sắc nhưng m&aacute;y vẫn c&oacute; thể chơi được c&aacute;c game nặng v&agrave; đồ hoạ hiển thị kh&aacute; đẹp mắt</em></p>\r\n<p style=\"text-align: justify;\"><strong>Camera chụp đẹp</strong></p>\r\n<p style=\"text-align: justify;\">M&aacute;y được trang bị camera ch&iacute;nh l&ecirc;n đến 13 MP c&ugrave;ng khả năng xử l&yacute; h&igrave;nh ảnh kh&aacute; đẹp mắt, m&aacute;y c&oacute; c&aacute;c chức năng như tự động lấy n&eacute;t, chạm lấy n&eacute;t, nhận diện khu&ocirc;n mặt, <a title=\"T&igrave;m hiểu chế độ chụp panorama\" href=\"https://www.thegioididong.com/tin-tuc/cac-che-do-chup-anh-tren-smartphone-tiep-theo--739084#panorama\" target=\"_blank\" rel=\"noopener\">panorama</a>.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-8-1.jpg\"><img class=\" lazyloaded\" title=\"H&igrave;nh ảnh chụp bằng Galaxy A5 đẹp mắt của một kh&aacute;ch h&agrave;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-8-1.jpg\" alt=\"H&igrave;nh ảnh chụp bằng Galaxy A5 đẹp mắt của một kh&aacute;ch h&agrave;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-8-1.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>H&igrave;nh ảnh chụp bằng Galaxy A5 đẹp mắt của một kh&aacute;ch h&agrave;ng (Ảnh: <a title=\"Th&ocirc;ng tin người d&ugrave;ng\" href=\"https://www.thegioididong.com/anh-dep/thong-tin-nguoi-choi-1716671\" target=\"_blank\" rel=\"noopener\">Đinh V&otilde; Nam Hiếu</a>)</em></p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-9-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp một con đường nhiều tuyết rơi (Ảnh: Hanh Chu)\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-9-1.jpg\" alt=\"Ảnh chụp một con đường nhiều tuyết rơi (Ảnh: Hanh Chu)\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-9-1.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>Ảnh chụp một con đường nhiều tuyết rơi (Ảnh: <a title=\"Th&ocirc;ng tin người d&ugrave;ng\" href=\"https://www.thegioididong.com/anh-dep/thong-tin-nguoi-choi-1698925\" target=\"_blank\" rel=\"noopener\">Hanh Chu</a>)</em></p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-8000.gif\"><img class=\" lazyloaded\" title=\"Camera phụ c&oacute; độ ph&acirc;n giải 5 MP, bạn c&oacute; thể chụp ảnh bằng cử chỉ tay kh&aacute; đơn giản\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-8000.gif\" alt=\"Camera phụ c&oacute; độ ph&acirc;n giải 5 MP, bạn c&oacute; thể chụp ảnh bằng cử chỉ tay kh&aacute; đơn giản\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-8000.gif\" /></a></p>\r\n<p style=\"text-align: center;\"><em>Camera phụ c&oacute; độ ph&acirc;n giải 5 MP, bạn c&oacute; thể chụp ảnh bằng cử chỉ tay kh&aacute; đơn giản</em></p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-10.jpg\"><img class=\" lazyloaded\" title=\"G&oacute;c chụp tr&ecirc;n m&aacute;y rộng hơn, m&aacute;y nhận dạng khu&ocirc;n mặt để l&agrave;m mịn da rất tốt\" src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-10.jpg\" alt=\"G&oacute;c chụp tr&ecirc;n m&aacute;y rộng hơn, m&aacute;y nhận dạng khu&ocirc;n mặt để l&agrave;m mịn da rất tốt\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/70049/samsung-galaxy-a500-1-10.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>G&oacute;c chụp tr&ecirc;n m&aacute;y rộng hơn, m&aacute;y nhận dạng khu&ocirc;n mặt để l&agrave;m mịn da rất tốt</em></p>\r\n<p style=\"text-align: justify;\">M&aacute;y c&oacute; dung lượng pin 2300 mAh, c&oacute; thể gắn 2 sim hoặc 1 sim 1 <a title=\"Thẻ nhớ cho điện thoại\" href=\"https://www.thegioididong.com/the-nho-dien-thoai\" target=\"_blank\" rel=\"noopener\">thẻ nhớ</a> t&ugrave;y theo nhu cầu sử dụng của bạn, m&aacute;y c&oacute; c&aacute;c kết nối cơ bản nhất cho người d&ugrave;ng như wifi, 3G, định vị hay bluetooth.</p>\r\n<p style=\"text-align: justify;\">Với thiết kế đẹp nguy&ecirc;n khối kim loại, camera chụp ảnh đẹp v&agrave; cấu h&igrave;nh m&aacute;y ổn định th&igrave; Galaxy A5 sẽ l&agrave; một sự ưu ti&ecirc;n lựa chọn của bạn trong ph&acirc;n kh&uacute;c gi&aacute; tầm trung.</p>\r\n<p style=\"text-align: right;\"><strong>Ng&ocirc;n Ng&ocirc;n</strong></p>', 'Điện thoại thông minh Samsung Galaxy A5 nổi bật với màn hình Super AMOLED cùng thiết kế nguyên khối bằng kim loại vuông vức sang trọng và tinh tế.', 'Màn hình: Super AMOLED5\"HD\r\nCamera sau: 13 MP\r\nCamera trước: 5 MP\r\nChip: Snapdragon 410\r\nRAM: 2 GB\r\nBộ nhớ trong: 16 GB\r\nSIM: 1 Nano SIM\r\nPin, Sạc: 2300 mAh', 100, 10000000, 12000000, '2022-07-24 22:14:54', '2022-08-05 05:09:35', NULL, 'samsung-galaxy-a5', 7, 4, 'Sản phẩm,A8,Samsung,Điện thoại tầm trung'),
(9, 9, 24, 'Nokia G21', 'https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-xanh-thumb-600x600.jpg', '<h3 style=\"text-align: justify;\">Thiết kế trẻ trung</h3>\r\n<p style=\"text-align: justify;\">Ấn tượng đầu ti&ecirc;n khi m&agrave; m&igrave;nh cầm Nokia G21 tr&ecirc;n tay l&agrave; tổng thể của m&aacute;y kh&aacute; lớn, mặt lưng được l&agrave;m từ vật liệu nhựa c&ugrave;ng kiểu thiết kế v&acirc;n ngang, tạo cho m&igrave;nh một cảm gi&aacute;c sờ v&agrave;o rất th&iacute;ch cũng như hạn chế b&aacute;m dấu v&acirc;n tay v&agrave; mồ h&ocirc;i tay rong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051928.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Mặt lưng nhựa nh&aacute;m cao cấp - Nokia G21\" src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051928.jpg\" alt=\"Mặt lưng nhựa nh&aacute;m cao cấp - Nokia G21\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051928.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Mặt lưng của m&aacute;y được gia c&ocirc;ng bo cong v&agrave; c&oacute; hơi hướng tr&agrave;n về 2 cạnh, mang đến cho m&igrave;nh cảm gi&aacute;c cầm nắm dễ chịu m&agrave; kh&ocirc;ng cảm thấy cấn tay khi sử dụng li&ecirc;n tục trong một khoảng thời gian d&agrave;i.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051929.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Cạnh viền bo cong - Nokia G21\" src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051929.jpg\" alt=\"Cạnh viền bo cong - Nokia G21\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051929.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Nổi trội ở mặt lưng l&agrave; cụm camera được bố tr&iacute; chung một h&agrave;ng tr&ocirc;ng rất c&acirc;n đối, tuy nhi&ecirc;n c&aacute;c ống k&iacute;nh c&oacute; hơi lồi l&ecirc;n so với cụm l&agrave;m m&igrave;nh cảm thấy chưa thực sự an t&acirc;m cho lắm khi đặt thiết bị l&ecirc;n b&agrave;n, bởi n&oacute; c&oacute; thể dễ g&acirc;y trầy xước.</p>\r\n<p style=\"text-align: justify;\">M&aacute;y trang bị cụm m&aacute;y ảnh với camera ch&iacute;nh 50 MP v&agrave; hai cảm biến phụ c&oacute; c&ugrave;ng độ ph&acirc;n giải 2 MP gi&uacute;p bạn thu lại những bức ảnh chất lượng với độ sắc n&eacute;t cao.&nbsp;</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051931.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Trang bị 3 camera - Nokia G21\" src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051931.jpg\" alt=\"Trang bị 3 camera - Nokia G21\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051931.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Ở phần giọt nước tr&ecirc;n m&agrave;n h&igrave;nh, m&aacute;y sở hữu camera selfie 8 MP c&ugrave;ng nhiều t&iacute;nh năng l&agrave;m đẹp gi&uacute;p bạn tự tin hơn tr&ecirc;n từng bức ảnh tự chụp.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051954.jpg\"><img class=\" lazyloaded\" title=\"Camera selfie - Nokia G21\" src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051954.jpg\" alt=\"Camera selfie - Nokia G21\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051954.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Ở phần mặt trước, h&atilde;ng trang bị cho G21 một tấm nền TFT LCD c&oacute; k&iacute;ch thước 6.5 inch c&ugrave;ng độ ph&acirc;n giải HD+ (720 x 1600 Pixels), theo như m&igrave;nh cảm nhận th&igrave; m&agrave;u sắc mang lại c&oacute; độ tương phản kh&aacute;, độ chi tiết tốt ổn.</p>\r\n<p style=\"text-align: justify;\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-tft-lcd-la-gi-co-uu-diem-gi-thiet-bi-nao-905743\" target=\"_blank\" rel=\"noopener\">M&agrave;n h&igrave;nh TFT LCD l&agrave; g&igrave;? C&oacute; ưu điểm g&igrave;? Thiết bị n&agrave;o d&ugrave;ng m&agrave;n h&igrave;nh TFT?</a></p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051933.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh hiển thị đẹp mắt - Nokia G21\" src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051933.jpg\" alt=\"M&agrave;n h&igrave;nh hiển thị đẹp mắt - Nokia G21\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051933.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Một điểm cộng tiếp theo m&igrave;nh d&agrave;nh cho Nokia G21 l&agrave; tần số qu&eacute;t 90 Hz, đ&acirc;y l&agrave; điều kh&aacute; &iacute;t sản phẩm thuộc ph&acirc;n kh&uacute;c gi&aacute; n&agrave;y c&oacute; thể l&agrave;m được, nhờ vậy m&agrave; c&aacute;c t&aacute;c vụ vuốt chạm h&agrave;ng ng&agrave;y của m&igrave;nh được diễn ra v&ocirc; c&ugrave;ng trơn mượt hay đ&atilde; mắt hơn khi chơi c&aacute;c tựa game FPS cao.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051935.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ tần số qu&eacute;t cao - Nokia G21\" src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051935.jpg\" alt=\"Hỗ trợ tần số qu&eacute;t cao - Nokia G21\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270207/nokia-g21-040522-051935.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Một điểm m&agrave; m&igrave;nh chưa ưng &yacute; l&agrave; viền m&agrave;n h&igrave;nh kh&aacute; d&agrave;y, mang đến một c&aacute;i nh&igrave;n chưa được thanh tho&aacute;t cho lắm, nhưng theo m&igrave;nh nghĩ th&igrave; đ&acirc;y l&agrave; c&aacute;ch m&agrave; Nokia tối ưu được quy tr&igrave;nh sản xuất để đưa ra mức gi&aacute; phải chăng như vậy, n&ecirc;n m&igrave;nh cũng kh&ocirc;ng qu&aacute; khắt khe về vấn đề n&agrave;y.</p>', 'Nokia G21 là thế hệ tiếp theo thuộc G series do Nokia sản xuất với những cải tiến nổi bật như màn hình kích thước lớn, hiệu năng ổn định mang đến trải nghiệm mượt mà trên các tác vụ hằng ngày cùng thời gian sử dụng lâu dài nhờ viên pin khủng được tích hợp trên điện thoại.', 'Màn hình: TFT LCD6.5\"HD+\r\nHệ điều hành: Android 11\r\nCamera sau: Chính 50 MP & Phụ 2 MP, 2 MP\r\nCamera trước: 8 MP\r\nChip: Unisoc T606\r\nRAM: 4 GB\r\nBộ nhớ trong: 128 GB', 100, 3890000, 4290000, '2022-07-24 22:24:57', '2022-08-05 06:03:25', NULL, 'nokia-g21', 4, 2, 'Sản phẩm,Nokia,Điện thoại tầm trung,G21'),
(10, 16, 23, 'Sony Xperia 1', 'https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-1-600x600.jpg', '<h3>Smartphone đầu ti&ecirc;n c&oacute; m&agrave;n h&igrave;nh 21:9</h3>\r\n<p>Nếu như c&aacute;c nh&agrave; sản xuất <a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a> hiện nay đang l&agrave;m những chiếc m&aacute;y c&oacute; tỷ lệ m&agrave;n h&igrave;nh 18:9 hay 19:9 th&igrave; chiếc flagship mới c&oacute; <a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại Sony ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd-sony\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng điện thoại Sony ch&iacute;nh h&atilde;ng\">Sony</a> c&oacute; m&agrave;n h&igrave;nh tỷ lệ l&ecirc;n tới 21:9.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-17.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-17.jpg\" alt=\"Thiết kế của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-17.jpg\" /></a></p>\r\n<p>Xperia 1 c&oacute; m&agrave;n h&igrave;nh k&iacute;ch thước 6.5 inch hỗ trợ c&ocirc;ng nghệ hiển thị BRAVIA X1 của Sony cho m&agrave;u đen s&acirc;u hơn v&agrave; tự nhi&ecirc;n hơn.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-18.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-18.jpg\" alt=\"M&agrave;n h&igrave;nh của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-18.jpg\" /></a></p>\r\n<p>C&oacute; thể bạn chưa biết th&igrave; đ&acirc;y l&agrave; m&agrave;n h&igrave;nh OLED 4K HDR duy nhất tr&ecirc;n thế giới.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-19.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-19.jpg\" alt=\"M&agrave;n h&igrave;nh của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-19.jpg\" /></a></p>\r\n<p>Theo chia sẻ của Sony th&igrave;&nbsp;chất lượng hiển thị tr&ecirc;n Xperia 1 đạt ti&ecirc;u chuẩn để c&aacute;c nh&agrave; l&agrave;m phim sử dụng cho c&ocirc;ng việc chỉnh sửa video của họ.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-113.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-113.jpg\" alt=\"M&agrave;n h&igrave;nh của điện thoại Sony Xperia 1 ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/195747/sony-xperia-113.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra th&igrave; m&aacute;y cũng&nbsp;đạt chứng nhận chống bụi v&agrave; nước chuẩn IP68 v&agrave; được bảo vệ bằng k&iacute;nh cường lực Corning Gorilla Glass 6 ở cả mặt trước v&agrave; sau.</p>', 'Sony Xperia 1 - là phiên bản kế nhiệm người đàn anh Xperia XZ3, mang nhiều tính năng đặc biệt khi sở hữu những công nghệ lần đầu tiên có mặt trên một chiếc smartphone.', 'Màn hình: P-OLED6.5\"Ultra HD (4K)\r\nHệ điều hành: Android 9 (Pie)\r\nCamera sau: 3 camera 12 MP\r\nCamera trước: 8 MP\r\nChip: Snapdragon 855\r\nRAM: 6 GB\r\nBộ nhớ trong: 128 GB', 100, 4250000, 6000000, '2022-07-25 00:12:39', '2022-08-05 06:04:03', NULL, 'sony-xperia-1', 0, 0, 'Sản phẩm,Sony,Xperia,Điện thoại tầm trung');
INSERT INTO `products` (`id`, `user_id`, `cat_id`, `name`, `thumbnail`, `content`, `summary`, `description`, `qty`, `price`, `old_price`, `created_at`, `updated_at`, `deleted_at`, `slug`, `num_views`, `num_purchases`, `tags`) VALUES
(11, 9, 15, 'MacBook Air M1 2020', 'https://cdn.tgdd.vn/Products/Images/44/231244/TimerThumb/apple-macbook-air-2020-mgn63saa-(12).jpg', '<h3>Hiệu năng ấn tượng đến từ chip M1</h3>\r\n<p><a title=\"T&igrave;m hiểu về chip Apple M1: Con chip ARM 5nm đầu ti&ecirc;n d&agrave;nh cho m&aacute;y Mac\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-m1-con-chip-arm-5nm-dau-tien-danh-1305955\" target=\"_blank\" rel=\"noopener\">Chip M1</a> được Apple thiết kế d&agrave;nh ri&ecirc;ng cho <a title=\"Xem th&ecirc;m một số mẫu MacBook đang được b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" rel=\"noopener\">MacBook</a> mang đến hiệu năng vượt trội. Thực hiện tốt c&aacute;c t&aacute;c vụ văn ph&ograve;ng tr&ecirc;n c&aacute;c phần mềm như Word, Excel, Powerpoint,... Thiết kế đồ hoạ cũng chuy&ecirc;n nghiệp kh&ocirc;ng k&eacute;m, cho ph&eacute;p bạn chỉnh sửa h&igrave;nh ảnh với dung lượng lớn, kết xuất 2D mượt m&agrave; tr&ecirc;n c&aacute;c phần mềm Photoshop, AI, Figma,...</p>\r\n<p>Card đồ họa <strong>GPU 7 nh&acirc;n</strong>&nbsp;đem lại hiệu suất cao đ&aacute;ng kinh ngạc, đồ họa cao hơn gấp <strong>5 lần</strong>, thoả sức s&aacute;ng tạo nội dung, kết xuất 3D ổn định, render video, ph&aacute;t trực tiếp với chất lượng cao với chất ảnh sắc n&eacute;t c&ugrave;ng độ ph&acirc;n giải l&ecirc;n đến <strong>4K</strong>.</p>\r\n<p><a title=\"Xem th&ecirc;m một số laptop RAM 16 GB đang được b&aacute;n tại Thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=16-gb\" target=\"_blank\" rel=\"noopener\">Laptop RAM 16 GB</a> cho ph&eacute;p bạn sử dụng đa nhiệm, bạn c&oacute; thể thao t&aacute;c nhiều ứng dụng c&ugrave;ng một l&uacute;c thoải m&aacute;i với những t&aacute;c vụ như lướt web, soạn thảo văn bản, xem video hay thiết kế h&igrave;nh ảnh bằng Photoshop, Adobe IIIustrator,...</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-4-1-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Apple MacBook Air M1 2020 (Z124000DE) - Cấu h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-4-1-1.jpg\" alt=\"Apple MacBook Air M1 2020 (Z124000DE) - Cấu h&igrave;nh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-4-1-1.jpg\" /></a></p>\r\n<p><strong>Ổ cứng SSD 256 GB</strong> gi&uacute;p m&aacute;y c&oacute; tốc độ đọc ghi nhanh ch&oacute;ng đồng thời mang lại kh&ocirc;ng gian bộ nhớ đủ lớn phục vụ cho nhu cầu lưu trữ của bạn như t&agrave;i liệu trong học tập, dữ liệu trong c&ocirc;ng việc, c&aacute;c bộ phim hoặc bản nhạc trong giải tr&iacute;. Đồng thời, tiết kiệm được thời gian khởi động m&aacute;y hoặc mở c&aacute;c ứng dụng nặng, cải thiện được thời gian mở nhanh ch&oacute;ng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-5-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Apple MacBook Air M1 2020 (Z124000DE) - SSD\" src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-5-1.jpg\" alt=\"Apple MacBook Air M1 2020 (Z124000DE) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-5-1.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh Retina hiển thị sắc n&eacute;t, m&agrave;u sắc ch&iacute;nh x&aacute;c</h3>\r\n<p><strong>M&agrave;n h&igrave;nh 13.3 inch Retina</strong> cho h&igrave;nh ảnh chi tiết, sắc n&eacute;t với độ ph&acirc;n giải <strong>2560 &times; 1600</strong>, m&agrave;n h&igrave;nh mới n&agrave;y c&oacute; gam m&agrave;u rộng P3 v&agrave; độ phủ m&agrave;u <strong>125% sRGB</strong> cho những m&agrave;u sắc ch&acirc;n thật v&agrave; ch&iacute;nh x&aacute;c nhất. C&ugrave;ng với đ&oacute; l&agrave; độ s&aacute;ng l&ecirc;n đến <strong>400 nits</strong> đem đến khung h&igrave;nh rực rỡ, tươi s&aacute;ng, hiển thị tốt kể cả khi sử dụng ở nơi c&oacute; cường độ &aacute;nh s&aacute;ng cao.</p>\r\n<p>C&ocirc;ng nghệ <strong>True Tone Technology</strong> tự điều chỉnh điểm trắng, &aacute;nh s&aacute;ng v&agrave; m&agrave;u sắc của m&agrave;n h&igrave;nh theo nhiệt độ, &aacute;nh s&aacute;ng của m&ocirc;i trường để c&oacute; trải nghiệm h&igrave;nh ảnh tự nhi&ecirc;n hơn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-7.jpg\"><img class=\" lazyloaded\" title=\"Apple MacBook Air M1 2020 (Z124000DE) - H&igrave;nh ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-7.jpg\" alt=\"Apple MacBook Air M1 2020 (Z124000DE) - H&igrave;nh ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-7.jpg\" /></a></p>\r\n<p>Hệ thống<strong>&nbsp;loa k&eacute;p</strong>&nbsp;cho &acirc;m thanh to r&otilde;, sống động để bạn c&oacute; trải nghiệm nghe nhạc, xem phim v&agrave; gọi điện trực tuyến thoải m&aacute;i với &acirc;m thanh chất lượng cao. Tạo cho bạn cảm gi&aacute;c đắm ch&igrave;m trong những chương tr&igrave;nh giải tr&iacute; hấp dẫn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-8.jpg\"><img class=\" lazyloaded\" title=\"Apple MacBook Air M1 2020 (Z124000DE) - &Acirc;m thanh\" src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-8.jpg\" alt=\"Apple MacBook Air M1 2020 (Z124000DE) - &Acirc;m thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239552/apple-macbook-air-m1-2020-z124000de-8.jpg\" /></a></p>\r\n<p><strong>FaceTime Camera&nbsp;</strong>cho h&igrave;nh ảnh r&otilde; n&eacute;t kết hợp với&nbsp;<strong>3 microphone</strong>&nbsp;thuận tiện cho nhu cầu họp hoặc học online hay hội đ&agrave;m trực tuyến, giải quyết hiệu quả c&ocirc;ng việc từ xa qua c&aacute;c phần mềm Zoom, Google Meet,...</p>', 'Laptop Apple Air M1 2020 có thiết kế đẹp, sang trọng với CPU M1 độc quyền từ Apple cho hiệu năng đồ họa cao, màn hình Retina hiển thị siêu nét cùng với hệ thống bảo mật tối ưu.', 'CPU: Apple M1\r\nRAM: 16 GB\r\nỔ cứng: 256 GB SSD\r\nMàn hình: 13.3\"Retina (2560 x 1600)\r\nCard màn hình: Card tích hợp7 nhân GPU\r\nCổng kết nối: 2 x Thunderbolt 3 (USB-C)Jack tai nghe 3.5 mm\r\nĐặc biệt: Có đèn bàn phím\r\nHệ điều hành: Mac OS', 100, 33990000, 33990000, '2022-07-25 00:17:46', '2022-08-05 13:19:08', NULL, 'macbook-air-m1-2020', 14, 0, 'Sản phẩm,Laptop,Mac,Apple,Vỏ nhôm'),
(12, 9, 3, 'Alcatel-3t8', 'https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/tablet/alcatel-3t8-(6).jpg', '<h3 role=\"presentation\">Thiết kế cứng c&aacute;p</h3>\r\n<p role=\"presentation\"><a title=\"Tham khảo th&ocirc;ng tin sản phẩm tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang/alcatel-3t8\" target=\"_blank\" rel=\"noopener\">M&aacute;y t&iacute;nh bảng&nbsp;Alcatel 3T8</a> mỏng nhẹ với trọng lượng 290 g, l&agrave; người bạn l&yacute; tưởng khi bạn di chuyển. M&agrave;u sắc tinh tế c&ugrave;ng lớp nh&aacute;m mờ ho&agrave;n thiện đặc biệt gi&uacute;p bạn tr&ocirc;ng đẹp mắt ở bất cứ đ&acirc;u.</p>\r\n<p role=\"presentation\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061137.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế trẻ cứng c&aacute;p - Alcatel 3T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061137.jpg\" alt=\"Thiết kế trẻ cứng c&aacute;p - Alcatel 3T8\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061137.jpg\" /></a></p>\r\n<p role=\"presentation\"><a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">M&aacute;y t&iacute;nh bảng</a> sở hữu thiết kế nhựa nguy&ecirc;n khối, c&oacute; 2 m&agrave;u cơ bản gồm xanh v&agrave; đen.</p>\r\n<h3 role=\"presentation\">Trải nghiệm mượt m&agrave;</h3>\r\n<p>Cung cấp sức mạnh cho m&aacute;y l&agrave; CPU Mediatek MT8766 4 nh&acirc;n, <a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng c&oacute; RAM 2 GB đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang-ram-2gb\" target=\"_blank\" rel=\"noopener\">m&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 2 GB</a>, bộ nhớ trong 32 GB đủ d&ugrave;ng cho c&aacute;c t&aacute;c vụ cơ bản.</p>\r\n<p role=\"presentation\"><a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng Alcatel  đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang-alcatel\" target=\"_blank\" rel=\"noopener\">M&aacute;y t&iacute;nh bảng Alcatel</a> sử dụng Android 10 (phi&ecirc;n bản Go), tất cả đều th&ocirc;ng qua kiểm tra độ ổn định, đảm bảo cho bạn trải nghiệm mượt m&agrave; v&agrave; li&ecirc;n tục</p>\r\n<p role=\"presentation\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061135.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Android Go mượt m&agrave; - Alcatel 3T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061135.jpg\" alt=\"Android Go mượt m&agrave; - Alcatel 3T8\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061135.jpg\" /></a></p>\r\n<p role=\"presentation\">Pin 4080 mAh c&oacute; thể sử dụng đủ một ng&agrave;y với c&aacute;c t&aacute;c vụ nhẹ nh&agrave;ng như xem phim, lướt web v&agrave; cho thời gian chờ l&ecirc;n đến 400 giờ.</p>\r\n<h3 role=\"presentation\">Chăm s&oacute;c gia đ&igrave;nh</h3>\r\n<p role=\"presentation\">Hỗ trợ Chế độ d&agrave;nh cho Trẻ em, m&aacute;y cung cấp cho phụ huynh quyền kiểm so&aacute;t, thiết lập khả năng truy cập v&agrave; thời gian sử dụng cho người d&ugrave;ng trẻ em trong một giao diện đơn giản.</p>\r\n<p role=\"presentation\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061138.jpg\"><img class=\" lazyloaded\" title=\"Chế độ trẻ em - Alcatel 3T8\" src=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061138.jpg\" alt=\"Chế độ trẻ em - Alcatel 3T8\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/270132/alcatel-3t8-190122-061138.jpg\" /></a></p>', 'Máy tính bảng Alcatel 3T8 mỏng nhẹ với trọng lượng 290 g, là người bạn lý tưởng khi bạn di chuyển. Màu sắc tinh tế cùng lớp nhám mờ hoàn thiện đặc biệt giúp bạn trông đẹp mắt ở bất cứ đâu.', 'Màn hình: 8\"IPS LCD\r\nHệ điều hành: Android 10\r\nChip: MediaTek MT8766\r\nRAM: 2 GB\r\nBộ nhớ trong: 32 GB\r\nKết nối: Hỗ trợ 4GCó nghe gọi\r\nSIM: 1 Nano SIM', 100, 2440000, 2990000, '2022-07-25 00:24:58', '2022-08-05 08:48:31', NULL, 'alcatel-3t8', 1, 0, 'Sản phẩm'),
(13, 9, 3, 'Samsung galaxy tab a7 lite', 'https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/tablet/samsung-galaxy-tab-a7-lite-(2).jpg', '<h3>Thiết kế si&ecirc;u mỏng nhẹ</h3>\r\n<p>Galaxy Tab A7 Lite sở hữu kiểu thiết kế đặc trưng của d&ograve;ng Galaxy Tab A7. Thiết bị sử dụng chất liệu nh&ocirc;m với thiết kế nguy&ecirc;n khối gi&uacute;p cho m&aacute;y trở n&ecirc;n cao cấp hơn d&ugrave; chỉ thuộc ph&acirc;n kh&uacute;c tầm trung gi&aacute; rẻ.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-002.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy Tab A7 Lite | Thiết kế mặt lưng\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-002.jpg\" alt=\"Galaxy Tab A7 Lite | Thiết kế mặt lưng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-002.jpg\" /></a></p>\r\n<p>Tuy được ho&agrave;n thiện từ nh&ocirc;m nguy&ecirc;n khối, nhưng Galaxy Tab A7 Lite vẫn c&oacute; độ mỏng nhẹ ấn tượng khi chỉ nặng 371 g v&agrave; d&agrave;y chỉ 8 mm, mang đến sự thoải m&aacute;i khi cầm sử dụng trong thời gian d&agrave;i.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-004.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy Tab A7 Lite | Thiết kế cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-004.jpg\" alt=\"Galaxy Tab A7 Lite | Thiết kế cạnh viền\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-004.jpg\" /></a></p>\r\n<p>Với k&iacute;ch thước chỉ tương đương với một quyển sổ n&ecirc;n&nbsp;Galaxy Tab A7 Lite sẽ&nbsp;v&ocirc; c&ugrave;ng gọn g&agrave;ng để bạn c&oacute; thể mang theo khi cần thiết.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-009.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy Tab A7 Lite | Thiết kế gọn r&agrave;ng, cầm nắm thoải m&aacute;i\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-009.jpg\" alt=\"Galaxy Tab A7 Lite | Thiết kế gọn r&agrave;ng, cầm nắm thoải m&aacute;i\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-009.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh lớn hiển thị nhiều hơn, r&otilde; hơn</h3>\r\n<p>M&aacute;y t&iacute;nh bảng Galaxy Tab A7 Lite c&oacute; m&agrave;n h&igrave;nh k&iacute;ch thước 8.7 inch, độ ph&acirc;n giải cao 800 x 1340 pixel, cho chất lượng hiển thị kh&aacute;, m&agrave;u sắc tươi tắn, m&agrave;n h&igrave;nh lớn sẽ gi&uacute;p bạn xem được nhiều nội dung hơn, thoải m&aacute;i k&eacute;o thả, chỉnh sửa h&igrave;nh ảnh, văn bản dễ d&agrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-001.jpg\"><img class=\" lazyloaded\" title=\"Galaxy Tab A7 Lite | Trang bị m&agrave;n h&igrave;nh k&iacute;ch thước lớn 8.7 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-001.jpg\" alt=\"Galaxy Tab A7 Lite | Trang bị m&agrave;n h&igrave;nh k&iacute;ch thước lớn 8.7 inch\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-001.jpg\" /></a></p>\r\n<p>Phần viền 2 b&ecirc;n cũng được Samsung l&agrave;m mỏng tối đa gi&uacute;p cho khung h&igrave;nh được trải rộng hơn, tăng trải nghiệm khi xem phim hay thưởng thức c&aacute;c chương tr&igrave;nh giải tr&iacute;.</p>\r\n<h3>Hiệu năng ổn định cho nhu cầu giải tr&iacute; cơ bản</h3>\r\n<p>Trang bị vi xử l&yacute; MediaTek MT8768T 8 nh&acirc;n xung nhịp tối đa 2.3 GHz, Galaxy Tab A7 Lite c&oacute; hiệu năng ổn định, đ&aacute;p ứng cho nhu cầu giải tr&iacute; kh&aacute;c nhau như chơi game nhẹ nh&agrave;ng, kiểm tra email, tin nhắn hay đọc b&aacute;o cập nhật tin tức.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-09.jpg\"><img class=\" lazyloaded\" title=\"Galaxy Tab A7 Lite | Trang bị con chip Helio P22T đến từ MediaTek\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-09.jpg\" alt=\"Galaxy Tab A7 Lite | Trang bị con chip Helio P22T đến từ MediaTek\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-09.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng c&oacute; RAM 3 GB  đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang-ram-3gb\" target=\"_blank\" rel=\"noopener\">M&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 3 GB</a>&nbsp;v&agrave;&nbsp;bộ nhớ trong 32 GB&nbsp;l&agrave; những th&ocirc;ng số kh&aacute; vừa vặn trong tầm gi&aacute;, vẫn đủ cho đa nhiệm v&agrave; nhu cầu lưu trữ ở mức độ cơ bản. B&ecirc;n cạnh đ&oacute;, m&aacute;y hỗ trợ khe cắm thẻ nhớ l&ecirc;n đến 1 TB gi&uacute;p tăng th&ecirc;m kh&ocirc;ng gian để lưu trữ những b&agrave;i h&aacute;t hay bộ phim y&ecirc;u th&iacute;ch của bạn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-008.jpg\"><img class=\" lazyloaded\" title=\"Galaxy Tab A7 Lite | RAM 3 GB, ROM 32 GB hỗ trợ thẻ nhớ mở rộng l&ecirc;n đến 1 TB\" src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-008.jpg\" alt=\"Galaxy Tab A7 Lite | RAM 3 GB, ROM 32 GB hỗ trợ thẻ nhớ mở rộng l&ecirc;n đến 1 TB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-008.jpg\" /></a></p>\r\n<p>Galaxy Tab A7 Lite trang bị vi&ecirc;n pin 5100 mAh gi&uacute;p m&aacute;y c&oacute; thể hoạt động li&ecirc;n tục trong nhiều giờ liền với c&aacute;c t&aacute;c vụ th&ocirc;ng thường.&nbsp;</p>\r\n<p>Kh&ocirc;ng chỉ vậy, m&aacute;y cũng hỗ trợ&nbsp;sạc nhanh&nbsp;l&ecirc;n đến 15 W gi&uacute;p pin được nạp đầy nhanh ch&oacute;ng. Tuy nhi&ecirc;n, sạc đi k&egrave;m m&aacute;y chỉ 7.75 W n&ecirc;n bạn cần mua ri&ecirc;ng phụ kiện tương th&iacute;ch nếu muốn sạc nhanh hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-007.jpg\"><img class=\"lazyload\" title=\"Galaxy Tab A7 Lite | Hỗ trợ sạc nhanh 15 W\" alt=\"Galaxy Tab A7 Lite | Hỗ trợ sạc nhanh 15 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-007.jpg\" /></a></p>\r\n<p>Galaxy Tab A7 Lite hỗ trợ đầy đủ c&aacute;c kết nối mạng th&ocirc;ng dụng như Wi-Fi, mạng 4G với tốc độ truy cập nhanh v&agrave; ổn định. Bạn cũng c&oacute; thể lắp được&nbsp;sim nghe gọi, cho ph&eacute;p Galaxy Tab A7 Lite c&oacute; thể nhận v&agrave; thực hiện được cuộc gọi như&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại, smartphone ch&iacute;nh h&atilde;ng, nhiều khuyến m&atilde;i HOT nhất tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo gi&aacute; điện thoại, smartphone ch&iacute;nh h&atilde;ng, nhiều khuyến m&atilde;i HOT nhất tại Thegioididong.com\">điện thoại</a>&nbsp;th&ocirc;ng thường.</p>\r\n<h3>Thưởng thức chất &acirc;m sống động từ loa stereo</h3>\r\n<p>Đắm ch&igrave;m trong thế giới giải tr&iacute; với loa stereo c&ocirc;ng nghệ Dolby Atmos mang đến chất &acirc;m lớn v&agrave; sống động, cho bạn trải nghiệm nghe nhạc hay xem phim sẽ c&agrave;ng ho&agrave;n hảo v&agrave; ch&acirc;n thật hơn bao giờ hết.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-005.jpg\"><img class=\"lazyload\" title=\"Galaxy Tab A7 Lite | Đắm ch&igrave;m trong thế giới giải tr&iacute; với loa stereo c&ocirc;ng nghệ Dolby Atmos\" alt=\"Galaxy Tab A7 Lite | Đắm ch&igrave;m trong thế giới giải tr&iacute; với loa stereo c&ocirc;ng nghệ Dolby Atmos\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-005.jpg\" /></a></p>\r\n<p>M&aacute;y cũng trang bị camera sau 8 MP&nbsp;quay được video Full HD v&agrave; camera trước 2 MP hỗ trợ gọi video call cũng như ghi lại những tấm ảnh chụp thường ng&agrave;y của gia đ&igrave;nh v&agrave; cuộc sống xung quanh bạn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-003.jpg\"><img class=\"lazyload\" title=\"Galaxy Tab A7 Lite | Camera sau 8 MP\" alt=\"Galaxy Tab A7 Lite | Camera sau 8 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/237325/samsung-galaxy-tab-a7-lite-003.jpg\" /></a></p>\r\n<h3>OneUI 3.1 th&ocirc;ng minh, mượt m&agrave; dễ sử dụng</h3>\r\n<p>Galaxy Tab A7 Lite sử dụng giao diện OneUI 3.1 ho&agrave;n to&agrave;n mới được tối ưu, với c&aacute;c thao t&aacute;c sử dụng v&ocirc; c&ugrave;ng th&acirc;n thiện đơn giản v&agrave; kh&ocirc;ng chứa ứng dụng r&aacute;c g&acirc;y nặng m&aacute;y, đảm bảo cho bạn c&oacute; một trải nghiệm tốt nhất.</p>\r\n<p>Với mức gi&aacute; phải chăng, cấu h&igrave;nh ổn, m&agrave;n h&igrave;nh lớn nhưng vẫn nhỏ gọn, Galaxy Tab A7 Lite sẽ l&agrave; một trong những sản phẩm ph&ugrave; hợp với mọi người đ&aacute;p ứng tốt nhu cầu học tập cũng như&nbsp;cho nhu cầu giải tr&iacute; hằng ng&agrave;y.</p>', 'Máy tính bảng Samsung Galaxy Tab A7 Lite là phiên bản rút gọn của dòng tablet \"ăn khách\" Galaxy Tab A7 thuộc thương hiệu Samsung, đáp ứng nhu cầu giải trí của khách hàng thuộc phân khúc bình dân với màn hình lớn nhưng vẫn gọn nhẹ hợp túi tiền.', 'Màn hình: 8.7\"TFT LCD\r\nHệ điều hành: Android 11\r\nChip: MediaTek MT8768T\r\nRAM: 3 GB\r\nBộ nhớ trong: 32 GB\r\nKết nối: Hỗ trợ 4GCó nghe gọi\r\nSIM: 1 Nano SIM', 100, 3990000, 4490000, '2022-07-25 00:27:19', '2022-08-05 08:13:07', NULL, 'samsung-galaxy-tab-a7-lite', 4, 0, 'Sản phẩm'),
(14, 17, 3, 'Galaxy Tab S8 Ultra', 'https://minhtrung.unitopcv.com/admin/storage/app/public/photos/9/images/tablet/Samsung-Galaxy-tab-s8-black-thumb-600x600.jpg', '<h3 style=\"text-align: justify;\">Kh&ocirc;ng gian l&agrave;m việc rộng lớn</h3>\r\n<p style=\"text-align: justify;\">Samsung Galaxy Tab S8 Ultra trang bị tấm nền Super AMOLED với k&iacute;ch thước l&ecirc;n đến 14.6 inch, điều n&agrave;y gi&uacute;p cho h&igrave;nh ảnh hiển thị được rực rỡ v&agrave; bắt mắt hơn khi trải nghiệm tr&ecirc;n một chiếc m&agrave;n h&igrave;nh khủng.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104912.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Tấm nền chất lượng - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104912.jpg\" alt=\"Tấm nền chất lượng - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104912.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-hd-la-gi-905777\" target=\"_blank\" rel=\"noopener\">M&agrave;n h&igrave;nh Super AMOLED l&agrave; g&igrave;? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh SUPER AMOLED?</a></p>\r\n<p style=\"text-align: justify;\">B&ecirc;n cạnh đ&oacute; Galaxy Tab S8 Ultra c&ograve;n được hỗ trợ tần số qu&eacute;t l&ecirc;n đến 120 Hz, giờ đ&acirc;y người d&ugrave;ng c&oacute; thể cảm nhận độ trơn mượt tr&ecirc;n mọi thao t&aacute;c vuốt chạm, hay chơi những tựa game FPS cao được đ&atilde; mắt hơn bao giờ hết.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104914.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thao t&aacute;c mượt m&agrave; - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104914.jpg\" alt=\"Thao t&aacute;c mượt m&agrave; - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104914.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Cấu h&igrave;nh mạnh mẽ h&agrave;ng đầu</h3>\r\n<p style=\"text-align: justify;\">Galaxy Tab S8 Ultra được <a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" target=\"_blank\" rel=\"noopener\">Samsung</a> trang bị b&ecirc;n trong con chip c&oacute; hiệu năng mạnh mẽ h&agrave;ng đầu tr&ecirc;n c&aacute;c thiết bị Android (05/2022) c&oacute; t&ecirc;n Snapdragon 8 Gen 1, gi&uacute;p xử l&yacute; c&aacute;c c&ocirc;ng việc như chỉnh sửa video nhanh ch&oacute;ng hay chiến game đồ họa cao một c&aacute;ch mượt m&agrave;.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104916.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104916.jpg\" alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104916.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Nhằm mang đến sự tối ưu d&agrave;nh cho những bạn l&agrave;m việc tr&ecirc;n c&aacute;c tr&igrave;nh duyệt web, Samsung trang bị cho m&aacute;y 8 GB RAM để gi&uacute;p bạn c&oacute; thể l&agrave;m việc v&agrave; thao t&aacute;c nhiều tab c&ugrave;ng một l&uacute;c m&agrave; &iacute;t khi gặp phải t&igrave;nh trạng tải lại từ đầu.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104918.jpg\"><img class=\"lazyload\" title=\"Trang bị 8 GB RAM - Samsung Galaxy Tab S8 Ultra\" alt=\"Trang bị 8 GB RAM - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104918.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Khả năng chụp ảnh sắc n&eacute;t</h3>\r\n<p style=\"text-align: justify;\">Mặt sau của Galaxy Tab S8 Ultra sẽ l&agrave; cụm 2 camera c&oacute; độ ph&acirc;n giải lần lượt l&agrave; 12 MP v&agrave; 6 MP gi&uacute;p c&aacute;c t&aacute;c vụ chụp văn bản c&oacute; độ chi tiết cao, ngo&agrave;i ra bạn cũng c&oacute; thể ghi được những thước phim chất lượng th&ocirc;ng qua t&iacute;nh năng quay video chuẩn 4K.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104920.jpg\"><img class=\"lazyload\" title=\"Camera chất lượng - Samsung Galaxy Tab S8 Ultra\" alt=\"Camera chất lượng - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104920.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Ph&iacute;a trước phần m&agrave;n h&igrave;nh sẽ l&agrave; cụm 2 camera c&oacute; chung độ ph&acirc;n giải 12 MP, Galaxy Tab S8 Ultra mang lại cho bạn khả năng selfie tốt hơn hay thực hiện c&aacute;c t&aacute;c vụ video call được r&otilde; r&agrave;ng.</p>\r\n<h3 style=\"text-align: justify;\">Thiết kế hợp xu hướng</h3>\r\n<p style=\"text-align: justify;\">Galaxy Tab S8 Ultra c&oacute; một diện mạo vu&ocirc;ng vức với c&aacute;c cạnh v&agrave; mặt lưng được gia c&ocirc;ng phẳng, đem đến một c&aacute;i nh&igrave;n thanh lịch, sang trọng v&agrave; v&ocirc; c&ugrave;ng hiện đại.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104922.jpg\"><img class=\"lazyload\" title=\"Diện mạo hợp xu hướng - Samsung Galaxy Tab S8 Ultra\" alt=\"Diện mạo hợp xu hướng - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104922.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Với chất liệu nh&ocirc;m nguy&ecirc;n khối v&agrave; được ho&agrave;n thiện mặt lưng c&oacute; độ nh&aacute;m nhẹ, nhờ vậy m&agrave; Galaxy Tab S8 Ultra sở hữu một độ bền bỉ cao c&ugrave;ng khả năng hạn chế được c&aacute;c vết b&aacute;m mồ h&ocirc;i v&agrave; dấu v&acirc;n tay trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<h3 style=\"text-align: justify;\">Trang bị vi&ecirc;n pin cực khủng</h3>\r\n<p style=\"text-align: justify;\">B&ecirc;n trong một thiết bị c&oacute; k&iacute;ch thước si&ecirc;u mỏng chỉ với 5.5 mm l&agrave; một vi&ecirc;n pin lớn c&oacute; dung lượng 11200 mAh, đ&acirc;y được xem l&agrave; điểm vượt trội tr&ecirc;n Galaxy Tab S8 Ultra so với những chiếc <a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">tablet</a> kh&aacute;c tr&ecirc;n thị trường, vậy n&ecirc;n giờ đ&acirc;y người d&ugrave;ng c&oacute; thể thoải m&aacute;i sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến vấn đề hết pin.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104924.jpg\"><img class=\"lazyload\" title=\"Sử dụng d&agrave;i l&acirc;u  -Samsung Galaxy Tab S8 Ultra\" alt=\"Sử dụng d&agrave;i l&acirc;u  -Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104924.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Với một kh&ocirc;ng gian hiển thị rộng lớn v&agrave; một cấu h&igrave;nh mạnh mẽ, Galaxy Tab S8 Ultra sẽ l&agrave; một sự lựa chọn kh&ocirc;ng thể bỏ qua đối với những ai đang băn khoăn muốn chọn mua cho m&igrave;nh một thiết bị phục vụ cho c&ocirc;ng việc, hay d&ugrave;ng để chơi game cấu h&igrave;nh cao.</p>', 'Samsung Galaxy Tab S8 Ultra ra mắt với kích thước màn hình siêu to cùng một cấu hình mạnh mẽ, được xem là thiết bị phù hợp đối với những ai thường xuyên làm các công việc thiết kế hay trên trình duyệt web, bên cạnh đó Tab S8 Ultra còn mang đến những trải nghiệm tương tự một chiếc laptop khi sử dụng kèm với bàn phím.', 'Màn hình: 14.6\"Super AMOLED\r\nHệ điều hành: Android 12\r\nChip: Snapdragon 8 Gen 1\r\nRAM: 8 GB\r\nBộ nhớ trong: 128 GB\r\nKết nối: 5GCó nghe gọi\r\nSIM: 1 Nano SIM', 100, 29990000, 30990000, '2022-07-25 00:34:57', '2022-07-25 00:34:57', NULL, 'galaxy-tab-s8-ultra', 0, 0, 'Sản phẩm'),
(15, 16, 11, 'Xiaomi Redmi Note 11', 'https://cdn.tgdd.vn/Products/Images/42/269832/Xiaomi-redmi-note-11-blue-600x600.jpeg', '<h3 style=\"text-align: justify;\"><a title=\"Xiaomi Redmi Note 11 (4/128GB)\" href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-11-4gb-128gb\" target=\"_blank\" rel=\"noopener\">Xiaomi Redmi Note 11 (4GB/128GB)</a> ch&iacute;nh thức cập bến ph&acirc;n kh&uacute;c <a title=\"Tham khảo gi&aacute; điện thoại smartphone ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a> gi&aacute; tầm trung, cấu h&igrave;nh kh&aacute; ổn từ chip Snapdragon v&agrave; một ngoại h&igrave;nh trẻ trung, sang đẹp c&ugrave;ng cụm camera chất lượng, sẽ l&agrave; lựa chọn tốt vừa t&uacute;i tiền cho mọi kh&aacute;ch h&agrave;ng.</h3>\r\n<h3 style=\"text-align: justify;\">Thiết kế đậm chất Xiaomi</h3>\r\n<p style=\"text-align: justify;\">Phi&ecirc;n bản Redmi Note 11 n&agrave;y c&oacute; c&aacute;c lựa chọn m&agrave;u sắc với x&aacute;m v&agrave; xanh dương. Thiết kế thanh mảnh, c&aacute;c cạnh bo cong nhẹ tinh tế, điện thoại cho cảm gi&aacute;c cầm kh&aacute; thoải m&aacute;i.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-260122-045721.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;u sắc trẻ trung | Xiaomi Redmi Note 11\" src=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-260122-045721.jpg\" alt=\"M&agrave;u sắc trẻ trung | Xiaomi Redmi Note 11\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-260122-045721.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Redmi Note 11 c&oacute; m&agrave;n h&igrave;nh 6.43 inch, c&aacute;c cạnh viền mỏng kết hợp camera đục lỗ cho kh&ocirc;ng gian trải nghiệm mở rộng. M&agrave;n h&igrave;nh AMOLED cho độ n&eacute;t v&agrave; m&agrave;u sắc chi tiết tốt, tươi s&aacute;ng, rực rỡ, độ ph&acirc;n giải đạt chuẩn Full HD+, chất lượng khung h&igrave;nh hiển thị rất tuyệt so với tầm gi&aacute; của sản phẩm.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-2.jpg\"><img class=\" ls-is-cached lazyloaded\" style=\"text-align: justify;\" title=\"M&agrave;n h&igrave;nh đục lỗ - Xiaomi Redmi Note 11 (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-2.jpg\" alt=\"M&agrave;n h&igrave;nh đục lỗ - Xiaomi Redmi Note 11 (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-2.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Đặc biệt, tần số qu&eacute;t m&agrave;n h&igrave;nh l&ecirc;n đến 90 Hz cho cảm gi&aacute;c chạm, vuốt, cuộn m&agrave;n h&igrave;nh cực mượt, chuyển cảnh độ trễ thấp cho khung h&igrave;nh lu&ocirc;n liền mạch, l&agrave; điểm cộng khi sử dụng điện thoại để chơi c&aacute;c tựa game tốc độ, game h&agrave;nh động.</p>\r\n<p style=\"text-align: justify;\">M&aacute;y trang bị <a title=\"Tham khảo điện thoại c&oacute; mở kh&oacute;a bằng v&acirc;n tay tại TGDĐ\" href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" rel=\"noopener\">bảo mật v&acirc;n tay</a> cạnh viền thao t&aacute;c thuận tiện, truy cập m&agrave;n h&igrave;nh nhanh v&agrave; bảo mật tốt.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-260122-045714.jpg\"><img class=\" lazyloaded\" title=\"Cảm biến v&acirc;n tay cạnh viền | Xiaomi Redmi Note 11\" src=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-260122-045714.jpg\" alt=\"Cảm biến v&acirc;n tay cạnh viền | Xiaomi Redmi Note 11\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/269832/xiaomi-redmi-note-11-4gb-128gb-260122-045714.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Cấu h&igrave;nh ổn định, đ&aacute;p ứng tốt mọi t&aacute;c vụ cơ bản</h3>\r\n<p style=\"text-align: justify;\">Chip Snapdragon 680 c&ugrave;ng với chip đồ họa Adreno&nbsp;610 v&agrave; <a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" rel=\"noopener\">RAM 4 GB</a> cho khả năng xử l&yacute; kh&aacute; mạnh mẽ v&agrave; mượt m&agrave;, c&aacute;c t&aacute;c vụ lướt web, nghe nhạc, xem phim, chỉnh sửa ảnh,&hellip; đều trơn tru v&agrave; ổn định, thậm ch&iacute; bạn c&oacute; thể với c&aacute;c tựa game mobile như: Li&ecirc;n Qu&acirc;n Mobile, PUBG Mobile cấu h&igrave;nh tầm trung tr&ecirc;n m&aacute;y.</p>\r\n<p style=\"text-align: justify;\"><a title=\"Tham khảo gi&aacute; điện thoại smartphone Xiaomi ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" rel=\"noopener\">Điện thoại Xiaomi</a>&nbsp;c&oacute; bộ nhớ trong 128 GB đủ d&ugrave;ng cho kho h&igrave;nh ảnh v&agrave; t&agrave;i liệu c&aacute; nh&acirc;n tr&ecirc;n m&aacute;y. Hệ điều h&agrave;nh Android 11 giao diện MIUI 13 th&acirc;n thiện dễ d&ugrave;ng, rất thoải m&aacute;i khi trải nghiệm.</p>\r\n<h3 style=\"text-align: justify;\">Cụm camera chất lượng để trải nghiệm</h3>\r\n<p style=\"text-align: justify;\">Được thiết kế trong m&ocirc;-đun đặt nổi đẹp mặt, cụm camera sau của Redmi Note 11 bao gồm camera ch&iacute;nh 50 MP, một camera 8 MP v&agrave; 2 camera c&ugrave;ng độ ph&acirc;n giải 2 MP, hỗ trợ quay phim v&agrave; chụp ảnh chất lượng cao với c&aacute;c hiệu ứng l&agrave;m đẹp th&ocirc;ng minh, c&acirc;n bằng tốt m&agrave;u sắc v&agrave; &aacute;nh s&aacute;ng cho bức ảnh ch&acirc;n thực, c&oacute; độ n&eacute;t cao.</p>\r\n<p style=\"text-align: justify;\">C&aacute;c t&iacute;nh năng HDR, chụp cận, x&oacute;a ph&ocirc;ng, chụp to&agrave;n cảnh,&hellip; l&agrave;m gi&agrave;u lựa chọn s&aacute;ng tạo để người d&ugrave;ng tạo ra những bức ảnh nghệ thuật ưng &yacute; bằng ch&iacute;nh smartphone c&aacute; nh&acirc;n của m&igrave;nh.</p>\r\n<p style=\"text-align: justify;\">Camera selfie 13 MP cũng được hỗ trợ c&aacute;c t&iacute;nh năng AI để cho c&aacute;c bức ảnh selfie đẹp ho&agrave;n chỉnh, kh&ocirc;ng cần chỉnh sửa cầu kỳ qua c&aacute;c bộ lọc, v&agrave; bạn cũng c&oacute; thể thoải m&aacute;i tr&ograve; chuyện, giao tiếp với bạn b&egrave; từ xa th&ocirc;ng qua c&aacute;c ứng dụng video call.</p>\r\n<h3 style=\"text-align: justify;\">Dung lượng pin tốt cho ng&agrave;y d&agrave;i sử dụng</h3>\r\n<p style=\"text-align: justify;\">Sở hữu vi&ecirc;n pin dung lượng 5000 mAh&nbsp;bền bỉ c&ugrave;ng với c&ocirc;ng nghệ si&ecirc;u tiết kiệm pin đạt hiệu quả tốt từ con chip sử dụng, smartphone n&agrave;y cho ph&eacute;p bạn thoải m&aacute;i đ&agrave;m thoại, xem video hay chơi game nhiều giờ liền m&agrave; kh&ocirc;ng &aacute;i ngại việc cạn nguồn qu&aacute; nhanh.</p>\r\n<p style=\"text-align: justify;\">C&ugrave;ng với c&ocirc;ng nghệ <a title=\"Tham khảo điện thoại c&oacute; sạc nhanh tại Thegioididong.com \" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">sạc pin nhanh</a> 33 W, việc kh&ocirc;i phục 100% dung lượng pin cho điện thoại sẽ kh&ocirc;ng mất nhiều thời gian chờ, y&ecirc;n t&acirc;m sử dụng điện thoại theo nhu cầu.</p>\r\n<p style=\"text-align: justify;\">Xiaomi Redmi Note 11 (4GB/128GB) sở hữu được nhiều ưu điểm trong cả thiết kế v&agrave; cấu h&igrave;nh, rất đ&aacute;ng l&agrave; 1 sự chờ mong cho kh&aacute;ch h&agrave;ng mục ti&ecirc;u m&agrave; h&atilde;ng nhắm tới.</p>', 'Xiaomi Redmi Note 11 (4GB/128GB) chính thức cập bến phân khúc smartphone giá tầm trung, cấu hình khá ổn từ chip Snapdragon và một ngoại hình trẻ trung, sang đẹp cùng cụm camera chất lượng, sẽ là lựa chọn tốt vừa túi tiền cho mọi khách hàng.', 'Màn hình: AMOLED6.43\"Full HD+\r\nHệ điều hành: Android 11\r\nCamera sau: Chính 50 MP & Phụ 8 MP, 2 MP, 2 MP\r\nCamera trước: 13 MP\r\nChip: Snapdragon 680 8 nhân\r\nRAM:4 GB\r\nBộ nhớ trong: 128 GB', 100, 4490000, 4990000, '2022-08-05 03:04:22', '2022-08-05 10:04:42', NULL, 'xiaomi-redmi-note-11', 1, 0, 'Sản phẩm,Xiaomi,Note 11'),
(16, 9, 11, 'Xiaomi Redmi 10A', 'https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-thumb-600x600.jpg', '<h3 style=\"text-align: justify;\">Diện mạo trẻ trung</h3>\r\n<p style=\"text-align: justify;\">Redmi 10A c&oacute; một thiết kế kh&aacute; đơn giản, mặt trước l&agrave; m&agrave;n h&igrave;nh giọt nước với c&aacute;c cạnh mỏng, chỉ c&oacute; cạnh dưới hơi d&agrave;y. Ph&iacute;a mặt lưng c&oacute; kết cấu đường v&acirc;n nổi chạy dọc m&aacute;y, mang đến một cảm gi&aacute;c cầm nắm chắc chắn hơn, chống trơn trượt. Xiaomi đ&atilde; đơn giản h&oacute;a mặt lưng khi đưa chung m&ocirc;-đun camera v&agrave; cảm biến v&acirc;n tay v&agrave;o một cụm.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-160722-081536.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Diện mạo trẻ trung - Redmi 10A\" src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-160722-081536.jpg\" alt=\"Diện mạo trẻ trung - Redmi 10A\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-160722-081536.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Hơn nữa&nbsp;Redmi 10A c&ograve;n c&oacute; nhiều m&agrave;u sắc trẻ trung đ&oacute; l&agrave;: X&aacute;m, xanh dương, bạc. Từ đ&oacute; cung cấp cho kh&aacute;ch h&agrave;ng sự lựa chọn để đ&aacute;nh bật c&aacute; t&iacute;nh.</p>\r\n<h3 style=\"text-align: justify;\">M&agrave;n h&igrave;nh 6.53 inch lớn</h3>\r\n<p style=\"text-align: justify;\">Xiaomi trang bị cho m&aacute;y m&agrave;n h&igrave;nh k&iacute;ch thước lớn l&ecirc;n đến 6.53 inch với độ ph&acirc;n giải HD+ v&agrave; tỉ lệ khung h&igrave;nh chuẩn 20 : 9. M&agrave;n h&igrave;nh lớn của thiết bị hứa hẹn sẽ đem đến kh&ocirc;ng gian hiển thị rộng hơn, dễ d&agrave;ng xem phim một c&aacute;ch r&otilde; n&eacute;t.&nbsp;</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-310322-095233.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh 6.53 inch lớn - Xiaomi Redmi 10A\" src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-310322-095233.jpg\" alt=\"M&agrave;n h&igrave;nh 6.53 inch lớn - Xiaomi Redmi 10A\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-310322-095233.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">M&agrave;n h&igrave;nh lớn c&ograve;n dễ d&agrave;ng đọc nội dung của một trang s&aacute;ch, b&aacute;o hoặc tin tức, hay chat c&ugrave;ng bạn b&egrave; cũng th&ecirc;m nhanh v&agrave; tiện lợi.</p>\r\n<h3 style=\"text-align: justify;\">Camera chụp ảnh r&otilde; n&eacute;t</h3>\r\n<p style=\"text-align: justify;\">Redmi 10A được trang bị camera 13 MP v&agrave; một camera phụ 2 MP ở mặt lưng, hỗ trợ chụp những bức ảnh một c&aacute;ch r&otilde; n&eacute;t hơn. Ph&iacute;a trước thiết bị c&oacute; ống k&iacute;nh 5 triệu điểm ảnh gi&uacute;p bức ảnh th&ecirc;m phần ảo diệu.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-160722-081538.jpg\"><img class=\" lazyloaded\" title=\"Camera chụp ảnh r&otilde; n&eacute;t - Redmi 10A\" src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-160722-081538.jpg\" alt=\"Camera chụp ảnh r&otilde; n&eacute;t - Redmi 10A\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-160722-081538.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Pin khủng kh&ocirc;ng lo hết</h3>\r\n<p style=\"text-align: justify;\"><a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">Điện thoại</a> sở hữu pin dung lượng cao l&ecirc;n đến 5000 mAh, Xiaomi hứa hẹn thời gian nghe nhạc l&ecirc;n đến cả tuần, xem phim từ s&aacute;ng đến tối, gi&uacute;p đi chơi thoải m&aacute;i m&agrave; kh&ocirc;ng c&ograve;n lo hết pin nữa.&nbsp;</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-310322-095229.jpg\"><img class=\" lazyloaded\" title=\"Pin khủng kh&ocirc;ng lo hết - Xiaomi Redmi 10A\" src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-310322-095229.jpg\" alt=\"Pin khủng kh&ocirc;ng lo hết - Xiaomi Redmi 10A\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271734/xiaomi-redmi-10a-310322-095229.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Theo th&ocirc;ng tin từ Xiaomi, Redmi 10A c&oacute; chế độ chờ l&ecirc;n đến 35 ng&agrave;y, c&oacute; thể ph&aacute;t nhạc trong 6 ng&agrave;y, xem video 19 giờ, gọi điện 32 giờ.</p>\r\n<p style=\"text-align: justify;\">Kh&ocirc;ng cần phải n&oacute;i cũng biết Redmi 10A l&agrave; một sản phẩm gi&aacute; rẻ c&oacute; nhiều t&iacute;nh năng tốt, khi sở hữu m&agrave;n h&igrave;nh lớn, camera chụp ảnh r&otilde; n&eacute;t, hơn nữa lại c&oacute; pin khủng v&agrave; thiết kế thời trang, đ&uacute;ng thật l&agrave; một sản phẩm đ&aacute;ng sắm.</p>', 'Xiaomi đã bổ sung dòng điện thoại giá rẻ bằng cách tung ra thị trường thiết bị mới có tên Xiaomi Redmi 10A, nổi bật với thiết kế đơn giản mà tinh tế, màn hình hiển thị lớn và có pin dung lượng lên đến 5000 mAh.', 'Màn hình: IPS LCD6.53\"HD+\r\nHệ điều hành: Android 11\r\nCamera sau: Chính 13 MP & Phụ 2 MP\r\nCamera trước: 5 MP\r\nChip: MediaTek Helio G25\r\nRAM: 2 GB\r\nBộ nhớ trong: 32 GB\r\nSIM: 2 Nano SIMHỗ trợ 4G', 100, 2590000, 2890000, '2022-08-05 03:08:09', '2022-08-05 03:28:56', NULL, 'xiaomi-redmi-10a', 0, 0, 'Sản phẩm'),
(17, 9, 8, 'iPhone 11', 'https://cdn.tgdd.vn/Products/Images/42/153856/iphone-xi-xanhla-600x600.jpg', '<h3>N&acirc;ng cấp mạnh mẽ về camera</h3>\r\n<p>N&oacute;i về n&acirc;ng cấp th&igrave; camera ch&iacute;nh l&agrave; điểm c&oacute; nhiều cải tiến nhất tr&ecirc;n thế hệ <a title=\"Tham khảo gi&aacute; điện thoại smartphone iPhone ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">iPhone</a> mới.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Điện thoại iPhone 11 64GB | Thiết kế nhiều m&agrave;u sắc\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\" alt=\"Điện thoại iPhone 11 64GB | Thiết kế nhiều m&agrave;u sắc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\" /></a></p>\r\n<p>Nếu như trước đ&acirc;y iPhone Xr chỉ c&oacute; một camera th&igrave; nay với <a title=\"Tham khảo điện thoại iPhone 11 đang kinh doanh tại TopZone thương hiệu của Thế Giới Di Động\" href=\"https://www.topzone.vn/iphone-11\" target=\"_blank\" rel=\"noopener\">iPhone 11</a> ch&uacute;ng ta sẽ c&oacute; tới hai camera ở mặt sau.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Điện thoại iPhone 11 64GB | Camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\" alt=\"Điện thoại iPhone 11 64GB | Camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\" /></a></p>\r\n<p>Ngo&agrave;i camera ch&iacute;nh vẫn c&oacute; độ ph&acirc;n giải 12 MP th&igrave; ch&uacute;ng ta sẽ c&oacute; th&ecirc;m một camera g&oacute;c si&ecirc;u rộng v&agrave; cũng với độ ph&acirc;n giải tương tự.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ ch&acirc;n dung\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\" alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ ch&acirc;n dung\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em><span style=\"text-align: center;\">Ảnh chụp ch&acirc;n dung với iPhone 11</span></em></p>\r\n<p>Theo Apple th&igrave; việc chuyển đổi qua lại giữa hai ống k&iacute;nh th&igrave; sẽ kh&ocirc;ng l&agrave;m thay đổi m&agrave;u sắc của bức ảnh.</p>\r\n<p style=\"text-align: center;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-2-1.jpg\"><img class=\" lazyloaded\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ ch&acirc;n dung\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-2-1.jpg\" alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ ch&acirc;n dung\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-2-1.jpg\" /></a></p>\r\n<p style=\"text-align: center;\"><em>Ảnh chụp chế độ ch&acirc;n dung với iPhone 11</em></p>\r\n<p>Đ&acirc;y l&agrave; một điều được xem l&agrave; bước ngoặt bởi những chiếc <a title=\"Tham khảo gi&aacute; điện thoại smartphone ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a> Android c&oacute; nhiều camera hiện nay sẽ thường bị sai lệch về m&agrave;u sắc khi chuyển đổi qua lại giữa c&aacute;c ống k&iacute;nh g&acirc;y cảm gi&aacute;c kh&aacute; kh&oacute; chịu cho người d&ugrave;ng.</p>', 'Apple đã chính thức trình làng bộ 3 siêu phẩm iPhone 11, trong đó phiên bản iPhone 11 64GB có mức giá rẻ nhất nhưng vẫn được nâng cấp mạnh mẽ như iPhone Xr ra mắt trước đó.', 'Màn hình: IPS LCD6.1\"Liquid Retina\r\nHệ điều hành: iOS 15\r\nCamera sau: 2 camera 12 MP\r\nCamera trước: 12 MP\r\nChip: Apple A13 Bionic\r\nRAM: 4 GB\r\nBộ nhớ trong: 64 GB', 100, 13000000, 15000000, '2022-08-05 03:28:10', '2022-08-05 03:29:06', NULL, 'iphone-11', 0, 0, 'Sản phẩm,Iphone,Iphone11');
INSERT INTO `products` (`id`, `user_id`, `cat_id`, `name`, `thumbnail`, `content`, `summary`, `description`, `qty`, `price`, `old_price`, `created_at`, `updated_at`, `deleted_at`, `slug`, `num_views`, `num_purchases`, `tags`) VALUES
(18, 9, 8, 'iPhone 13 Pro Max', 'https://cdn.tgdd.vn/Products/Images/42/230529/TimerThumb/iphone-13-pro-max-(16).jpg', '<h3 style=\"text-align: justify;\">Thiết kế đẳng cấp h&agrave;ng đầu</h3>\r\n<p style=\"text-align: justify;\">iPhone mới kế thừa thiết kế đặc trưng từ&nbsp;iPhone 12 Pro Max khi sở hữu khung viền vu&ocirc;ng vức, mặt lưng k&iacute;nh c&ugrave;ng m&agrave;n h&igrave;nh tai thỏ tr&agrave;n viền nằm ở ph&iacute;a trước.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-3.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế vu&ocirc;ng vức đặc trưng - iPhone 13 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-3.jpg\" alt=\"Thiết kế vu&ocirc;ng vức đặc trưng - iPhone 13 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-3.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Với iPhone 13 Pro Max phần tai thỏ đ&atilde; được thu gọn lại 20% so với thế hệ trước, kh&ocirc;ng chỉ giải ph&oacute;ng nhiều kh&ocirc;ng gian hiển thị hơn m&agrave; c&ograve;n gi&uacute;p mặt trước chiếc <a title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a> trở n&ecirc;n hấp dẫn hơn m&agrave; vẫn đảm bảo được hoạt động của c&aacute;c cảm biến.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-2.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh tai thỏ được tinh giản - iPhone 13 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-2.jpg\" alt=\"M&agrave;n h&igrave;nh tai thỏ được tinh giản - iPhone 13 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-2.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Điểm thay đổi dễ d&agrave;ng nhận biết tr&ecirc;n iPhone 13 Pro Max ch&iacute;nh l&agrave; k&iacute;ch thước của cảm biến camera sau được l&agrave;m to hơn v&agrave; để tăng độ nhận diện cho sản phẩm mới th&igrave; Apple cũng đ&atilde; bổ sung một t&ugrave;y chọn m&agrave;u sắc&nbsp;Sierra Blue (m&agrave;u xanh dương nhạt hơn so với Pacific Blue của iPhone 12 Pro Max).</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-9.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Sierra Blue trẻ trung, thanh lịch - iPhone 13 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-9.jpg\" alt=\"Sierra Blue trẻ trung, thanh lịch - iPhone 13 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-9.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">M&aacute;y vẫn tiếp tục sử dụng khung viền th&eacute;p cao cấp cho khả năng chống trầy xước v&agrave; va đập một c&aacute;ch vượt trội, kết hợp với khả năng kh&aacute;ng bụi, nước&nbsp;chuẩn IP68.</p>\r\n<p style=\"text-align: justify;\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tieu-chuan-chong-nuoc-ip68-tren-smartphone-la-gi-co-cong-1396848\" target=\"_blank\" rel=\"noopener\">Ti&ecirc;u chuẩn chống nước IP68 tr&ecirc;n smartphone l&agrave; g&igrave;? C&oacute; c&ocirc;ng dụng g&igrave;?</a></p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-4.jpg\"><img class=\" lazyloaded\" title=\"Viền th&eacute;p cao cấp - iPhone 13 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-4.jpg\" alt=\"Viền th&eacute;p cao cấp - iPhone 13 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-4.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">M&agrave;n h&igrave;nh giải tr&iacute; si&ecirc;u mượt c&ugrave;ng tần số qu&eacute;t 120 Hz</h3>\r\n<p style=\"text-align: justify;\">iPhone 13 Pro Max được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước 6.7 inch c&ugrave;ng độ ph&acirc;n giải 1284 x 2778 Pixels, sử dụng tấm nền OLED c&ugrave;ng c&ocirc;ng nghệ Super Retina XDR cho khả năng tiết kiệm năng lượng vượt trội nhưng vẫn đảm bảo hiển thị sắc n&eacute;t sống động ch&acirc;n thực.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-5.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh k&iacute;ch thước 6.7 inch - iPhone 13 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-5.jpg\" alt=\"M&agrave;n h&igrave;nh k&iacute;ch thước 6.7 inch - iPhone 13 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-5.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">iPhone Pro Max năm nay đ&atilde; được n&acirc;ng cấp l&ecirc;n tần số qu&eacute;t 120 Hz, mọi thao t&aacute;c chuyển cảnh khi lướt ng&oacute;n tay tr&ecirc;n m&agrave;n h&igrave;nh trở n&ecirc;n mượt m&agrave; hơn đồng thời hiệu ứng thị gi&aacute;c khi ch&uacute;ng ta chơi game hoặc xem video cũng cực kỳ m&atilde;n nh&atilde;n.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-6.jpg\"><img class=\" lazyloaded\" title=\"Tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh - iPhone 13 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-6.jpg\" alt=\"Tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh - iPhone 13 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230529/iphone-13-pro-max-6.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">C&ugrave;ng c&ocirc;ng nghệ ProMotion th&ocirc;ng minh c&oacute; thể thay đổi tần số qu&eacute;t từ 10 đến 120 lần mỗi gi&acirc;y t&ugrave;y thuộc v&agrave;o ứng dụng, thao t&aacute;c bạn đang sử dụng, nhằm tối ưu thời lượng sử dụng pin v&agrave; trải nghiệm của bạn.</p>\r\n<p style=\"text-align: justify;\">Nếu bạn d&ugrave;ng iPhone 13 Pro Max để chơi game, tần số qu&eacute;t 120 Hz kết hợp hiệu suất đồ họa tuyệt vời của GPU sẽ khiến m&aacute;y trở n&ecirc;n v&ocirc; c&ugrave;ng ho&agrave;n hảo khi trải nghiệm.</p>\r\n<p style=\"text-align: justify;\">Ngo&agrave;i ra m&aacute;y cũng hỗ trợ c&ocirc;ng nghệ True Tone, dải m&agrave;u rộng chuẩn điện ảnh P3 sẽ cho mọi trải nghiệm của bạn tr&ecirc;n m&aacute;y trở n&ecirc;n ấn tượng hơn bao giờ hết.</p>\r\n<h3 style=\"text-align: justify;\">Cụm camera được n&acirc;ng cấp to&agrave;n diện</h3>\r\n<p style=\"text-align: justify;\">iPhone 13 Pro Max vẫn sẽ c&oacute; bộ camera 3 ống k&iacute;nh xếp xen kẽ th&agrave;nh một cụm vu&ocirc;ng, đặt ở g&oacute;c tr&ecirc;n b&ecirc;n tr&aacute;i của lưng m&aacute;y gồm camera telephoto, camera g&oacute;c si&ecirc;u rộng&nbsp;v&agrave; camera ch&iacute;nh g&oacute;c rộng với khẩu độ f/1.5 si&ecirc;u lớn.</p>\r\n<p style=\"text-align: justify;\">Camera g&oacute;c si&ecirc;u rộng cũng được n&acirc;ng cấp với khẩu độ f/1.8, cảm biến nhanh hơn, mang tới những bức ảnh g&oacute;c si&ecirc;u rộng tự nhi&ecirc;n v&agrave; ch&acirc;n thực v&agrave; c&ograve;n tăng cường c&oacute; khả năng lấy n&eacute;t ở khoảng c&aacute;ch chỉ 2 cm, mang đến t&iacute;nh năng chụp ảnh quay phim macro đầy th&uacute; vị.</p>\r\n<p style=\"text-align: justify;\">Camera tele tr&ecirc;n iPhone 13 Pro Max c&oacute; thể zoom quang học 3x, ph&oacute;ng to h&igrave;nh ảnh v&agrave; video gấp 3 lần nhưng vẫn giữ nguy&ecirc;n chất lượng, hỗ trợ chụp ảnh ch&acirc;n dung <a title=\"Tham khảo gi&aacute; điện thoại smartphone camera chụp h&igrave;nh xo&aacute; ph&ocirc;ng\" href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\" rel=\"noopener\">x&oacute;a ph&ocirc;ng</a>, t&iacute;ch hợp sẵn rất nhiều bộ lọc m&agrave;u tự nhi&ecirc;n gi&uacute;p c&oacute; được bức ảnh đ&uacute;ng như &yacute; muốn.</p>\r\n<p style=\"text-align: justify;\">B&ecirc;n cạnh đ&oacute;, cảm biến LiDAR vẫn sẽ hiện diện tr&ecirc;n iPhone 13 Pro Max nhằm mang đến trải nghiệm thực tế tăng cường (AR) tốt nhất cho tất cả người d&ugrave;ng cũng như hỗ trợ cho cụm camera lấy n&eacute;t trong m&ocirc;i trường &aacute;nh s&aacute;ng yếu.</p>\r\n<p style=\"text-align: justify;\">Apple c&ograve;n tăng cường khả năng nhiếp ảnh tr&ecirc;n iPhone 13 Pro Max với chế độ quay phim chuẩn điện ảnh Cinematic&nbsp;gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng lấy n&eacute;t v&agrave;o chủ thể cả trong v&agrave; sau khi quay, đồng thời l&agrave;m mờ hậu cảnh v&agrave; những nh&acirc;n vật kh&aacute;c trong khung h&igrave;nh.</p>\r\n<p style=\"text-align: justify;\">Đ&acirc;y cũng l&agrave; chiếc điện thoại th&ocirc;ng minh đầu ti&ecirc;n cung cấp quy tr&igrave;nh l&agrave;m việc chuy&ecirc;n nghiệp \"end-to-end\"&nbsp;cho ph&eacute;p bạn ghi v&agrave; chỉnh sửa video ở định dạng n&eacute;n ProRes hoặc Dolby Vision với nhiều thiết lập chuy&ecirc;n s&acirc;u&nbsp;tạo&nbsp;gi&uacute;p r&uacute;t ngắn đ&aacute;ng kể qu&aacute; tr&igrave;nh hậu kỳ tạo n&ecirc;n những thước phim chất lượng đ&aacute;ng kinh ngạc ngay tr&ecirc;n ch&iacute;nh chiếc điện thoại của m&igrave;nh.</p>\r\n<h3 style=\"text-align: justify;\">Hiệu năng đầy hứa hẹn với Apple A15 Bionic&nbsp;</h3>\r\n<p style=\"text-align: justify;\">iPhone 13 Pro Max sẽ được trang bị bộ vi xử l&yacute; Apple A15 Bionic mới nhất của h&atilde;ng, được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm, đảm bảo mang lại hiệu năng vận h&agrave;nh ấn tượng m&agrave; vẫn tiết kiệm điện tốt nhất c&ugrave;ng khả năng hỗ trợ mạng 5G tốc độ si&ecirc;u cao.</p>\r\n<p style=\"text-align: justify;\">Theo Apple c&ocirc;ng bố, A15 Bionic l&agrave; chipset nhanh nhất trong thế giới smartphone (9/2021), c&oacute;&nbsp;tốc độ nhanh hơn 50% so với c&aacute;c chip kh&aacute;c tr&ecirc;n thị trường, c&oacute; thể thực hiện 15.8 ngh&igrave;n tỷ ph&eacute;p t&iacute;nh mỗi gi&acirc;y, gi&uacute;p hiệu năng CPU nhanh hơn bao giờ hết.</p>\r\n<p style=\"text-align: justify;\">M&aacute;y sở hữu bộ nhớ trong 128 GB, vừa đủ với nhu cầu sử dụng của một người d&ugrave;ng cơ bản, kh&ocirc;ng c&oacute; nhu cầu quay video qu&aacute; nhiều, ngo&agrave;i ra năm nay cũng c&oacute; th&ecirc;m phi&ecirc;n bản bộ nhớ trong l&ecirc;n đến 1TB, bạn c&oacute; thể c&acirc;n nhắc nếu c&oacute; nhu cầu lưu trữ cao.</p>\r\n<p style=\"text-align: justify;\">Ngo&agrave;i ra, m&aacute;y c&ograve;n được t&iacute;ch hợp c&ocirc;ng nghệ Wi-Fi 6, chuẩn kết nối kh&ocirc;ng d&acirc;y mới với việc trang bị nhiều băng tần 5G, tương th&iacute;ch với nhiều nh&agrave; mạng ở c&aacute;c quốc gia kh&aacute;c nhau, iPhone 13 Pro Max lu&ocirc;n cho tốc độ mạng tối đa, cho trải nghiệm xem phim 4K mượt m&agrave;, tải tệp tin trong chớp mắt, chơi game online kh&ocirc;ng độ trễ ở bất cứ đ&acirc;u.</p>\r\n<h3 style=\"text-align: justify;\">Bước nhảy vọt về thời lượng pin</h3>\r\n<p style=\"text-align: justify;\">iPhone Pro Max đ&aacute;nh dấu bước ngoặt mới trong thời lượng pin sử dụng. Với vi&ecirc;n pin dung lượng pin lớn kết hợp c&ugrave;ng m&agrave;n h&igrave;nh mới v&agrave; bộ vi xử l&yacute; Apple A15 Bionic tiết kiệm điện, gi&uacute;p iPhone 13 Pro Max trở th&agrave;nh chiếc iPhone c&oacute; thời lượng pin tốt nhất từ trước đến nay, d&agrave;i hơn 2.5 giờ so với người tiền nhiệm.&nbsp;</p>\r\n<p style=\"text-align: justify;\">Đ&aacute;ng tiếc l&agrave; dung lượng pin của c&aacute;c mẫu iPhone mới được cải thiện nhưng tốc độ sạc nhanh của ch&uacute;ng vẫn chỉ dừng ở mức 20 W qua kết nối c&oacute; d&acirc;y v&agrave; sạc qua MagSafe ở mức tối đa 15 W hoặc c&oacute; thể qua bộ sạc kh&ocirc;ng d&acirc;y dựa tr&ecirc;n Qi với c&ocirc;ng suất 7.5 W.</p>\r\n<p style=\"text-align: justify;\">Apple đ&atilde; kh&ocirc;ng ngừng cải tiến đem đến cho người d&ugrave;ng sản phẩm tốt nhất, iPhone 13 Pro Max 128GB vẫn giữ được c&aacute;c điểm nổi bật của người tiền nhiệm, nổi bật với cải tiến về cấu h&igrave;nh, thời lượng pin cũng như camera v&agrave; nhiều điều c&ograve;n chờ bạn kh&aacute;m ph&aacute;.</p>', 'Điện thoại iPhone 13 Pro Max 128 GB - siêu phẩm được mong chờ nhất ở nửa cuối năm 2021 đến từ Apple. Máy có thiết kế không mấy đột phá khi so với người tiền nhiệm, bên trong đây vẫn là một sản phẩm có màn hình siêu đẹp, tần số quét được nâng cấp lên 120 Hz mượt mà, cảm biến camera có kích thước lớn hơn, cùng hiệu năng mạnh mẽ với sức mạnh đến từ Apple A15 Bionic, sẵn sàng cùng bạn chinh phục mọi thử thách.', 'Màn hình: OLED6.7\"Super Retina XDR\r\nHệ điều hành: iOS 15\r\nCamera sau: 3 camera 12 MP\r\nCamera trước: 12 MP\r\nChip: Apple A15 Bionic\r\nRAM: 6 GB\r\nBộ nhớ trong: 128 GB', 100, 28490000, 33990000, '2022-08-05 03:32:01', '2022-08-05 03:32:01', NULL, 'iphone-13-pro-max', 0, 0, 'Sản phẩm,Iphone,Iphone 13 Pro Max'),
(19, 9, 8, 'iPhone 13 Pro', 'https://cdn.tgdd.vn/Products/Images/42/230521/TimerThumb/iphone-13-pro-(12).jpg', '<h3>Thiết kế đặc trưng với m&agrave;u sắc thời thượng</h3>\r\n<p>iPhone 13 Pro kh&ocirc;ng c&oacute; nhiều sự thay đổi về thiết kế, khi m&aacute;y vẫn sở hữu kiểu d&aacute;ng tương tự như <a href=\"https://www.thegioididong.com/dtdd/iphone-12-pro\">điện thoại&nbsp;iPhone 12 Pro</a>&nbsp;với c&aacute;c cạnh viền vu&ocirc;ng vắn v&agrave; hai mặt k&iacute;nh cường lực cao cấp. Sở hữu 4 phi&ecirc;n bản m&agrave;u gồm xanh dương, bạc, v&agrave;ng đồng v&agrave; x&aacute;m cho bạn t&ugrave;y chọn theo sở th&iacute;ch của m&igrave;nh.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Sở hữu 4 phi&ecirc;n bản m&agrave;u sắc trẻ trung - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-1.jpg\" alt=\"Sở hữu 4 phi&ecirc;n bản m&agrave;u sắc trẻ trung - iPhone 13 Pro 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-1.jpg\" /></a></p>\r\n<p>M&aacute;y đạt ti&ecirc;u chuẩn&nbsp;kh&aacute;ng nước và bụi&nbsp;IP68 c&oacute; khả năng chống lại một số hạt bụi, v&agrave; được bảo vệ khi rơi xuống nước ở độ s&acirc;u đến 6 m&eacute;t trong 30 ph&uacute;t&nbsp;theo chuẩn IEC 60529, thoải m&aacute;i nhắn tin khi lỡ ra ngo&agrave;i gặp mưa, chụp ảnh tự tin khi đi hồ bơi, b&atilde;i biển,...</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-2.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Khả năng kh&aacute;ng bụi, nước - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-2.jpg\" alt=\"Khả năng kh&aacute;ng bụi, nước - iPhone 13 Pro 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-2.jpg\" /></a></p>\r\n<h3>N&acirc;ng cấp m&agrave;n h&igrave;nh với tần số qu&eacute;t 120 Hz</h3>\r\n<p><a href=\"https://www.thegioididong.com/dtdd/iphone-13-pro\" target=\"_blank\" rel=\"noopener\">Điện thoại iPhone 13 Pro</a> vẫn được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước 6.1 inch, với phần tai thỏ được l&agrave;m nhỏ hơn gi&uacute;p hiển thị th&ecirc;m nhiều th&ocirc;ng tin cũng như tăng cường cảm gi&aacute;c trải nghiệm. Bề mặt c&oacute; lớp phủ oleophobic chống b&aacute;m v&acirc;n tay, giữ cho m&agrave;n h&igrave;nh lu&ocirc;n được sạch mới.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-man-hinh.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Trang bị m&agrave;n h&igrave;nh 6.1 inch - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-man-hinh.jpg\" alt=\"Trang bị m&agrave;n h&igrave;nh 6.1 inch - iPhone 13 Pro 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-man-hinh.jpg\" /></a></p>\r\n<p>iPhone 13 Pro đ&atilde; được n&acirc;ng cấp l&ecirc;n tần số qu&eacute;t 120 Hz, với ProMotion c&oacute; thể l&agrave;m mới từ 10 đến 120 khung h&igrave;nh/gi&acirc;y. Với tần số qu&eacute;t cao, mọi thao t&aacute;c chuyển cảnh khi lướt ng&oacute;n tay tr&ecirc;n m&agrave;n h&igrave;nh trở n&ecirc;n mượt m&agrave; hơn đồng thời hiệu ứng thị gi&aacute;c khi ch&uacute;ng ta d&ugrave;ng&nbsp;<a title=\"Tham khảo c&aacute;c mẫu điện thoại chơi game tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" rel=\"noopener\">điện thoại chơi game</a>&nbsp;hoặc xem video cũng cực kỳ m&atilde;n nh&atilde;n.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-5.jpg\"><img class=\" lazyloaded\" title=\"Chuyển động mượt m&agrave; nhờ tần số 120 Hz - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-5.jpg\" alt=\"Chuyển động mượt m&agrave; nhờ tần số 120 Hz - iPhone 13 Pro 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-5.jpg\" /></a></p>\r\n<p>Tốc độ l&agrave;m tươi tự động tăng l&ecirc;n để hiển thị hiệu suất đồ họa, tự động giảm xuống khi kh&ocirc;ng sử dụng để tiết kiệm năng lượng v&agrave; tốc độ l&agrave;m mới cũng được tự tinh chỉnh linh động để ph&ugrave; hợp hơn tốc độ lướt của ng&oacute;n tay bạn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-4.jpg\"><img class=\" lazyloaded\" title=\"Tốc độ l&agrave;m tươi được tự động h&oacute;a - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-4.jpg\" alt=\"Tốc độ l&agrave;m tươi được tự động h&oacute;a - iPhone 13 Pro 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-4.jpg\" /></a></p>\r\n<p>Sử dụng tấm nền OLED c&oacute; số điểm ảnh l&ecirc;n đến 1170 x 2532 Pixels, độ s&aacute;ng điển h&igrave;nh 1000 nits, khi hiển thị video HDR 1200 nits, tăng l&ecirc;n 25% khi đặt dưới &aacute;nh mặt trời, sắc đen hiển thị s&acirc;u, sắc s&aacute;ng tươi tắn, khung h&igrave;nh sinh động như thực.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-6.jpg\"><img class=\" lazyloaded\" title=\"Độ s&aacute;ng đạt 1200 nits - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-6.jpg\" alt=\"Độ s&aacute;ng đạt 1200 nits - iPhone 13 Pro 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-6.jpg\" /></a></p>\r\n<h3>Quay chụp đỉnh cao với camera Pro</h3>\r\n<p>iPhone 13 Pro có b&ocirc;̣ camera 3 &ocirc;́ng kính 12 MP x&ecirc;́p xen kẽ thành m&ocirc;̣t cụm vu&ocirc;ng, đặt ở góc tr&ecirc;n b&ecirc;n trái của lưng máy gồm camera tele, camera góc si&ecirc;u r&ocirc;̣ng (Ultra&nbsp;Wide) khẩu độ f/1.8, g&oacute;c nh&igrave;n đến 120 độ v&agrave; camera ch&iacute;nh g&oacute;c rộng (Wide) với khẩu độ f/1.5.</p>\r\n<p>Camera Ultra Wide được l&agrave;m mới c&oacute; thể lấy n&eacute;t tự động trong khoảng c&aacute;ch 2 cm, gi&uacute;p ph&oacute;ng to h&igrave;nh ảnh của giọt sương, l&ocirc;ng chim, c&aacute;t v&agrave;ng,... tạo n&ecirc;n những tuyệt t&aacute;c nghệ thuật chỉ bằng một thao t&aacute;c chụp đơn giản. Camera n&agrave;y cũng cho khả năng quay cảnh chuyển động tua nhanh thời gian hoặc chuyển động chậm linh hoạt để bạn l&agrave;m video giải tr&iacute; cực đẹp.&nbsp;</p>\r\n<p>Apple gọi iPhone 13 Pro l&agrave; thiết bị được tạo ra cho &aacute;nh s&aacute;ng yếu, bởi hệ thống camera được hỗ trợ chế độ ban đ&ecirc;m Night Mode, trang bị&nbsp;m&aacute;y qu&eacute;t LiDAR (khả dụng cho chế độ chụp ch&acirc;n dung ban đ&ecirc;m), camera Wide n&acirc;ng cao độ s&aacute;ng đến 2.2 lần, camera Ultra Wide tăng 92% độ s&aacute;ng cho ph&eacute;p quay chụp lại c&aacute;c h&igrave;nh ảnh, thước phim ban đ&ecirc;m cực kỳ chi tiết, sắc n&eacute;t.&nbsp;</p>\r\n<p>Hệ thống camera zoom quang học 6x, ri&ecirc;ng camera&nbsp;telephoto c&oacute; thể zoom quang 3x rất tuyệt khi chụp ảnh v&agrave; quay video ở xa. Kết hợp nhiều t&ugrave;y chọn hiệu ứng bokeh v&agrave; c&aacute;c hiệu ứng &aacute;nh s&aacute;ng chất lượng để bạn tạo n&ecirc;n những bức ảnh lung linh như nhiếp ảnh gia chuy&ecirc;n nghiệp.&nbsp;</p>\r\n<p>Một chế độ nổi bật kh&aacute;c hỗ trợ tr&ecirc;n camera Pro n&agrave;y l&agrave; chế độ&nbsp;điện ảnh Cinematic cho ph&eacute;p quay&nbsp;với độ s&acirc;u trường ảnh n&ocirc;ng, tự động th&ecirc;m c&aacute;c chuyển đổi lấy n&eacute;t giữa c&aacute;c đối tượng, đo&aacute;n trước chủ thể mới v&agrave;o khung h&igrave;nh v&agrave; đưa họ v&agrave;o ti&ecirc;u điểm, đổi ti&ecirc;u điểm, chỉnh hiệu ứng&nbsp;bokeh dễ d&agrave;ng kể cả sau khi quay.</p>\r\n<p>C&oacute; chế độ chụp phong cảnh Photographic Styles dễ d&agrave;ng điều chỉnh độ ấm, t&ocirc;ng m&agrave;u ph&ugrave; hợp với &yacute; tưởng chụp của bạn, tuy vậy kh&aacute;c với c&aacute;c bộ lọc th&ocirc;ng thường, với chế độ n&agrave;y c&aacute;c chi tiết như bầu trời, m&agrave;u da sẽ được giữ lại độ ch&acirc;n thật, tự nhi&ecirc;n nhất c&oacute; thể.&nbsp;</p>\r\n<p>Ở mặt trước điện thoại c&oacute; camera TrueDepth độ ph&acirc;n giải&nbsp;12 MP cho bạn tự selfie tuyệt đẹp, đi k&egrave;m nhiều chế độ chụp tiện &iacute;ch như chế độ ch&acirc;n dung t&iacute;ch hợp hiệu ứng bokeh n&acirc;ng cao v&agrave; kiểm so&aacute;t độ s&acirc;u&nbsp;mạnh mẽ, th&ecirc;m Animoji v&agrave; Memoji, chế độ Night Mode,&nbsp;Burst,&nbsp;Cinematic,...</p>\r\n<p>Hơn nữa, camera TrueDepth v&agrave; chip&nbsp;A15 Bionic hỗ trợ Face ID để x&aacute;c minh khu&ocirc;n mặt, gi&uacute;p mở m&aacute;y nhanh v&agrave; an to&agrave;n hơn.&nbsp;</p>\r\n<p>Cả camera trước v&agrave; sau đều quay được video&nbsp;HDR hỗ trợ Dolby Vision đạt chuẩn chất lượng l&ecirc;n đến 4K với tốc độ khung h&igrave;nh 60&nbsp;fps, quay video&nbsp;ProRes l&ecirc;n đến 1080p ở tốc độ 30&nbsp;fps,&nbsp;c&ugrave;ng nhiều chế độ quay kh&aacute;c gi&uacute;p bạn c&oacute; nhiều sự thay đổi kh&aacute;c nhau khi quay.</p>\r\n<h3>Hiệu năng đột ph&aacute; với Apple A15 Bionic</h3>\r\n<p>iPhone 13 Pro được trang bị bộ vi xử l&yacute; Apple A15 Bionic, sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm cải tiến, theo Apple c&ocirc;ng bố th&igrave; đ&acirc;y&nbsp;l&agrave; chipset nhanh nhất trong thế giới smartphone tại thời điểm 09/2021.</p>\r\n<p>T&iacute;ch hợp chip đồ họa GPU 5 l&otilde;i hiệu suất tăng l&ecirc;n 50%,&nbsp;CPU đ&aacute;p ứng đa dạng t&aacute;c vụ, n&acirc;ng cao tuổi thọ pin. Đặc biệt, c&ocirc;ng nghệ tr&iacute; tuệ nh&acirc;n tạo Neural Engine tăng tốc c&aacute;c t&aacute;c vụ m&aacute;y học, thực hiện được đến 15.8 ngh&igrave;n tỷ hoạt động mỗi gi&acirc;y, cho qu&aacute; tr&igrave;nh sử dụng thiết bị mượt m&agrave;, ổn định.</p>\r\n<p>M&aacute;y sở hữu con&nbsp;RAM 6 GB, bộ nhớ trong 128 GB, vừa đủ với nhu cầu sử dụng của một người d&ugrave;ng cơ bản kh&ocirc;ng c&oacute; nhu cầu quay video qu&aacute; nhiều, m&aacute;y cũng c&oacute; th&ecirc;m phi&ecirc;n bản <a href=\"https://www.thegioididong.com/dtdd/iphone-13-pro-1tb\">điện thoại&nbsp;iPhone 13 Pro 1 TB</a>, bạn c&oacute; thể c&acirc;n nhắc nếu c&oacute; nhu cầu lưu trữ cao.</p>\r\n<p>Ngo&agrave;i ra, m&aacute;y c&ograve;n được hỗ trợ mạng di động 5G&nbsp;chất lượng cao, c&oacute; khả năng phản hồi tức th&igrave;, việc tải xem, xem phim 4K,... sẽ v&ocirc; c&ugrave;ng nhanh ch&oacute;ng, đưa trải nghiệm internet của người d&ugrave;ng l&ecirc;n tầm cao mới.</p>\r\n<p>Thiết bị được c&agrave;i đặt nền tảng iOS 15 cho bạn vừa giao tiếp qua FaceTime vừa chia sẻ b&agrave;i h&aacute;t, t&agrave;i liệu với mọi người tiện lợi, dễ d&agrave;ng tương t&aacute;c với văn bản trong h&igrave;nh ảnh để gửi email, nhận chỉ đường, thực hiện cuộc gọi,...&nbsp;</p>\r\n<h3>N&acirc;ng cấp thời lượng pin</h3>\r\n<p>iPhone 13 Pro sẽ c&oacute; thời lượng pin sử dụng tốt hơn 1.5 tiếng khi so với iPhone 12 Pro, m&aacute;y c&oacute; thể ph&aacute;t video li&ecirc;n tục trong 22 tiếng, nghe nhạc trong 75 tiếng v&agrave; hỗ trợ sạc nhanh 20 W qua c&aacute;p.</p>\r\n<p>Lưu &yacute;: Củ sạc 20 W sẽ kh&ocirc;ng đi k&egrave;m trong hộp khi mua m&aacute;y.</p>\r\n<p>Ngo&agrave;i ra c&ograve;n hỗ trợ sạc kh&ocirc;ng d&acirc;y chuẩn Qi 7.5 W, sạc kh&ocirc;ng d&acirc;y&nbsp;MagSafe 15 W bạn c&oacute; thể lựa chọn phương ph&aacute;p sạc ph&ugrave; hợp v&agrave; tiện lợi nhất trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p>Dung lượng pin tr&ecirc;n iPhone lu&ocirc;n thấp hơn khi so với c&aacute;c d&ograve;ng m&aacute;y Android nhưng việc trang bị chipset mới c&ugrave;ng sự tối ưu ho&aacute; của hệ điều h&agrave;nh sẽ đảm bảo thời lượng sử dụng kh&ocirc;ng hề thua k&eacute;m tr&ecirc;n sản phẩm Android, thậm ch&iacute; c&ograve;n c&oacute; thể vượt trội hơn.&nbsp;</p>\r\n<p><span style=\"text-align: justify;\"><a title=\"Tham khảo th&ocirc;ng tin sản phẩm tại TopZone.vn\" href=\"https://www.topzone.vn/iphone/iphone-13-pro\" target=\"_blank\" rel=\"noopener\">iPhone 13 Pro 128GB</a> với thiết kế cứng c&aacute;p, sang trọng c&ugrave;ng khả năng nhiếp ảnh ấn tượng, sẽ l&agrave; một sản phẩm phục vụ tốt c&aacute;c nhu cầu của bạn cũng như l&agrave; một m&oacute;n đồ thời trang hiện đại.</span></p>', 'Mỗi lần ra mắt phiên bản mới là mỗi lần iPhone chiếm sóng trên khắp các mặt trận và lần này cái tên khiến vô số người \"sục sôi\" là iPhone 13 Pro, chiếc điện thoại thông minh vẫn giữ nguyên thiết kế cao cấp, cụm 3 camera được nâng cấp, cấu hình mạnh mẽ cùng thời lượng pin lớn ấn tượng.', 'Màn hình: OLED6.1\"Super Retina XDR\r\nHệ điều hành: iOS 15\r\nCamera sau: 3 camera 12 MP\r\nCamera trước: 12 MP\r\nChip: Apple A15 Bionic\r\nRAM: 6 GB\r\nBộ nhớ trong: 128 GB', 100, 26490000, 30990000, '2022-08-05 03:34:54', '2022-08-05 03:49:19', NULL, 'iphone-13-pro', 0, 0, 'Sản phẩm,Iphone,Iphone 13 Pro'),
(20, 9, 14, 'Samsung Galaxy Z Flip3', 'https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-cream-1-600x600.jpg', '<h3>Thiết kế hiện đại c&ugrave;ng m&agrave;u sắc thời trang</h3>\r\n<p>Galaxy Z Flip 3 sở hữu cơ cấu gập theo chiều dọc xịn s&ograve;, tạo ra chiếc smartphone kh&aacute;c biệt so với phần c&ograve;n lại, c&oacute; thể đ&oacute;ng lại gọn g&agrave;ng khi kh&ocirc;ng sử dụng để bạn thuận tiện mang theo b&ecirc;n m&igrave;nh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-5.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Thiết kế hiện đại c&ugrave;ng nhiều m&agrave;u sắc thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-5.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Thiết kế hiện đại c&ugrave;ng nhiều m&agrave;u sắc thời trang\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-5.jpg\" /></a></p>\r\n<p>Kết hợp c&ugrave;ng khung nh&ocirc;m bo tr&ograve;n nhẹ nh&agrave;ng tạo cảm gi&aacute;c cao cấp v&agrave; tinh tế khi chạm v&agrave;o, đồng thời bạn kh&ocirc;ng c&ograve;n phải lo lắng về việc để lại dấu v&acirc;n tay khi Samsung đ&atilde; phủ một lớp nh&aacute;m nhẹ để ho&agrave;n thiện cho phần k&iacute;nh n&agrave;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-15.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Khung viền nh&ocirc;m cao cấp, thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-15.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Khung viền nh&ocirc;m cao cấp, thời trang\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-15.jpg\" /></a></p>\r\n<p>Thiết bị c&ograve;n được <a title=\"Xem th&ecirc;m c&aacute;c sản phẩm Samsung đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">Samsung</a> ưu &aacute;i khi cung cấp nhiều m&agrave;u sắc như: Kem, đen, t&iacute;m, xanh l&aacute; đậm thoải m&aacute;i cho người d&ugrave;ng lựa chọn để ph&ugrave; hợp với phong c&aacute;ch của bản th&acirc;n. Ngo&agrave;i ra, Mặt lưng của chiếc m&aacute;y n&agrave;y c&ograve;n được thiết kế 2 t&ocirc;ng m&agrave;u tr&ocirc;ng rất bắt mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-34.jpg\"><img class=\" lazyloaded\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Nhiều m&agrave;u sắc hiện đại, thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-34.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Nhiều m&agrave;u sắc hiện đại, thời trang\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-34.jpg\" /></a></p>\r\n<p>Ở m&agrave;n h&igrave;nh ch&iacute;nh, Z Flip3 được trang bị <a href=\"https://www.thegioididong.com/hoi-dap/ultra-thin-glass-1236998\">k&iacute;nh si&ecirc;u mỏng Ultra Thin Glass (UTG)</a>,&nbsp;cho bạn trải nghiệm sự linh hoạt kh&ocirc;ng tưởng với độ bền hơn 80% khi so với phi&ecirc;n bản Galaxy Z Flip LTE v&agrave; c&oacute; khả năng gập mở đến 200.000 lần.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-24.jpg\"><img class=\" lazyloaded\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Bộ khung viền cao cấp, cho khả năng sử dụng bền bỉ theo thời gian\" src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-24.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | Bộ khung viền cao cấp, cho khả năng sử dụng bền bỉ theo thời gian\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-24.jpg\" /></a></p>\r\n<h3>Mượt m&agrave; trong từng khung h&igrave;nh</h3>\r\n<p>Galaxy Z Flip3 5G được trang bị m&agrave;n h&igrave;nh chất lượng cao <a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\">Dynamic AMOLED 2X </a>c&oacute; k&iacute;ch thước 6.7 inch gi&uacute;p cho h&igrave;nh ảnh đang xem trở n&ecirc;n sống động v&agrave; sắc n&eacute;t.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-19.jpg\"><img class=\" lazyloaded\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | M&agrave;n h&igrave;nh gập si&ecirc;u tr&agrave;n viền ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-19.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | M&agrave;n h&igrave;nh gập si&ecirc;u tr&agrave;n viền ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-19.jpg\" /></a></p>\r\n<p>Khi gập điện thoại lại, b&ecirc;n cạnh cụm camera c&oacute; sự xuất hiện một m&agrave;n h&igrave;nh phụ 1.9 inch lớn hơn phi&ecirc;n bản Z Flip tiền nhiệm gi&uacute;p hiển thị tin nhắn, th&ocirc;ng b&aacute;o, thời gian, thời tiết chi tiết hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-39.jpg\"><img class=\" lazyloaded\" title=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | N&acirc;ng cấp m&agrave;n h&igrave;nh phụ hiển thị được nhiều th&ocirc;ng tin\" src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-39.jpg\" alt=\"Điện thoại Samsung Galaxy Z Flip3 5G 128GB | N&acirc;ng cấp m&agrave;n h&igrave;nh phụ hiển thị được nhiều th&ocirc;ng tin\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/229949/samsung-galaxy-z-flip-3-39.jpg\" /></a></p>\r\n<p>Thiết bị cũng sẽ được trang bị tốc độ l&agrave;m mới l&ecirc;n đến 120 Hz gi&uacute;p người d&ugrave;ng tận hưởng trải nghiệm m&agrave;n h&igrave;nh liền mạch v&agrave; mượt m&agrave; trong cuộc sống h&agrave;ng ng&agrave;y.</p>\r\n<p>Cảm biến v&acirc;n tay cũng đ&atilde; được t&iacute;ch hợp v&agrave;o ph&iacute;m nguồn, v&igrave; vậy người d&ugrave;ng c&oacute; thể bật v&agrave; mở kh&oacute;a c&ugrave;ng một l&uacute;c, việc truy cập <a title=\"Xem th&ecirc;m c&aacute;c sản phẩm điện thoại di động đang b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a> chưa bao giờ th&ocirc;ng minh, tiện lợi đến thế.</p>\r\n<h3>Hiệu suất tuyệt vời</h3>\r\n<p>Galaxy Z Flip3 5G sẽ hoạt động mượt m&agrave; với <a title=\"Chip Snapdragon 888 hiệu năng c&oacute; mạnh kh&ocirc;ng? C&oacute; tr&ecirc;n thiết bị n&agrave;o?\" href=\"https://www.thegioididong.com/hoi-dap/chip-snapdragon-888-la-gi-hieu-nang-manh-the-nao-1337297\" target=\"_blank\" rel=\"noopener\">chip Snapdragon 888 5 nm</a>, đi k&egrave;m <a title=\"Tham khảo điện thoại c&oacute; RAM 8 GB tại Thegioididong.com \" href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" rel=\"noopener\">RAM 8 GB</a> hứa hẹn sẽ mang đến trải nghiệm tốt nhất cho người d&ugrave;ng.</p>\r\n<p>Với vi xử l&yacute; mạnh mẽ sẽ gi&uacute;p m&aacute;y trở n&ecirc;n mượt m&agrave; v&ocirc; c&ugrave;ng ấn tượng, kh&ocirc;ng một tựa game hay ứng dụng nặng n&agrave;o c&oacute; thể l&agrave;m kh&oacute; được con chip n&agrave;y. Kết hợp c&ugrave;ng GPU Adreno 660 cho khả năng xử l&yacute; đồ họa v&agrave; c&aacute;c t&aacute;c vụ được diễn ra nhanh ch&oacute;ng nhất c&oacute; thể.</p>\r\n<p><a title=\"Tham khảo điện thoại c&oacute; bộ nhớ trong 128 GB tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd-rom-128gb\" target=\"_blank\" rel=\"noopener\">Bộ nhớ trong 128 GB</a> cho kh&ocirc;ng gian lưu trữ kh&aacute; ổn, tải xuống những bộ phim truyền h&igrave;nh d&agrave;i tập y&ecirc;u th&iacute;ch hoặc chụp nhiều ảnh v&agrave; video t&ugrave;y th&iacute;ch m&agrave; kh&ocirc;ng lo đầy bộ nhớ.</p>\r\n<p>Mọi t&aacute;c vụ ở m&ocirc;i trường internet được xử l&yacute; mượt m&agrave; với tốc độ mạng 5G cực k&igrave; nhanh ch&oacute;ng. Bạn sẽ ho&agrave;n to&agrave;n h&agrave;i l&ograve;ng bởi mọi y&ecirc;u cầu đều được thực hiện ngay trong nh&aacute;y mắt m&agrave; kh&ocirc;ng mất thời gian chờ đợi.</p>\r\n<p>M&aacute;y sẽ chạy tr&ecirc;n giao diện One UI 3.1.1 dựa tr&ecirc;n hệ điều h&agrave;nh Android 11 ngay khi ra mắt, hỗ trợ nhiều t&iacute;nh năng nổi bật cho người d&ugrave;ng tha hồ kh&aacute;m ph&aacute;.</p>\r\n<p>Galaxy Z Flip3 5G sẽ bảo vệ tối đa dữ liệu trong thời gian thực với nền tảng bảo mật đa lớp ho&agrave;n thiện nhất với Samsung Knox.</p>\r\n<h3>Nhiếp ảnh si&ecirc;u chất</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c điện thoại Samsung Galaxy Z  đang b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-z\" target=\"_blank\" rel=\"noopener\">Điện thoại Samsung Galaxy Z</a> n&agrave;y c&oacute; sự thay đổi về bố cục cụm camera sau, giờ đ&acirc;y 2 m&aacute;y ảnh sẽ được đặt dọc thay v&igrave; đặt nằm ngang như mẫu trước đ&oacute;. Hệ thống ống k&iacute;nh bao gồm một cảm biến ch&iacute;nh 12 MP v&agrave; một <a title=\"Tham khảo điện thoại c&oacute; camera g&oacute;c rộng tại Thegioididong.com \" href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" rel=\"noopener\">camera si&ecirc;u rộng</a> 12 MP.&nbsp;</p>\r\n<p>Khả năng quay video được Samsung rất ch&uacute; trọng với độ ph&acirc;n giải 4K cực đỉnh c&ugrave;ng chế độ Flex Mode cho ph&eacute;p Galaxy Z Flip3 5G \"đứng vững\" tr&ecirc;n bề mặt phẳng gi&uacute;p giảm hiện tượng giật m&agrave;n h&igrave;nh một c&aacute;ch hiệu quả cho những khoảnh khắc đời thường được thể hiện một c&aacute;ch mượt m&agrave; hơn.</p>\r\n<p>Đừng để b&oacute;ng tối l&agrave;m lu mờ vẻ đẹp của bạn. Z Flip3 lu&ocirc;n ch&uacute; &yacute; v&agrave;o bạn v&agrave; lấy n&eacute;t từng chuyển động của bạn với c&ocirc;ng nghệ chụp đ&ecirc;m ti&ecirc;n tiến, kh&ocirc;ng bao giờ l&agrave; qu&aacute; muộn để c&oacute; được những bức ảnh ban đ&ecirc;m tuyệt đẹp, ngay cả khi b&ecirc;n ngo&agrave;i trời tối đen như mực.</p>\r\n<p>Kh&ocirc;ng những vậy, thiết bị c&ograve;n khả năng zoom kỹ thuật số cho bạn dễ d&agrave;ng ghi lại chi tiết cuộc biểu diễn tr&ecirc;n s&acirc;n khấu hay c&aacute;c địa danh ở xa ngo&agrave;i tầm với một c&aacute;ch r&otilde; n&eacute;t tuyệt vời.</p>\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh của Galaxy Z Flip3 c&oacute; thiết kế đục lỗ với camera selfie 10 MP được tăng cường bởi c&ocirc;ng nghệ l&agrave;m đẹp th&ocirc;ng minh gi&uacute;p c&aacute;c đặc điểm tr&ecirc;n khu&ocirc;n mặt từ t&ocirc;ng m&agrave;u da đến trang điểm đều được l&agrave;m đẹp một c&aacute;ch cẩn thận.</p>\r\n<h3>Lu&ocirc;n b&ecirc;n bạn mọi l&uacute;c</h3>\r\n<p>Vi&ecirc;n pin 3300 mAh mặc d&ugrave; kh&ocirc;ng qu&aacute; ấn tượng nhưng cũng đủ để c&oacute; thể trải nghiệm l&acirc;u d&agrave;i. Ngo&agrave;i ra, Galaxy Z Flip3 5G c&ograve;n c&oacute; khả năng sạc 15 W v&agrave; hỗ trợ&nbsp;sạc kh&ocirc;ng d&acirc;y&nbsp;hiện đại.</p>\r\n<p>M&aacute;y c&ograve;n c&oacute; t&iacute;nh năng đặc biệt đ&oacute; l&agrave; dịch vụ thanh to&aacute;n Samsung Pay d&agrave;nh cho ri&ecirc;ng cho điện thoại Samsung, gi&uacute;p bạn dễ d&agrave;ng thanh to&aacute;n tại mọi điểm mua sắm, giải tr&iacute; một c&aacute;ch cực kỳ tiện lợi v&agrave; nhanh ch&oacute;ng th&ocirc;ng qua kết nối NFC.&nbsp;</p>\r\n<p>C&oacute; thể thấy Samsung đ&atilde; tạo ra cỗ m&aacute;y thực sự hữu &iacute;ch trong sử dụng thường ng&agrave;y, chứ kh&ocirc;ng chỉ để cho đẹp, cho ấn tượng nữa. Chắc chắn sẽ mang lại tầm đẳng cấp cho người sở hữu v&agrave; n&oacute; c&ograve;n c&oacute; thể đ&aacute;p ứng những nhu cầu kh&oacute; nhất tr&ecirc;n chiếc smartphone.</p>', 'Trong sự kiện Galaxy Unpacked hồi 11/8, Samsung đã chính thức trình làng mẫu điện thoại màn hình gập thế hệ mới mang tên Samsung Galaxy Z Flip3 5G 128GB. Đây là một siêu phẩm với màn hình gập dạng vỏ sò cùng nhiều điểm cải tiến và thông số ấn tượng, sản phẩm chắc chắn sẽ thu hút được rất nhiều sự quan tâm của người dùng, đặc biệt là phái nữ.', 'Màn hình: Dynamic AMOLED 2XChính 6.7\" & Phụ 1.9\"Full HD+\r\nHệ điều hành: Android 11\r\nCamera sau: 2 camera 12 MP\r\nCamera trước: 10 MP\r\nChip: Snapdragon 888\r\nRAM: 8 GB\r\nBộ nhớ trong: 128 GB', 100, 15000000, 25000000, '2022-08-05 03:52:36', '2022-08-05 03:52:36', NULL, 'samsung-galaxy-z-flip3', 0, 0, 'Sản phẩm,Samsung,Z Flip3');
INSERT INTO `products` (`id`, `user_id`, `cat_id`, `name`, `thumbnail`, `content`, `summary`, `description`, `qty`, `price`, `old_price`, `created_at`, `updated_at`, `deleted_at`, `slug`, `num_views`, `num_purchases`, `tags`) VALUES
(21, 9, 14, 'Samsung Galaxy Z Fold3', 'https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold-3-green-1-600x600.jpg', '<h3>Đột ph&aacute; thiết kế m&agrave;n h&igrave;nh gập</h3>\r\n<p>Đầu ti&ecirc;n, khung viền Galaxy Z Fold3 được ho&agrave;n thiện bằng chất liệu Armor Aluminum cao cấp nhất từ trước đến giờ nhằm tăng cường được độ bền, m&agrave; vẫn đảm bảo được trọng lượng c&acirc;n đối đem tới cảm gi&aacute;c v&ocirc; c&ugrave;ng chắc chắn v&agrave; cao cấp.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-23.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Thiết kế khung viền cao cấp\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-23.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Thiết kế khung viền cao cấp\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-23.jpg\" /></a></p>\r\n<p>Nhờ đ&oacute;, tổng thể m&aacute;y cũng đ&atilde; mỏng nhẹ hơn khi trọng lượng giảm xuống c&ograve;n 271 g, độ d&agrave;y phần bản lề khi gập lại c&ograve;n 16 mm cho cảm gi&aacute;c cầm nắm, sử dụng điện thoại Z Fold3 giờ đ&acirc;y đ&atilde; thoải m&aacute;i v&agrave; dễ chịu hơn rất nhiều.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-2.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Trọng lượng giảm so với tiền nhiệm\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-2.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Trọng lượng giảm so với tiền nhiệm\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-2.jpg\" /></a></p>\r\n<p>Khi kh&ocirc;ng d&ugrave;ng c&oacute; thể gấp gọn như một cuốn sổ ghi ch&uacute; nhỏ dễ d&agrave;ng bỏ t&uacute;i mang theo b&ecirc;n m&igrave;nh mọi l&uacute;c mọi nơi.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102514.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Trạng th&aacute;i khi gập\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102514.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Trạng th&aacute;i khi gập\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102514.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, với&nbsp;cảm biến v&acirc;n tay&nbsp;ở cạnh b&ecirc;n, người d&ugrave;ng c&oacute; thể nhanh ch&oacute;ng mở kh&oacute;a v&agrave; sử dụng chỉ bằng thao t&aacute;c chạm nhẹ.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102533.jpg\"><img class=\" lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Cảm biến v&acirc;n tay ở cạnh viền\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102533.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Cảm biến v&acirc;n tay ở cạnh viền\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102533.jpg\" /></a></p>\r\n<p>Hệ thống loa k&eacute;p stereo k&iacute;ch thước lớn ở 2 cạnh tr&ecirc;n dưới t&iacute;ch hợp c&ocirc;ng nghệ Dolby Atmos cho trải nghiệm chơi game, xem phim đ&atilde; tai với &acirc;m lượng lớn, r&otilde; r&agrave;ng v&agrave; sống động.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102545.jpg\"><img class=\" lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Hệ thống loa k&eacute;p, giải tr&iacute; ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102545.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Hệ thống loa k&eacute;p, giải tr&iacute; ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102545.jpg\" /></a></p>\r\n<h3>Cụm camera đỉnh cao c&ocirc;ng nghệ</h3>\r\n<p>Kế đ&oacute;, cụm camera tr&ecirc;n Galaxy Z Fold3 bao gồm 3 ống k&iacute;nh: Camera g&oacute;c rộng,&nbsp;camera g&oacute;c si&ecirc;u rộng, camera tele&nbsp;t&iacute;ch hợp chống rung OIS v&agrave; đều sở hữu độ ph&acirc;n giải 12 MP đặt trong cụm m&ocirc;-đun h&igrave;nh chữ nhật được bo tr&ograve;n 2 đầu tr&ocirc;ng rất thon gọn v&agrave; đầy tinh tế.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102526.jpg\"><img class=\" lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Cụm camera đỉnh cao c&ocirc;ng nghệ\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102526.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Cụm camera đỉnh cao c&ocirc;ng nghệ\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-060921-102526.jpg\" /></a></p>\r\n<p>Chất lượng h&igrave;nh ảnh theo đ&aacute;nh gi&aacute; nhanh l&agrave; kh&aacute; ổn, m&agrave;u sắc tươi tắn, đặc biệt khả năng xử l&yacute;&nbsp;x&oacute;a ph&ocirc;ng&nbsp;cũng đ&atilde; cải thiện đ&aacute;ng kể, &iacute;t t&igrave;nh trạng bị lem như trước đ&acirc;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-3.jpg\"><img class=\" lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Giao diện camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-3.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Giao diện camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-3.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, m&aacute;y c&ograve;n sở hữu camera trước ở m&agrave;n h&igrave;nh ngo&agrave;i c&oacute; độ ph&acirc;n giải 10 MP v&agrave; camera trước ở m&agrave;n h&igrave;nh trong sẽ được đặt ẩn dưới m&agrave;n h&igrave;nh c&oacute; độ ph&acirc;n giải 4 MP tất cả đều cho chất lượng ảnh tốt ngo&agrave;i mong đợi.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-15.jpg\"><img class=\" lazyloaded\" title=\"Samsung Galaxy Z Fold3 5G | Khả năng chụp ảnh selfie \" src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-15.jpg\" alt=\"Samsung Galaxy Z Fold3 5G | Khả năng chụp ảnh selfie \" data-src=\"https://cdn.tgdd.vn/Products/Images/42/248284/samsung-galaxy-z-fold3-5g-512gb-15.jpg\" /></a></p>\r\n<p>Đặc biệt, c&oacute; chế độ Flex Mode khi gập m&agrave;n h&igrave;nh theo nhiều g&oacute;c độ, c&oacute; thể chia ra 2 m&agrave;n h&igrave;nh khi chụp ảnh, cho ph&eacute;p người d&ugrave;ng vừa chụp vừa xem lại c&aacute;c ảnh đ&atilde; chụp, tr&aacute;nh sự tr&ugrave;ng lặp, cũng dễ hơn trong việc tương t&aacute;c với người hay vật mẫu để điều khiển c&aacute;c tư thế tạo d&aacute;ng.</p>\r\n<h3>N&acirc;ng tầm giải tr&iacute; m&agrave;n h&igrave;nh gập với camera ẩn</h3>\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh tr&ecirc;n Z Fold3 c&oacute; k&iacute;ch thước 7.6 inches, sử dụng tấm nền Dynamic AMOLED 2X cho độ ph&acirc;n giải 1768 x 2208 Pixels hỗ trợ khả năng hiển thị HDR10+ c&ugrave;ng độ s&aacute;ng cao l&ecirc;n tới 1200 nits cho chất lượng hiển thị sắc n&eacute;t, m&agrave;u sắc sống động ch&acirc;n thực trong mọi điều kiện.</p>\r\n<p>Z Fold3 ch&iacute;nh l&agrave; chiếc&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone Samsung ch&iacute;nh h&atilde;ng\" href=\"https://thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">điện thoại Samsung</a>&nbsp;đầu ti&ecirc;n sử dụng c&ocirc;ng nghệ camera selfie ẩn dưới m&agrave;n h&igrave;nh gập, đem tới khả năng hiển thị si&ecirc;u tr&agrave;n viền gần như kh&ocirc;ng c&oacute; khiếm khuyết, những t&aacute;c vụ tr&ecirc;n Samsung Galaxy Z Fold3 đều hiển thị một c&aacute;ch trọn vẹn sắc n&eacute;t, từ v&agrave;o mạng x&atilde; hội, lướt web cho đến xem phim, chơi game.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, m&agrave;n h&igrave;nh ngo&agrave;i tr&ecirc;n Z Fold3 5G c&oacute; k&iacute;ch thước 6.2 inch cũng sử dụng tấm nền Dynamic AMOLED 2X, độ ph&acirc;n giải 832 x 2268 Pixels v&agrave; thiết kế Infinity-O tr&agrave;n viền, được n&acirc;ng cấp l&ecirc;n tần số qu&eacute;t 120 Hz cho khả năng vuốt chạm mượt m&agrave;.</p>\r\n<p>Về độ bền, Samsung tự h&agrave;o tuy&ecirc;n bố Z Fold3 m&agrave;n h&igrave;nh gập của m&igrave;nh được tối ưu h&oacute;a với độ bền hơn 80% so với trước đ&acirc;y c&ugrave;ng khả năng hạn chế c&aacute;c vết trầy xước v&agrave; rơi vỡ ấn tượng nhờ được trang bị k&iacute;nh Ultra Thin Glass (UTG).</p>\r\n<p>Galaxy Z Fold3 cũng hỗ trợ b&uacute;t S Pen lần đầu được &aacute;p dụng tr&ecirc;n điện thoại m&agrave;n h&igrave;nh gập. Mọi thao ghi ch&uacute;, ph&aacute;c thảo hoặc đ&aacute;nh dấu v&agrave;o t&agrave;i liệu đều được thực hiện một c&aacute;ch dễ d&agrave;ng.</p>\r\n<h3>Hiệu năng h&agrave;ng đầu với Snapdragon 888</h3>\r\n<p>Galaxy Z Fold3 được cung cấp sức mạnh bởi chip set Snapdragon 888 cho hiệu năng mạnh mẽ v&agrave; khả năng xử l&yacute; mọi t&aacute;c vụ đều trở n&ecirc;n nhanh ch&oacute;ng mượt m&agrave; tr&ecirc;n cả hai m&agrave;n h&igrave;nh.</p>\r\n<p>Dung lượng&nbsp;<a title=\"Tham khảo điện thoại c&oacute; dung lượng RAM 12 GB đang b&aacute;n tại TGDĐ\" href=\"https://www.thegioididong.com/dtdd-ram-12gb\" target=\"_blank\" rel=\"noopener\">RAM 12 GB</a>&nbsp;cho khả năng đa nhiệm tuyệt vời nay lại c&ograve;n t&iacute;ch hợp th&ecirc;m t&iacute;nh năng Multi-Active Window, gi&uacute;p người d&ugrave;ng c&oacute; thể chạy v&agrave; tương t&aacute;c song song giữa hai ứng dụng hoặc chạy tối đa 3 ứng dụng c&ugrave;ng l&uacute;c tr&ecirc;n m&agrave;n h&igrave;nh vừa xem phim, vừa nhắn tin hay đọc b&aacute;o.&nbsp;</p>\r\n<p>Đi k&egrave;m với đ&oacute; l&agrave;&nbsp;<a title=\"Tham khảo điện thoại c&oacute; bộ nhớ trong từ 512 GB đang b&aacute;n tại TGDĐ\" href=\"https://www.thegioididong.com/dtdd-rom-512gb\" target=\"_blank\" rel=\"noopener\">bộ nhớ trong 512 GB</a>&nbsp;cho bạn thoải m&aacute;i lưu trữ dữ liệu để l&agrave;m việc, giải tr&iacute; hay c&agrave;i đặt c&aacute;c ứng dụng nặng m&agrave; kh&ocirc;ng cần lo đến việc đầy bộ nhớ.</p>\r\n<p>Điểm hiệu năng đo nhanh bằng AnTuTu đạt hơn 750 ngh&igrave;n điểm, chơi mượt m&agrave; c&aacute;c tựa game nặng k&yacute; như Genshin Impact, Call of Duty Mobile ở thiết lập đồ họa cao.</p>\r\n<p>Ngo&agrave;i ra, Galaxy Z Fold3 cũng được cải thiện khả năng tản nhiệt khi chơi game, ho&agrave;n to&agrave;n khắc phục được t&igrave;nh trạng n&oacute;ng m&aacute;y như tr&ecirc;n c&aacute;c d&ograve;ng m&aacute;y sử dụng chip Snapdragon 888 kh&aacute;c.&nbsp;</p>\r\n<p>Samsung Galaxy Z Fold3 cũng được trang bị chuẩn kết nối internet 5G cho khả năng kết nối internet tốc độ si&ecirc;u cao gi&uacute;p bạn c&oacute; thể truy cập sử dụng mạng internet mọi l&uacute;c mọi nơi.</p>\r\n<h3>Thời lượng pin thoải m&aacute;i sử dụng giải tr&iacute;</h3>\r\n<p>Về pin, Samsung cho biết Z Fold3 sở hữu pin dung lượng 4.400 mAh, thấp hơn một ch&uacute;t so với người tiền nhiệm nhưng nhu cầu sử dụng vẫn đ&aacute;p ứng tốt trong một ng&agrave;y.</p>\r\n<p>B&ecirc;n canh đ&oacute;, điện thoại cũng hỗ trợ&nbsp;<a title=\"Tham khảo gi&aacute; điện thoại smartphone sạc pin nhanh\" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">sạc pin nhanh</a>&nbsp;25 W, sạc kh&ocirc;ng d&acirc;y hay chia sẻ pin kh&ocirc;ng d&acirc;y gi&uacute;p nạp đầy năng lượng cho c&aacute;c thiết bị ngoại vi kh&aacute;c một c&aacute;ch dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng.</p>\r\n<p>Nh&igrave;n chung, Galaxy Z Fold 3 mang trong m&igrave;nh một cấu h&igrave;nh \"đụng n&oacute;c\" trong thế giới&nbsp;Android&nbsp;hiện nay, kết hợp với m&agrave;n h&igrave;nh 120 Hz v&agrave; b&uacute;t S-Pen thần th&aacute;nh c&oacute; thể n&oacute;i đ&acirc;y sẽ l&agrave; thiết bị c&ocirc;ng nghệ phục vụ cho c&ocirc;ng việc hay giải tr&iacute; rất đ&aacute;ng để sở hữu.</p>', 'Samsung Galaxy Z Fold3 5G 512GB đánh dấu bước tiến mới của hãng trong phân khúc điện thoại gập cao cấp khi được cải tiến về độ bền cùng những nâng cấp đáng giá về cấu hình hiệu năng, hứa hẹn sẽ mang đến trải nghiệm sử dụng đột phá cho người dùng.', 'Màn hình: Dynamic AMOLED 2XChính 7.6\" & Phụ 6.2\"Full HD+\r\nHệ điều hành: Android 11\r\nCamera sau: 3 camera 12 MP\r\nCamera trước: 10 MP & 4 MP\r\nChip: Snapdragon 888\r\nRAM: 12 GB', 100, 35990000, 44990000, '2022-08-05 03:55:26', '2022-08-05 03:55:26', NULL, 'samsung-galaxy-z-fold3', 0, 0, 'Sản phẩm,Samsung,Z Fold'),
(22, 9, 13, 'Samsung Galaxy S22 Ultra', 'https://cdn.tgdd.vn/Products/Images/42/271698/Galaxy-S22-Ultra-Black-600x600.jpg', '<h3 style=\"text-align: justify;\"><a title=\"Samsung Galaxy S22 Ultra 5G 512GB\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s22-ultra-512gb\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy S22 Ultra 5G 512GB</a>&nbsp;- l&agrave; chiếc <a title=\"Tham khảo điện thoại Galaxy S tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-s\" target=\"_blank\" rel=\"noopener\">điện thoại Galaxy S</a> đầu ti&ecirc;n hỗ trợ b&uacute;t S Pen t&iacute;ch hợp b&ecirc;n trong th&acirc;n m&aacute;y, c&oacute; nhiều cải tiến về camera, m&agrave;n h&igrave;nh ấn tượng hơn v&agrave; hỗ trợ sạc 45 W nhanh hơn.</h3>\r\n<h3 style=\"text-align: justify;\">Những bức ảnh chụp đ&ecirc;m ho&agrave;n hảo với Nightography Camera - Mắt Thần B&oacute;ng Đ&ecirc;m</h3>\r\n<p style=\"text-align: justify;\">Galaxy S22 Ultra trang bị cảm biến điểm ảnh 2.4 um lớn hơn để thu nhiều &aacute;nh s&aacute;ng v&agrave; dữ liệu hơn, c&ugrave;ng ống k&iacute;nh Super Clear Glass để quay video ban đ&ecirc;m m&agrave; kh&ocirc;ng bị l&oacute;a. Ngo&agrave;i ra ch&uacute;ng ta c&ograve;n c&oacute;&nbsp;ống k&iacute;nh si&ecirc;u rộng 12 MP, camera tele 10 MP k&eacute;p kết hợp để cung cấp khả năng thu ph&oacute;ng quang học 10x v&agrave; thu ph&oacute;ng kh&ocirc;ng gian 100x.</p>\r\n<h3 style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271698/samsung-galaxy-s22-ultra-512gb-030322-112615.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy S22 Ultra 5G 512GB - Camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/271698/samsung-galaxy-s22-ultra-512gb-030322-112615.jpg\" alt=\"Galaxy S22 Ultra 5G 512GB - Camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271698/samsung-galaxy-s22-ultra-512gb-030322-112615.jpg\" /></a></h3>\r\n<p style=\"text-align: justify;\">Chiếc <a title=\"Tham khảo điện thoại Samsung tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">điện thoại Samsung</a> n&agrave;y trang bị khả năng chụp ảnh ch&acirc;n dung ban đ&ecirc;m, trong điều kiện thiếu s&aacute;ng ấn tượng với bộ xử l&yacute; h&igrave;nh ảnh AI kết hợp với camera g&oacute;c rộng 108 MP mang lại bức ảnh ban đ&ecirc;m s&aacute;ng r&otilde;. Vậy n&ecirc;n S22 Ultra l&agrave; lựa chọn ho&agrave;n hảo để lưu giữ những khoảnh khắc trong c&aacute;c buổi tiệc tối.</p>\r\n<p style=\"text-align: justify;\">Ngay cả khi kh&ocirc;ng chọn chế độ chụp đ&ecirc;m, những tấm ảnh được chụp trong điều kiện &aacute;nh s&aacute;ng yếu cũng đều r&otilde; r&agrave;ng v&agrave; s&aacute;ng hơn, đầy đủ chi tiết v&agrave; kh&ocirc;ng bị nhiễu.</p>\r\n<h3 style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271698/samsung-galaxy-s22-ultra-512gb-030322-112613.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy S22 Ultra 5G 512GB - Chụp ảnh\" src=\"https://cdn.tgdd.vn/Products/Images/42/271698/samsung-galaxy-s22-ultra-512gb-030322-112613.jpg\" alt=\"Galaxy S22 Ultra 5G 512GB - Chụp ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271698/samsung-galaxy-s22-ultra-512gb-030322-112613.jpg\" /></a></h3>\r\n<p style=\"text-align: justify;\">Kh&ocirc;ng chỉ mạnh về phần chụp ảnh, chiếc <a title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a> n&agrave;y c&ograve;n mang lại khả năng quay&nbsp;video sắc n&eacute;t l&ecirc;n đến 8K ở tốc độ 24 khung h&igrave;nh/gi&acirc;y,&nbsp;v&agrave; c&oacute; thể giữ cho cảnh quay của bạn tr&ocirc;ng si&ecirc;u ổn định nhờ&nbsp;khả năng chống rung v&agrave; c&ocirc;ng nghệ AI.&nbsp;Giải ph&aacute;p si&ecirc;u chụp đ&ecirc;m nhờ vi xử l&yacute; 4 nm khử nhiễu hiệu quả mang đến trải nghiệm quay phim gi&agrave;u chi tiết từ b&igrave;nh minh tới đ&ecirc;m khuya đầy ấn tượng.</p>\r\n<h3 style=\"text-align: justify;\">T&iacute;ch hợp&nbsp;S Pen phản hồi nhanh hơn</h3>\r\n<p style=\"text-align: justify;\">Bằng c&aacute;ch giảm độ trễ xuống 70% (từ 9 ms xuống c&ograve;n khoảng 2.8 ms) v&agrave; kết hợp với c&ocirc;ng nghệ AI để dự đo&aacute;n chuyển động tay n&ecirc;n cảm gi&aacute;c cầm S Pen viết, vẽ tr&ecirc;n Galaxy S22 Ultra mượt m&agrave; như b&uacute;t viết tr&ecirc;n giấy.</p>\r\n<p style=\"text-align: justify;\">Một số cải tiến đ&aacute;ng ch&uacute; &yacute; kh&aacute;c của S Pen m&agrave; m&igrave;nh đ&atilde; trải nghiệm đ&oacute; l&agrave; chuyển đổi chữ viết tay th&agrave;nh văn bản c&oacute; độ ch&iacute;nh x&aacute;c cao hơn, cũng như khả năng nhanh ch&oacute;ng lưu ghi ch&uacute; trong t&agrave;i liệu v&agrave; email tr&ecirc;n Outlook.</p>\r\n<h3 style=\"text-align: justify;\">Thiết kế đậm chất ri&ecirc;ng, dễ d&agrave;ng nổi bật giữa đ&aacute;m đ&ocirc;ng</h3>\r\n<p style=\"text-align: justify;\">Galaxy S22 Ultra nghi&ecirc;ng về phong c&aacute;ch thiết kế đơn giản nhưng đồng thời cũng tuyệt đẹp với c&aacute;c g&oacute;c cạnh sắc nết, vu&ocirc;ng vắn. Đặc biệt l&agrave; cụm camera kh&ocirc;ng viền, rất độc đ&aacute;o v&agrave; kh&aacute;c biệt. Cảm gi&aacute;c cầm nắm S22 Ultra tr&ecirc;n tay rất chắc chắn v&agrave; bền bỉ nhờ m&aacute;y được gia cố bằng khung nh&ocirc;m bọc gi&aacute;p v&agrave; mặt k&iacute;nh cường lực Gorilla Glass Victus +.</p>\r\n<p style=\"text-align: justify;\">M&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng cao nhất l&agrave; 1750 nits c&ugrave;ng với&nbsp;t&iacute;nh năng Vision Booster mới - c&oacute; thể điều chỉnh độ s&aacute;ng của m&agrave;n h&igrave;nh một c&aacute;ch nhanh ch&oacute;ng suốt cả ng&agrave;y gi&uacute;p bạn sử dụng xuy&ecirc;n suốt&nbsp;ngay cả ở ngo&agrave;i trời nắng gắt m&agrave; kh&ocirc;ng phải điều chỉnh nhiều lần.</p>\r\n<p style=\"text-align: justify;\">M&agrave;n h&igrave;nh 6.8 inch được bo cong nhẹ nh&agrave;ng n&ecirc;n tăng th&ecirc;m phần thẩm mỹ m&agrave; kh&ocirc;ng dẫn đến t&igrave;nh trạng cấn m&agrave;n h&igrave;nh. B&ecirc;n cạnh đ&oacute; th&igrave; trải nghiệm về mặt h&igrave;nh ảnh hiển thị tr&ecirc;n S22 Ultra th&igrave; thật sự tuyệt với với độ ph&acirc;n giải 2K tr&ecirc;n tấm nền&nbsp;<a title=\"Dynamic AMOLED 2X\" href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\" rel=\"noopener\">Dynamic AMOLED 2X</a>, vuốt chạm cực k&igrave; mượt m&agrave; nhờ tần số qu&eacute;t 120 Hz.</p>\r\n<h3 style=\"text-align: justify;\">Một trong những điện thoại đầu ti&ecirc;n trang bị CPU Snapdragon 8 Gen 1&nbsp;</h3>\r\n<p style=\"text-align: justify;\"><a title=\"Điện thoại RAM 8 GB ch&iacute;nh h&atilde;ng, gi&aacute; tốt đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" rel=\"noopener\">Điện thoại RAM 8 GB</a> cho cảm gi&aacute;c si&ecirc;u mượt m&agrave; khi mở v&agrave; đ&oacute;ng ứng dụng v&agrave; bộ nhớ trong 512 GB cho bạn thoải m&aacute;i lưu trữ những khung h&igrave;nh, thước phim chất lượng cao.&nbsp;Snapdragon 8 Gen 1 của Qualcomm 4 nm hứa hẹn cho hiệu năng CPU v&agrave; đồ họa tốt hơn đồng thời mang lại hiệu quả tốt hơn.</p>\r\n<p style=\"text-align: justify;\">Galaxy S22 Ultra sử dụng giao diện OneUI 4.1 độc quyền Samsung&nbsp;v&agrave; được chạy tr&ecirc;n nền tảng Android 12.&nbsp;Giao diện n&agrave;y trực quan v&agrave; hữu &iacute;ch hơn c&aacute;c phi&ecirc;n bản trước, đồng thời c&oacute; bẩng điều khiển quyền ri&ecirc;ng tư mới.</p>\r\n<h3 style=\"text-align: justify;\">Pin 5000 mAh c&ugrave;ng sạc 45 W nhanh hơn</h3>\r\n<p style=\"text-align: justify;\">Samsung trang bị cho S22 Ultra c&ocirc;ng nghệ AI cải tiến tối ưu h&oacute;a dung lượng pin 5000 mAh (ti&ecirc;u chuẩn) gi&uacute;p pin th&iacute;ch ứng với th&oacute;i quen sử dụng của người d&ugrave;ng. Kết hợp hỗ trợ sạc nhanh tối đa 45 W để&nbsp;nạp đầy năng lượng trong thời gian ngắn hơn.</p>\r\n<p style=\"text-align: justify;\">Với việc sử dụng đa t&aacute;c vụ thường ng&agrave;y từ chơi game, lướt web, mạng x&atilde; hội, xem phim,... li&ecirc;n tục th&igrave; S22 Ultra c&oacute; thời gian sử dụng l&ecirc;n đến 9 tiếng 18 ph&uacute;t, v&agrave; chỉ mất khoảng 1 tiếng 11 ph&uacute;t để nạp đầy 100% pin.*</p>\r\n<p style=\"text-align: justify;\">Lưu &yacute;: Thời gian sử dụng v&agrave; thời gian sạc&nbsp;c&oacute; thể thay đổi t&ugrave;y theo nhu cầu/t&aacute;c vụ sử dụng.</p>\r\n<p style=\"text-align: justify;\"><em>Bảng test thời gian sử dụng pin của Samsung Galaxy S22 Ultra</em></p>\r\n<p style=\"text-align: justify;\"><em>Bảng đo thời gian sạc pin của Samsung Galaxy S22 Ultra.</em></p>\r\n<p style=\"text-align: justify;\">C&oacute; thể n&oacute;i Galaxy S22 Ultra như một ph&eacute;p cộng của d&ograve;ng Galaxy Note v&agrave; Galaxy S - kết hợp những g&igrave; tốt nhất trong thiết kế của cả hai. Nếu bạn đ&atilde; l&agrave; fan của Galaxy Note v&agrave; chiếc S Pen th&igrave; S22 Ultra ch&iacute;nh l&agrave; lựa chọn ho&agrave;n hảo d&agrave;nh cho bạn.</p>', 'Samsung Galaxy S22 Ultra 5G 512GB - là chiếc điện thoại Galaxy S đầu tiên hỗ trợ bút S Pen tích hợp bên trong thân máy, có nhiều cải tiến về camera, màn hình ấn tượng hơn và hỗ trợ sạc 45 W nhanh hơn.', 'Màn hình: Dynamic AMOLED 2X6.8\"Quad HD+ (2K+)\r\nHệ điều hành: Android 12\r\nCamera sau: Chính 108 MP & Phụ 12 MP, 10 MP, 10 MP\r\nCamera trước: 40 MP\r\nChip: Snapdragon 8 Gen 1\r\nRAM: 12 GB\r\nBộ nhớ trong: 512 GB', 100, 31990000, 36990000, '2022-08-05 03:59:46', '2022-08-05 03:59:46', NULL, 'samsung-galaxy-s22-ultra', 0, 0, 'Sản phẩm,Samsung,Dòng S'),
(23, 9, 13, 'Samsung Galaxy S22+', 'https://cdn.tgdd.vn/Products/Images/42/271696/Galaxy-S22-Plus-Pink-600x600.jpg', '<p>Điểm ấn tượng đầu ti&ecirc;n tr&ecirc;n Galaxy S22+ 5G đ&oacute; ch&iacute;nh l&agrave; phần cụm camera đ&atilde; được h&atilde;ng cải tiến v&ocirc; c&ugrave;ng xịn s&ograve;. Trong đ&oacute;, camera ch&iacute;nh 50 MP với cảm biến điểm ảnh lớn v&igrave; thế m&agrave; n&oacute; c&oacute; thể thu được nhiều &aacute;nh s&aacute;ng, đồng nghĩa những bức ảnh chụp đ&ecirc;m ở tr&ecirc;n chiếc m&aacute;y sẽ được s&aacute;ng r&otilde; v&agrave; chất lượng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022417.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy S22+ 5G - Ảnh chụp tr&ecirc;n camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022417.jpg\" alt=\"Galaxy S22+ 5G - Ảnh chụp tr&ecirc;n camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022417.jpg\" /></a></p>\r\n<p><a title=\"Điện thoại camera g&oacute;c si&ecirc;u rộng kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" rel=\"noopener\">Điện thoại&nbsp;camera g&oacute;c si&ecirc;u rộng</a> 12 MP gi&uacute;p mở rộng trường nh&igrave;n dễ d&agrave;ng lưu lại những khoảnh khắc thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, những bức ảnh chụp nh&oacute;m. <a title=\"Tham khảo điện thoại camera tele kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-camera-zoom\" target=\"_blank\" rel=\"noopener\">Camera tele</a> 10 MP c&oacute; khả năng thu ph&oacute;ng quang học 3x mọi chi tiết ở xa đều c&oacute; thu lại với chất lượng tốt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022419.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy S22+ 5G - Ảnh chụp tr&ecirc;n camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022419.jpg\" alt=\"Galaxy S22+ 5G - Ảnh chụp tr&ecirc;n camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022419.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo điện thoại Samsung kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">Điện thoại Samsung</a> n&agrave;y c&ograve;n được trang bị chip rồng Snapdragon 8 Gen 1 tiến tr&igrave;nh 4 nm mới đầy mạnh mẽ, đi k&egrave;m <a title=\"Điện thoại RAM 8 GB kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" rel=\"noopener\">RAM 8 GB</a> đem lại cho bạn những trải nghiệm hiệu suất kh&ocirc;ng giới hạn, d&ugrave; l&agrave; chơi game với c&agrave;i đặt cấu h&igrave;nh cao, đa nhiệm m&agrave;n h&igrave;nh hay l&agrave;m việc đều rất nhanh ch&oacute;ng v&agrave; mượt m&agrave;.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022423.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Galaxy S22+ 5G - Trải nghiệm chơi game\" src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022423.jpg\" alt=\"Galaxy S22+ 5G - Trải nghiệm chơi game\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022423.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo điện thoại mặt lưng k&iacute;nh kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-mat-lung-kinh\" target=\"_blank\" rel=\"noopener\">Điện thoại mặt lưng k&iacute;nh</a>&nbsp;Corning Gorilla Glass Victus+ kh&ocirc;ng chỉ to&aacute;t l&ecirc;n vẻ sang trọng m&agrave; c&ograve;n c&oacute; khả năng chống xước, va đập tốt. Phần khung viền được chế tạo từ&nbsp;Armor Aluminum cứng c&aacute;p cho bạn trải nghiệm m&agrave; kh&ocirc;ng lo rơi vỡ. C&aacute;c chi tiết, đường n&eacute;t được ho&agrave;n thiện tỉ mỉ, cụm camera sau được thiết kế liền mạch tạo n&ecirc;n một tổng thể h&agrave;i h&ograve;a nhất qu&aacute;n cho sản phẩm.</p>\r\n<p><a title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">Điện thoại</a> sở hữu m&agrave;n h&igrave;nh rộng 6.6 inch với&nbsp;c&ocirc;ng nghệ Dynamic AMOLED 2X v&agrave; Vision Booster đem lại trải nghiệm xem nh&igrave;n tr&ecirc;n Galaxy S22+ 5G v&ocirc; c&ugrave;ng rực rỡ, dễ d&agrave;ng đắm ch&igrave;m v&agrave;o trong những bộ phim bom tấn. B&ecirc;n cạnh đ&oacute;, tần số qu&eacute;t 120 Hz v&agrave; độ s&aacute;ng m&agrave;n h&igrave;nh tối đa 1750 nits đem đến cảm gi&aacute;c vuốt chạm mượt m&agrave;, nhanh ch&oacute;ng cũng như khả năng hiển thị vẫn chất lượng cho d&ugrave; đang ở ngo&agrave;i trời.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-023031.jpg\"><img class=\" lazyloaded\" title=\"Galaxy S22+ 5G - M&agrave;n h&igrave;nh rộng 6.6 inch, 120 Hz\" src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-023031.jpg\" alt=\"Galaxy S22+ 5G - M&agrave;n h&igrave;nh rộng 6.6 inch, 120 Hz\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-023031.jpg\" /></a></p>\r\n<p>Dung lượng pin tr&ecirc;n Galaxy S22+ 5G l&agrave; 4500 mAh c&oacute; thấp hơn nếu so s&aacute;nh với Galaxy S21+ 5G l&agrave; 4800 mAh, nhưng b&ugrave; lại <a title=\"Tham khảo điện thoại sạc nhanh đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">điện thoại c&oacute; sạc nhanh</a> đến 45 W n&ecirc;n dung lượng c&oacute; thấp hơn cũng kh&ocirc;ng phải l&agrave; vấn đề đ&aacute;ng quan ngại.</p>\r\n<p>Lưu &yacute;: Củ sạc 45 W sẽ kh&ocirc;ng đi k&egrave;m theo m&aacute;y v&agrave; được mua ri&ecirc;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022421.jpg\"><img class=\" lazyloaded\" title=\"Galaxy S22+ 5G - Dung lượng pin 4500 mAh\" src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022421.jpg\" alt=\"Galaxy S22+ 5G - Dung lượng pin 4500 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271696/samsung-galaxy-s22-plus-256gb-020322-022421.jpg\" /></a></p>', 'Samsung Galaxy S22+ 5G 256GB đã được Samsung cho ra mắt vào ngày 09/02/2022. Máy nổi bật với cụm camera được cải tiến giúp bạn sáng tạo khoảnh khắc ấn tượng mỗi ngày, một vi xử lý mạnh mẽ cho mọi tác vụ sử dụng đều trở nên mượt mà hơn.', 'Màn hình: Dynamic AMOLED 2X6.6\"Full HD+\r\nHệ điều hành: Android 12\r\nCamera sau: Chính 50 MP & Phụ 12 MP, 10 MP\r\nCamera trước: 10 MP\r\nChip: Snapdragon 8 Gen 1\r\nRAM: 8 GB\r\nBộ nhớ trong: 256 GB', 100, 22490000, 27490000, '2022-08-05 04:02:33', '2022-08-05 04:02:33', NULL, 'samsung-galaxy-s22', 0, 0, 'Sản phẩm,Samsung,Dòng S'),
(24, 9, 28, 'Samsung Galaxy A13', 'https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-xanh-thumb-1-600x600.jpg', '<h3 style=\"text-align: justify;\">Thiết kế trẻ trung, b&oacute;ng bẩy</h3>\r\n<p style=\"text-align: justify;\"><a title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">Điện thoại</a> Galaxy A13 được chế tạo từ vật liệu nhựa nguy&ecirc;n khối gi&uacute;p thiết bị trở n&ecirc;n bền bỉ, độ ho&agrave;n thiện của c&aacute;c chi tiết kh&aacute; tốt. Khi cầm nắm sử dụng m&igrave;nh thấy m&aacute;y kh&ocirc;ng xuất hiện t&igrave;nh trạng &ldquo;ọp ẹp&rdquo; như tr&ecirc;n một v&agrave;i sản phẩm được l&agrave;m từ nhựa kh&aacute;c.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025943.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế nguy&ecirc;n khối - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025943.jpg\" alt=\"Thiết kế nguy&ecirc;n khối - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025943.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Mặt lưng thiết kế nhựa b&oacute;ng mang đến vẻ ngo&agrave;i &oacute;ng &aacute;nh, tuy nhi&ecirc;n t&igrave;nh trạng b&aacute;m dấu v&acirc;n tay thường xuy&ecirc;n xuất hiện khi m&igrave;nh sử dụng, điều n&agrave;y được cải thiện đối với phi&ecirc;n bản m&agrave;u xanh dương v&agrave; cam.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025945.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Mặt lưng nhựa b&oacute;ng - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025945.jpg\" alt=\"Mặt lưng nhựa b&oacute;ng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025945.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Cụm camera ch&iacute;nh được đặt thẳng tr&ecirc;n mặt lưng, kh&ocirc;ng c&ograve;n bố tr&iacute; trong cụm nữa, mang đến một vẻ ngo&agrave;i cực kỳ &ldquo;ph&aacute; c&aacute;ch&rdquo;, gi&uacute;p bạn g&acirc;y ấn tượng đối với mọi người xung quanh bởi thiết kế độc đ&aacute;o chưa từng c&oacute; tr&ecirc;n d&ograve;ng sản phẩm <a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" rel=\"noopener\">A series</a>.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025946.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Bố tr&iacute; camera lạ mắt - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025946.jpg\" alt=\"Bố tr&iacute; camera lạ mắt - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025946.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Trải nghiệm đ&atilde; mắt với m&agrave;n h&igrave;nh lớn&nbsp;</h3>\r\n<p style=\"text-align: justify;\">Galaxy A23 sở hữu m&agrave;n h&igrave;nh tr&agrave;n viền c&oacute; k&iacute;ch thước l&ecirc;n đến 6.6 inch sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh PLS TFT LCD, mang lại chất lượng h&igrave;nh ảnh theo m&igrave;nh đ&aacute;nh gi&aacute; c&oacute; độ tương phản kh&aacute; tốt, t&aacute;i tạo m&agrave;u sắc ch&iacute;nh x&aacute;c.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025948.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh k&iacute;ch thước lớn - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025948.jpg\" alt=\"M&agrave;n h&igrave;nh k&iacute;ch thước lớn - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025948.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Với độ ph&acirc;n giải Full HD+ (1080 x 2408 Pixels) cho ra h&igrave;nh ảnh hiển thị sắc n&eacute;t v&agrave; chi tiết, gi&uacute;p cho những trải nghiệm giải tr&iacute; th&ecirc;m phần sống động.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025950.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;u sắc hiển thị đẹp mắt - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025950.jpg\" alt=\"M&agrave;u sắc hiển thị đẹp mắt - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025950.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Chụp ảnh sắc n&eacute;t c&ugrave;ng nhiều t&iacute;nh năng</h3>\r\n<p style=\"text-align: justify;\">Trang bị ở phần mặt lưng l&agrave; 4 camera với th&ocirc;ng số lần lượt: Camera ch&iacute;nh 50 MP, phụ 5 MP v&agrave; 2 cảm biến c&oacute; c&ugrave;ng độ ph&acirc;n giải 2 MP, gi&uacute;p bạn dễ d&agrave;ng ghi lại những khoảnh khắc th&uacute; vị xung quanh.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025952.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ nhiều t&iacute;nh năng - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025952.jpg\" alt=\"Hỗ trợ nhiều t&iacute;nh năng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025952.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Ở điều kiện đầy đủ &aacute;nh s&aacute;ng, ảnh cho ra c&oacute; độ chi tiết tốt, m&agrave;u sắc h&agrave;i h&ograve;a, ảnh zoom l&ecirc;n kh&ocirc;ng bị bể qu&aacute; nhiều, gi&uacute;p việc hậu kỳ - chỉnh sửa ảnh trở n&ecirc;n dễ d&agrave;ng hơn. Tuy nhi&ecirc;n ảnh chụp đ&ecirc;m chưa thực sự l&agrave;m m&igrave;nh ưng &yacute; cho lắm bởi t&igrave;nh trạng nhiễu ảnh vẫn c&ograve;n xuất hiện.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025953.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp ở m&ocirc;i trường đủ s&aacute;ng - Samsung Galaxy A13 6GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025953.jpg\" alt=\"Ảnh chụp ở m&ocirc;i trường đủ s&aacute;ng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025953.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Với t&iacute;nh năng chụp ảnh x&oacute;a ph&ocirc;ng, Galaxy A13 cho ra c&aacute;c bức ảnh kh&aacute; tốt, m&agrave;u sắc t&aacute;i tạo sắc n&eacute;t, chủ thể được t&aacute;ch biệt với nền v&agrave; t&igrave;nh trạng lẹm ảnh gần như kh&ocirc;ng xuất hiện khi chụp ở m&ocirc;i trường đầy đủ &aacute;nh s&aacute;ng.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025955.jpg\"><img class=\"lazyload\" title=\"Ảnh chụp ch&acirc;n dung - Samsung Galaxy A13 6GB\" alt=\"Ảnh chụp ch&acirc;n dung - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025955.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">C&ograve;n ở chế độ chụp ảnh g&oacute;c si&ecirc;u rộng, h&igrave;nh ảnh được thể hiện một c&aacute;ch chi tiết, c&aacute;c g&oacute;c mở rộng kh&ocirc;ng bị b&oacute;p m&eacute;o qu&aacute; nhiều, mang đến h&igrave;nh ảnh c&oacute; độ ch&acirc;n thực cao.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025957.jpg\"><img class=\"lazyload\" title=\"Chế độ g&oacute;c si&ecirc;u rộng - Samsung Galaxy A13 6GB\" alt=\"Chế độ g&oacute;c si&ecirc;u rộng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025957.jpg\" /></a></p>\r\n<h3 style=\"text-align: justify;\">Xử l&yacute; mượt m&agrave; cho mọi t&aacute;c vụ cơ bản</h3>\r\n<p style=\"text-align: justify;\">Trang bị b&ecirc;n trong l&agrave; bộ vi xử l&yacute; được ch&iacute;nh <a title=\"Tham khảo điện thoại Samsung kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" rel=\"noopener\">Samsung</a> nghi&ecirc;n cứu v&agrave; ph&aacute;t triển mang t&ecirc;n Exynos 850 8 nh&acirc;n với hiệu suất tối đa 2.0 GHz, theo m&igrave;nh đ&aacute;nh gi&aacute; th&igrave; đ&acirc;y l&agrave; con chip c&oacute; hiệu năng tương đối ổn trong ph&acirc;n kh&uacute;c, c&oacute; thể đ&aacute;p ứng ổn định trong c&aacute;c c&ocirc;ng việc h&agrave;ng ng&agrave;y.</p>\r\n<p style=\"text-align: justify;\">Dưới đ&acirc;y l&agrave; bảng đ&aacute;nh gi&aacute; hiệu năng qua hai ứng dụng: Benchmark (tr&aacute;i) v&agrave; 3D Mark (phải) m&agrave; m&igrave;nh c&oacute; test qua:</p>\r\n<p style=\"text-align: justify;\">Đối với tựa game Li&ecirc;n Qu&acirc;n Mobile ở mức max setting, m&aacute;y mang lại cho m&igrave;nh trải nghiệm chưa được tốt cho lắm khi t&igrave;nh trạng drop FPS vẫn c&ograve;n xuất hiện, người d&ugrave;ng n&ecirc;n c&agrave;i đặt mức cấu h&igrave;nh trung b&igrave;nh để đảm bảo trải nghiệm chơi được ổn định nhất.</p>\r\n<p style=\"text-align: justify;\">PUBG Mobile - tựa game n&agrave;y bạn chơi ở mức đồ họa c&acirc;n bằng v&agrave; tốc độ khung h&igrave;nh cực cao, m&aacute;y đ&aacute;p ứng với mức FPS dao động tầm 20 - 28, t&igrave;nh trạng khựng m&agrave;n h&igrave;nh khi gặp kẻ địch hay di chuyển bằng phương tiện vẫn c&ograve;n xuất hiện, bạn c&oacute; thể hạ đồ họa xuống để c&oacute; trải nghiệm chơi tốt nhất.&nbsp;</p>\r\n<p style=\"text-align: justify;\">Trang bị <a href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\" rel=\"noopener\">RAM 6 GB</a>&nbsp;mang đến sự mượt m&agrave; cho t&aacute;c vụ đa nhiệm t&igrave;nh trạng load lại app gần như kh&ocirc;ng xảy ra cho d&ugrave; mở nhiều ứng dụng c&ugrave;ng l&uacute;c.</p>\r\n<h3 style=\"text-align: justify;\">Sử dụng l&acirc;u d&agrave;i với vi&ecirc;n pin lớn</h3>\r\n<p style=\"text-align: justify;\">M&aacute;y cung cấp một thời gian sử dụng l&acirc;u d&agrave;i nhờ trang bị vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n đến 5000 mAh, khi sử dụng li&ecirc;n tục thời gian onscren l&ecirc;n đến hơn 8 tiếng.</p>\r\n<p style=\"text-align: justify;\">Samsung trang bị cho m&aacute;y c&ocirc;ng nghệ sạc 15 W gi&uacute;p tối ưu thời gian lấp đầy vi&ecirc;n pin xuống c&ograve;n 2 giờ 40 ph&uacute;t, tuy nhi&ecirc;n mức thời gian n&agrave;y vẫn chưa thực sự nhanh, n&ecirc;n bạn c&oacute; thể sạc thiết bị v&agrave;o những l&uacute;c nghỉ ngơi để kh&ocirc;ng mất nhiều thời gian chờ đợi.</p>\r\n<p style=\"text-align: justify;\">Qua những trải nghiệm tr&ecirc;n cho thấy Samsung Galaxy A13 đ&aacute;p ứng kh&aacute; tốt c&aacute;c nhu cầu sử dụng cơ bản h&agrave;ng ng&agrave;y, m&aacute;y c&oacute; thiết kế trẻ trung, camera chụp ảnh ổn, hứa hẹn sẽ mang đến những trải nghiệm tuyệt vời so với mức gi&aacute; m&agrave; n&oacute; trang bị.</p>', 'Samsung Galaxy A13 6GB đem đến những trải nghiệm tuyệt vời cùng mức giá cực kỳ phải chăng, nhằm hướng tới đối tượng học sinh, sinh viên. Máy có thiết kế trẻ trung, thanh thoát, cụm camera được chăm chút chỉnh chu cùng thời gian sử dụng đáp ứng cho cả ngày dài.', 'Màn hình: PLS TFT LCD6.6\"Full HD+\r\nHệ điều hành: Android 12\r\nCamera sau: Chính 50 MP & Phụ 5 MP, 2 MP, 2 MP\r\nCamera trước: 8 MP\r\nChip: Exynos 850\r\nRAM: 6 GB\r\nBộ nhớ trong: 128 GB', 100, 4890000, 5190000, '2022-08-05 04:06:21', '2022-08-05 04:06:21', NULL, 'samsung-galaxy-a13', 0, 0, 'Sản phẩm,Samsung,Dòng A');
INSERT INTO `products` (`id`, `user_id`, `cat_id`, `name`, `thumbnail`, `content`, `summary`, `description`, `qty`, `price`, `old_price`, `created_at`, `updated_at`, `deleted_at`, `slug`, `num_views`, `num_purchases`, `tags`) VALUES
(25, 9, 28, 'Samsung Galaxy A73', 'https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-5g-xanh-thumb-600x600.jpg', '<h3 style=\"text-align: justify;\"><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a73\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy A73 5G 128GB</a> được xem l&agrave; sản phẩm nổi bật nhất d&ograve;ng <a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" rel=\"noopener\">Galaxy A</a> 2022 mới ra mắt, m&aacute;y trang bị bộ th&ocirc;ng số kỹ thuật ấn tượng về phần hiệu năng, chất lượng m&agrave;n h&igrave;nh v&agrave; nổi bật nhất trong số đ&oacute; l&agrave; camera khi n&oacute; đem lại bức ảnh c&oacute; độ ph&acirc;n giải l&ecirc;n đến 108 MP.</h3>\r\n<h3 style=\"text-align: justify;\">Thỏa sức nhiếp ảnh với camera chụp ảnh sắc n&eacute;t</h3>\r\n<p style=\"text-align: justify;\">Điều m&agrave; m&igrave;nh ấn tượng nhất tr&ecirc;n Galaxy A73 5G đ&oacute; ch&iacute;nh l&agrave; khả năng chụp ảnh khi m&aacute;y sở hữu 4 ống k&iacute;nh c&oacute; độ ph&acirc;n giải lần lượt: Camera ch&iacute;nh 108 MP,<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" rel=\"noopener\"> camera g&oacute;c si&ecirc;u rộng</a>&nbsp;12 MP,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\" rel=\"noopener\">cảm biến độ s&acirc;u</a> v&agrave; <a href=\"https://www.thegioididong.com/dtdd-camera-macro\" target=\"_blank\" rel=\"noopener\">macro</a> c&oacute; c&ugrave;ng độ ph&acirc;n giải 5 MP gi&uacute;p c&aacute;c bức ảnh thu lại trở n&ecirc;n chi tiết hơn.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041611.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Trang bị 4 camera - Samsung Galaxy A73 5G 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041611.jpg\" alt=\"Trang bị 4 camera - Samsung Galaxy A73 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041611.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Chụp ảnh ở m&ocirc;i trường đủ s&aacute;ng, ảnh do Galaxy A73 thu lại c&oacute; độ chi tiết cao, m&agrave;u sắc tươi tắn, ảnh sau khi zoom l&ecirc;n vẫn mang một chất lượng rất tốt bởi camera ch&iacute;nh c&oacute; độ ph&acirc;n giải cao, rất ph&ugrave; hợp với những ai thường d&ugrave;ng điện thoại để chụp ảnh.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041613.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Ảnh chụp chế độ thường - Samsung Galaxy A73 5G 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041613.jpg\" alt=\"Ảnh chụp chế độ thường - Samsung Galaxy A73 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041613.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">Ảnh chụp v&agrave;o buổi đ&ecirc;m cũng cho ra chất lượng kh&aacute;, hiện tượng nhiễu hạt được cải thiện, c&acirc;n bằng &aacute;nh s&aacute;ng tốt gi&uacute;p tổng thể bức ảnh tr&ocirc;ng rất h&agrave;i h&ograve;a.</p>\r\n<p style=\"text-align: justify;\"><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041615.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Ảnh chụp đ&ecirc;m - Samsung Galaxy A73 5G 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041615.jpg\" alt=\"Ảnh chụp đ&ecirc;m - Samsung Galaxy A73 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246195/samsung-galaxy-a73-190422-041615.jpg\" /></a></p>\r\n<p style=\"text-align: justify;\">C&ograve;n về chế độ chụp ch&acirc;n dung cho d&ugrave; l&agrave; ng&agrave;y hay đ&ecirc;m th&igrave; kết quả thu được đều l&agrave;m m&igrave;nh bất ngờ, bởi m&aacute;y mang đến bức ảnh tr&ocirc;ng rất nịnh mắt v&agrave; c&oacute; chiều s&acirc;u, độ chi tiết tốt, ranh giới giữa những v&ugrave;ng x&oacute;a ph&ocirc;ng v&agrave; chủ thể được xử l&yacute; mịn m&agrave;ng.</p>\r\n<p style=\"text-align: justify;\">Hỗ trợ t&iacute;nh năng zoom 2x, 4x v&agrave; 10x mang lại khả năng ph&oacute;ng to khung cảnh một c&aacute;ch nhanh ch&oacute;ng, gi&uacute;p bạn quan s&aacute;t đối tượng v&agrave; bắt lại những khoảnh khắc đẹp chỉ trong t&iacute;ch tắc.</p>\r\n<p style=\"text-align: justify;\">Cuối c&ugrave;ng l&agrave; chụp ảnh selfie tr&ecirc;n camera 32 MP cho những bức ảnh mịn m&agrave;n, ảnh x&oacute;a ph&ocirc;ng tốt c&aacute;c v&ugrave;ng t&oacute;c li ti đều được t&aacute;ch khỏi nền một c&aacute;ch mượt m&agrave;.</p>\r\n<h3 style=\"text-align: justify;\">Vận h&agrave;nh ổn định nhờ chipset mạnh mẽ đến từ Qualcomm</h3>\r\n<p style=\"text-align: justify;\">Cung cấp sức mạnh cho Galaxy A73 l&agrave; bộ vi xử l&yacute; chuy&ecirc;n game Snapdragon 778G 5G 8 nh&acirc;n đến từ nh&agrave; Qualcomm sản xuất tr&ecirc;n tiến tr&igrave;nh 6 nm ti&ecirc;n tiến, được xem l&agrave; một cấu h&igrave;nh kh&aacute; tốt gi&uacute;p bạn chơi tốt hầu hết c&aacute;c tựa game đang hiện h&agrave;nh (04/2022).</p>\r\n<p style=\"text-align: justify;\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/danh-gia-hieu-nang-cuc-khung-tren-snapdragon-778g-5g-8-nhan-1386463\" target=\"_blank\" rel=\"noopener\">Đ&aacute;nh gi&aacute; hiệu năng cực khủng tr&ecirc;n Snapdragon 778G 5G 8 nh&acirc;</a></p>\r\n<p style=\"text-align: justify;\">Điểm số của Snapdragon 778G 5G cũng l&agrave;m cho m&igrave;nh kh&aacute; bất ngờ khi sức mạnh của chip được khai th&aacute;c gần như tuyệt đối tr&ecirc;n con m&aacute;y n&agrave;y với số điểm hiệu năng tr&ecirc;n hai ứng dụng đo th&ocirc;ng số như sau: 778 (đơn nh&acirc;n), 2871 (đa nh&acirc;n) cho BenchMark v&agrave; 11512 điểm tr&ecirc;n PCMark.</p>\r\n<p style=\"text-align: justify;\"><em>*BenchMark b&ecirc;n tr&aacute;i v&agrave; PCMark b&ecirc;n phải</em></p>\r\n<p style=\"text-align: justify;\">Khả năng xử l&yacute; đồ họa khi chiến game c&oacute; lẽ l&agrave; điều được kha kh&aacute; người d&ugrave;ng quan t&acirc;m, n&ecirc;n m&igrave;nh c&oacute; sử dụng Galaxy A73 5G để chơi qua hai tr&ograve; chơi hiện đang rất thịnh h&agrave;nh ở thời điểm hiện tại l&agrave; Li&ecirc;n Qu&acirc;n Mobile, PUBG Mobile.</p>\r\n<p style=\"text-align: justify;\">Đầu ti&ecirc;n l&agrave; Li&ecirc;n Qu&acirc;n Mobile ở mức c&agrave;i đặt cấu h&igrave;nh cao nhất* th&igrave; m&aacute;y mang lại cho m&igrave;nh qu&aacute; tr&igrave;nh chơi rất ổn định, h&igrave;nh ảnh hiển thị xuất sắc v&agrave; tốc độ khung h&igrave;nh dao động tầm 58 - 63 FPS, quả thực l&agrave; một con kh&aacute; ấn tượng.</p>\r\n<p style=\"text-align: justify;\"><em>*Mức cấu h&igrave;nh cao nhất tr&ecirc;n Li&ecirc;n Qu&acirc;n Mobile m&agrave; m&aacute;y c&oacute; thể bật được</em></p>\r\n<p style=\"text-align: justify;\">Tiếp đến l&agrave; tựa game bắn s&uacute;ng sinh tồn PUBG Mobile ở mức đồ đồ họa HDR c&ugrave;ng tốc độ khung h&igrave;nh cực cao để đảm bảo chất lượng hiển thị đẹp mắt nhất c&oacute; thể, h&igrave;nh ảnh nh&acirc;n vật v&agrave; quang cảnh được đổ b&oacute;ng tr&ocirc;ng rất ch&acirc;n thực v&agrave; r&otilde; n&eacute;t, gi&uacute;p qu&aacute; tr&igrave;nh chơi game trở n&ecirc;n dễ d&agrave;ng hơn.</p>\r\n<p style=\"text-align: justify;\">Về phần trải nghiệm th&igrave; m&aacute;y mang đến những ph&uacute;t gi&acirc;y giải tr&iacute; kh&aacute; ổn định, tốc độ khung h&igrave;nh đo được loanh quanh 38 - 41 FPS, m&igrave;nh c&oacute; thử chơi qua 2 trận (khoảng 40 ph&uacute;t) th&igrave; thấy nhiệt độ Galaxy A73 5G vẫn ở mức chấp nhận được, chỉ ấm nhẹ v&agrave; kh&aacute; l&agrave; dễ chịu.</p>\r\n<p style=\"text-align: justify;\">Ở những t&aacute;c vụ nhẹ nh&agrave;ng như lướt web, nghe nhạc, xem phim th&igrave; m&aacute;y vận h&agrave;nh rất l&agrave; trơn tru, đ&oacute;ng/mở ứng dụng nhanh ch&oacute;ng m&agrave; kh&ocirc;ng gặp hiện tượng khựng nhờ trang bị <a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" rel=\"noopener\">RAM 8 GB</a> c&ugrave;ng t&iacute;nh năng mở rộng RAM th&ecirc;m 8 GB (tổng số RAM đạt được l&agrave; 16 GB).</p>\r\n<p style=\"text-align: justify;\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/virtual-ram-tren-smartphone-la-gi-day-la-xu-huong-tuong-lai-1337924\" target=\"_blank\" rel=\"noopener\">RAM ảo (Virtual RAM) tr&ecirc;n smartphone l&agrave; g&igrave;?</a></p>\r\n<h3 style=\"text-align: justify;\">Vẻ ngo&agrave;i trẻ trung đầy c&aacute; t&iacute;nh</h3>\r\n<p style=\"text-align: justify;\">Tổng quan chiếc m&aacute;y n&agrave;y c&oacute; hơi hướng giống với người anh em tiền nhiệm&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a52s-5g\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy A52s 5G</a>, với mặt lưng được thiết kế từ nhựa nh&aacute;m gi&uacute;p hạn chế lưu lại dấu v&acirc;n tay trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p style=\"text-align: justify;\">Bộ khung Galaxy A73 được l&agrave;m từ nhựa c&oacute; hiệu ứng giả kim loại s&aacute;ng b&oacute;ng, mang đến một vẻ ngo&agrave;i sang trọng, cao cấp hơn cho sản phẩm.</p>\r\n<p style=\"text-align: justify;\">C&aacute;c ống k&iacute;nh v&agrave; đ&egrave;n LED được <a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">Samsung</a> bố tr&iacute; chung một cụm với kiểu thiết kế &ldquo;hiệu ứng nổi 3D&rdquo;, để mang đến một c&aacute;i nh&igrave;n liền mạch hơn giữa cụm camera v&agrave; mặt lưng.</p>\r\n<p style=\"text-align: justify;\">Về phần m&agrave;n h&igrave;nh, Galaxy A73 sở hữu cho m&igrave;nh một tấm nền Super AMOLED Plus c&oacute; k&iacute;ch thước l&ecirc;n đến 6.7 inch v&agrave; độ ph&acirc;n giải Full HD+ (1080 x 2400 Pixels), <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a> cho ra m&agrave;u sắc rực rỡ bởi độ tương phản cao, m&agrave;u đen s&acirc;u c&ugrave;ng h&igrave;nh ảnh hiển thị chi tiết gi&uacute;p m&igrave;nh trải nghiệm ch&acirc;n thực hơn tr&ecirc;n c&aacute;c bộ phim hay chơi game h&agrave;nh động.</p>\r\n<p style=\"text-align: justify;\">Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-plus-la-gi-905774\" target=\"_blank\" rel=\"noopener\">M&agrave;n h&igrave;nh Super AMOLED Plus l&agrave; g&igrave;? Thiết bị n&agrave;o c&oacute; Super AMOLED Plus?</a></p>\r\n<p style=\"text-align: justify;\">M&aacute;y hỗ trợ tần số qu&eacute;t 120 Hz gi&uacute;p m&igrave;nh thao t&aacute;c sử dụng trở n&ecirc;n mượt m&agrave; hơn hẳn, độ s&aacute;ng tối đa 800 nits cũng l&agrave; một điểm cộng hỗ trợ cho việc sử dụng thiết bị ngo&agrave;i trời tốt hơn bởi h&igrave;nh ảnh v&agrave; văn bản được hiển thị kh&aacute; r&otilde; r&agrave;ng.</p>\r\n<p style=\"text-align: justify;\">M&igrave;nh rất th&iacute;ch phần m&agrave;n h&igrave;nh tr&ecirc;n Galaxy A73 5G bởi h&atilde;ng tối ưu cạnh viền rất mỏng, phần \"cằm\" cũng được tinh chỉnh k&iacute;ch thước gần bằng với c&aacute;c cạnh c&ograve;n lại, mang đến cho m&igrave;nh một c&aacute;i nh&igrave;n thanh tho&aacute;t m&agrave; kh&ocirc;ng bị th&ocirc; như tr&ecirc;n một v&agrave;i sản phẩm c&oacute; viền lớn.</p>\r\n<h3 style=\"text-align: justify;\">Năng lượng đ&aacute;p ứng cho cả ng&agrave;y d&agrave;i</h3>\r\n<p style=\"text-align: justify;\">B&ecirc;n trong th&acirc;n h&igrave;nh mỏng 7.6 mm của <span style=\"text-align: justify;\">Galaxy A73&nbsp;</span>l&agrave; một vi&ecirc;n pin c&oacute; dung lượng 5000 mAh, đem đến một thời gian sử dụng l&acirc;u d&agrave;i l&ecirc;n đến hơn 8 tiếng* khi m&agrave; m&igrave;nh c&oacute; d&ugrave;ng n&oacute; để thực hiện c&aacute;c t&aacute;c vụ lướt web, xem phim, chơi game li&ecirc;n tục.</p>\r\n<p style=\"text-align: justify;\">Galaxy A73 5G được hỗ trợ c&ocirc;ng nghệ sạc tối đa l&ecirc;n đến 25 W gi&uacute;p r&uacute;t ngắn thời gian lấp đầy vi&ecirc;n pin từ 0 - 100% xuống c&ograve;n 2 giờ 5 ph&uacute;t*, đ&acirc;y l&agrave; khoảng thời gian tương đối ổn khi sạc vi&ecirc;n pin lớn như vậy.</p>\r\n<p style=\"text-align: justify;\"><em>*Thời gian sử dụng/sạc c&oacute; thể thay đổi t&ugrave;y v&agrave;o t&aacute;c vụ v&agrave; nhiệt độ của m&aacute;y</em></p>\r\n<p style=\"text-align: justify;\">Samsung c&oacute; lẽ l&agrave; thương hiệu được nhiều kh&aacute;ch h&agrave;ng nghĩ đến khi muốn chọn mua cho m&igrave;nh một chiếc điện thoại th&ocirc;ng minh, một trong những ứng cử vi&ecirc;n s&aacute;ng gi&aacute; trong số đ&oacute; l&agrave; Samsung Galaxy A73 5G, sở hữu một bộ th&ocirc;ng số kỹ thuật ấn tượng b&ecirc;n trong sẽ kh&ocirc;ng l&agrave;m bạn thất vọng với những g&igrave; m&agrave; n&oacute; mang lại, hứa hẹn sẽ mang đến những trải nghiệm tuyệt vời đến với bạn.</p>', 'Samsung Galaxy A73 5G 128GB được xem là sản phẩm nổi bật nhất dòng Galaxy A 2022 mới ra mắt, máy trang bị bộ thông số kỹ thuật ấn tượng về phần hiệu năng, chất lượng màn hình và nổi bật nhất trong số đó là camera khi nó đem lại bức ảnh có độ phân giải lên đến 108 MP.', 'Màn hình: Super AMOLED Plus6.7\"Full HD+\r\nHệ điều hành: Android 12\r\nCamera sau: Chính 108 MP & Phụ 12 MP, 5 MP, 5 MP\r\nCamera trước: 32 MP\r\nChip: Snapdragon 778G 5G\r\nRAM: 8 GB\r\nBộ nhớ trong: 128 GB', 100, 11690000, 11990000, '2022-08-05 04:09:16', '2022-08-05 04:09:16', NULL, 'samsung-galaxy-a73', 0, 0, 'Sản phẩm,Samsung,Dòng A'),
(26, 9, 22, 'Asus TUF Gaming FX506LHB', 'https://cdn.tgdd.vn/Products/Images/44/279259/TimerThumb/asus-tuf-gaming-fx506lhb-i5-hn188w-(12).jpg', '<h3>Nếu bạn đang t&igrave;m kiếm một chiếc <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Gaming hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" rel=\"noopener\">laptop gaming</a> nhưng vẫn sở hữu một mức gi&aacute; phải chăng th&igrave; <a title=\"Laptop Asus TUF Gaming FX506LHB i5 (HN188W)\" href=\"https://www.thegioididong.com/laptop/asus-tuf-gaming-fx506lhb-i5-hn188w\" target=\"_blank\" rel=\"noopener\">laptop Asus TUF Gaming FX506LHB i5 (HN188W)</a> sẽ l&agrave; sự lựa chọn đ&aacute;ng c&acirc;n nhắc với card đồ họa rời NVIDIA GeForce GTX mạnh mẽ c&ugrave;ng phong c&aacute;ch thiết kế cứng c&aacute;p, độc đ&aacute;o.&nbsp;</h3>\r\n<p>&bull; Chạy mượt m&agrave; c&aacute;c ứng dụng văn ph&ograve;ng tr&ecirc;n Word, Excel, PowerPoint,... đến chiến những con game đ&igrave;nh đ&aacute;m nhờ bộ vi xử l&yacute; <strong>Intel Core i5 10300H</strong> kết hợp với card đồ họa rời <strong>NVIDIA GeForce GTX 1650 4 GB</strong> mạnh mẽ.&nbsp;</p>\r\n<p>&bull; <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Asus hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" rel=\"noopener\">Laptop Asus</a> đa nhiệm mượt m&agrave; tr&ecirc;n nhiều cửa sổ Chrome c&ugrave;ng l&uacute;c nhờ bộ nhớ <strong>RAM 8 GB</strong>, b&ecirc;n cạnh đ&oacute; c&ograve;n mang đến tốc độ khởi động m&aacute;y v&agrave; ứng dụng nhanh ch&oacute;ng với ổ cứng <strong>SSD 512 GB</strong>.&nbsp; &nbsp;</p>\r\n<p>&bull; <a title=\"Xem thệm c&aacute;c mẫu Laptop hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">Laptop</a> c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh <strong>15.6 inch</strong> c&ugrave;ng tần số qu&eacute;t <strong>144 Hz</strong> mang đến những trải nghiệm chiến game cực đỉnh, kh&ocirc;ng bị giật lag hay x&eacute; h&igrave;nh khi chơi những tựa game c&oacute; tốc độ cao.</p>\r\n<p>&bull; <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Asus TUF Gaming hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-tuf-gaming\" target=\"_blank\" rel=\"noopener\">Laptop Asus TUF Gaming</a> được bao bọc bởi lớp vỏ nhựa m&agrave;u đen huyền b&iacute;, trọng lượng <strong>2.3 kg</strong> cho ph&eacute;p bạn chiến game ở mọi kh&ocirc;ng gian.</p>\r\n<p>&bull; M&aacute;y được trang bị <strong>đ&egrave;n b&agrave;n ph&iacute;m chuyển m&agrave;u RGB độc đ&aacute;o</strong>, tăng độ hăng h&aacute;i cho game thủ mỗi khi chiến game.</p>', 'Nếu bạn đang tìm kiếm một chiếc laptop gaming nhưng vẫn sở hữu một mức giá phải chăng thì laptop Asus TUF Gaming FX506LHB i5 (HN188W) sẽ là sự lựa chọn đáng cân nhắc với card đồ họa rời NVIDIA GeForce GTX mạnh mẽ cùng phong cách thiết kế cứng cáp, độc đáo.', 'CPU: i510300H2.5GHz\r\nRAM: 8 GBDDR4 2 khe (1 khe 8GB + 1 khe rời)2933 MHz\r\nỔ cứng: 512 GB SSD NVMe PCIe (Có thể tháo ra, lắp thanh khác tối đa 1TB)\r\nMàn hình: 15.6\"Full HD (1920 x 1080)144Hz\r\nCard màn hình: Card rờiGTX 1650 4GB', 100, 16490000, 19990000, '2022-08-05 06:25:16', '2022-08-05 14:07:15', NULL, 'asus-tuf-gaming-fx506lhb', 2, 0, 'Sản phẩm,Laptop,Gaming,Asus,Tuf'),
(27, 9, 22, 'Laptop Asus Vivobook 15 X1502ZA', 'https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-600x600.jpg', '<h3>Hiệu năng đ&aacute;ng gờm trong tầm gi&aacute;&nbsp;</h3>\r\n<p>Trước khi ph&acirc;n t&iacute;ch kỹ về em m&aacute;y n&agrave;y th&igrave; m&igrave;nh sẽ giới thiệu sơ qua con <a title=\"T&igrave;m hiểu th&ecirc;m về chip Intel Gen 12 \" href=\"https://www.thegioididong.com/hoi-dap/intel-the-he-12-alder-lake-tren-laptop-cau-hinh-va-thoi-1396454\" target=\"_blank\" rel=\"noopener\">chip Gen 12</a> mới nhất của nh&agrave; Intel, so với d&ograve;ng G tiết kiệm điện năm ngo&aacute;i th&igrave; d&ograve;ng P năm nay vẫn l&agrave; tiết kiệm điện nhưng lại mang một hiệu năng cao hơn đ&aacute;ng kể. Nếu bạn vẫn chưa h&igrave;nh dung được r&otilde; sức mạnh của em n&oacute; th&igrave; những con số sau đ&acirc;y sẽ l&agrave; minh chứng cụ thể nhất.&nbsp; &nbsp;&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Hiệu năng chip Intel Gen 12\" src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-1.jpg\" alt=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Hiệu năng chip Intel Gen 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-1.jpg\" /></a></p>\r\n<p>Nếu khả năng ăn điện của d&ograve;ng G năm ngo&aacute;i l&agrave; <strong>28 W</strong> th&igrave; con số tr&ecirc;n d&ograve;ng P năm nay l&ecirc;n đến <strong>64 W</strong> v&agrave; kh&ocirc;ng chỉ dừng lại ở đ&oacute;, khi m&igrave;nh thực hiện b&agrave;i kiểm tra th&ocirc;ng qua <strong>Cinebench R23</strong> đ&atilde; thu được số điểm đơn nh&acirc;n l&agrave; <strong>1630 điểm</strong> v&agrave; đa nh&acirc;n l&ecirc;n đến hơn <strong>10.000 điểm</strong>, một con số thuộc h&agrave;ng cực khủng trong ph&acirc;n kh&uacute;c <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop học tập - văn ph&ograve;ng hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" rel=\"noopener\">laptop học tập - văn ph&ograve;ng</a>.&nbsp;&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-2.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Test Cinebench R23\" src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-2.jpg\" alt=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Test Cinebench R23\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-2.jpg\" /></a></p>\r\n<p>M&igrave;nh đ&atilde; từng nghĩ rằng với con số <strong>hơn 10.000 điểm</strong> th&igrave; chiếc m&aacute;y n&agrave;y liệu c&oacute; hoạt động ổn kh&ocirc;ng, v&agrave; khi sở hữu em n&oacute; m&igrave;nh đ&atilde; t&igrave;m ra được c&acirc;u trả lời. Hệ thống tản nhiệt của chiếc <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">laptop</a> n&agrave;y đ&atilde; cải thiện tốt hơn rất nhiều với số lượng c&aacute;nh quạt l&ecirc;n đến <strong>87 c&aacute;nh</strong> được uốn cong <strong>3D</strong>, từ đ&oacute; cho ph&eacute;p m&aacute;y tạo ra luồng gi&oacute; nhiều hơn <strong>30%</strong> gi&uacute;p bạn duy tr&igrave; m&aacute;y ổn định ở nhiệt độ trung b&igrave;nh l&agrave; <strong>65 độ</strong>.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-3.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Hệ thống tản nhiệt\" src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-3.jpg\" alt=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Hệ thống tản nhiệt\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-3.jpg\" /></a></p>\r\n<p>Card đồ họa của m&aacute;y cũng l&agrave; phần khiến m&igrave;nh thắc mắc rất nhiều v&igrave; đ&atilde; h&atilde;ng đ&atilde; trang bị đến tận con chip Gen 12 m&agrave; vẫn sử dụng card <strong>Intel UHD Graphics</strong> nhưng sau khi t&igrave;m hiểu kỹ c&agrave;ng, m&igrave;nh sẽ giải đ&aacute;p ngay cho c&aacute;c bạn lu&ocirc;n nh&eacute;. Vốn dĩ em m&aacute;y n&agrave;y c&oacute; nh&acirc;n đồ họa Iris Xe nhưng RAM trang bị sẵn chỉ l&agrave; 1 thanh <strong>Onboard</strong>, bạn cần phải n&acirc;ng cấp RAM để k&iacute;ch hoạt <a title=\"RAM Dual-Channel l&agrave; g&igrave;?\" href=\"https://www.thegioididong.com/hoi-dap/ram-dual-channel-la-gi-cach-cam-ram-dung-chuan-de-chay-1320035\" target=\"_blank\" rel=\"noopener\">Dual-Channel</a> mới c&oacute; thể sử dụng được card <strong>Intel Iris Xe Graphics</strong>.</p>\r\n<p>M&igrave;nh đ&atilde; test qua thử con game quốc d&acirc;n&nbsp;<strong>Li&ecirc;n Minh Huyền Thoại</strong>. Th&uacute; thật m&igrave;nh nghĩ em m&aacute;y n&agrave;y sẽ th&iacute;ch hợp để chơi game giải tr&iacute; nhẹ nh&agrave;ng hơn v&igrave; khi c&agrave;i đặt ở mức cao nhất th&igrave; c&oacute; vẻ hơi đuối, tốc độ khung h&igrave;nh chỉ ở mức ổn định v&agrave; sẽ c&oacute; hiện tượng tuột FPS nếu xảy ra c&aacute;c cuộc giao tranh nảy lửa tr&ecirc;n chiến trường.&nbsp;&nbsp;</p>\r\n<p>Với những sinh vi&ecirc;n như m&igrave;nh th&igrave; việc n&acirc;ng cấp RAM cũng kh&aacute; đắn đo v&igrave; chi ph&iacute; sẽ dao động từ v&agrave;i trăm đến v&agrave;i triệu, nhưng đ&oacute; ho&agrave;n to&agrave;n kh&ocirc;ng phải l&agrave; vấn đề qu&aacute; lớn khi chỉ với bộ vi xử l&yacute; <strong>Intel Core i5 Alder Lake 1240P</strong> n&agrave;y th&ocirc;i cũng đủ để m&igrave;nh vận h&agrave;nh tốt mọi t&aacute;c vụ cần thiết rồi.</p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop RAM 8 GB hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" rel=\"noopener\">Laptop RAM 8 GB</a> cũng như ổ cứng <strong>SSD 512 GB</strong> đ&atilde; kh&aacute; quen thuộc trong ph&acirc;n kh&uacute;c n&agrave;y, m&igrave;nh c&oacute; thể l&agrave;m việc đa nhiệm tr&ecirc;n nhiều cửa sổ kh&aacute;c nhau, vừa soạn thảo văn bản vừa nghe nhạc giải tr&iacute; vừa lướt Facebook vẫn rất trơn tru. Tốc độ đọc ghi cũng được gia tăng đ&aacute;ng kể c&ugrave;ng kh&ocirc;ng gian lưu trữ đủ để m&igrave;nh sử dụng cho c&aacute;c c&ocirc;ng việc học tập, l&agrave;m việc hằng ng&agrave;y.</p>\r\n<h3><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-5.jpg\"><img class=\"lazyload\" title=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Bộ nhớ\" alt=\"Asus Vivobook 15 X1502ZA i5 1240P (EJ120W) - Bộ nhớ\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/281483/asus-vivobook-15-x1502za-i5-ej120w-xy-5.jpg\" /></a></h3>\r\n<h3>Ngoại h&igrave;nh tinh tế, đẹp mắt&nbsp;</h3>\r\n<p>Chiếc <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Asus hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" rel=\"noopener\">laptop Asus</a> n&agrave;y đưa m&igrave;nh đi từ bất ngờ n&agrave;y đến bất ngờ kh&aacute;c v&igrave; ngay cả khi n&oacute;i về thiết kế của em n&oacute; th&igrave; m&igrave;nh vẫn c&ograve;n thắc mắc. Theo th&ocirc;ng tin h&atilde;ng đưa ra, m&agrave;u sắc của m&aacute;y l&agrave; <strong>bạc băng gi&aacute;</strong> nhưng khi khảo s&aacute;t qua nhiều người trong đ&oacute; c&oacute; cả m&igrave;nh, n&oacute; lại sở hữu lớp &aacute;o <strong>xanh pastel</strong> nhẹ nh&agrave;ng cực h&uacute;t mắt, d&ugrave; cả 2 gam m&agrave;u đều rất đẹp nhưng nếu bạn c&oacute; &yacute; định sắm em n&agrave;y th&igrave; ra ngay cửa h&agrave;ng Thế Giới Di Động để nhận định thử nha.&nbsp;</p>\r\n<p>Trọng lượng <strong>1.7 kg</strong> đối với những bạn nữ c&oacute; thể sẽ kh&aacute; nặng để cầm m&aacute;y bằng một tay nhưng vẫn dễ d&agrave;ng bỏ v&agrave;o bất kỳ chiếc balo 15.6 inch n&agrave;o v&agrave; mang ch&uacute;ng đi khắp mọi nơi. Tuy được ho&agrave;n thiện từ nhựa nhưng m&aacute;y vẫn c&oacute; độ cứng c&aacute;p nhất định chứ kh&ocirc;ng ọp ẹp hay lỏng lẻo.</p>\r\n<p>M&igrave;nh đ&atilde; rất bất ngờ khi Asus c&ograve;n trang bị th&ecirc;m cho em m&aacute;y n&agrave;y những t&iacute;nh năng cực kỳ hấp dẫn như <strong>bản lề mở 180 độ</strong>, <strong>bảo mật v&acirc;n tay</strong>, <strong>c&ocirc;ng tắc kh&oacute;a camera</strong> v&agrave; <strong>độ bền chuẩn qu&acirc;n đội</strong> <strong>MIL STD 810H</strong>, thật sự kh&ocirc;ng n&ecirc;n bỏ lỡ trong tầm gi&aacute; lu&ocirc;n. Cổng kết nối cũng tương đối đầy đủ với USB 2.0, 2 cổng USB 3.2, HDMI, Jack tai nghe 3.5 mm v&agrave; USB Type-C.</p>\r\n<p>Tưởng chừng như mọi thứ đều ho&agrave;n hảo nhưng m&igrave;nh đ&atilde; hơi hụt hẫng nhẹ khi con <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Asus VivoBook hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-asus-vivobook\" target=\"_blank\" rel=\"noopener\">laptop Asus VivoBook</a> n&agrave;y kh&ocirc;ng được trang bị đ&egrave;n nền b&agrave;n ph&iacute;m, g&acirc;y kh&ocirc;ng &iacute;t kh&oacute; khăn mỗi khi c&oacute; nhu cầu sử dụng trong m&ocirc;i trường thiếu s&aacute;ng. B&ugrave; lại cảm gi&aacute;c g&otilde; ph&iacute;m kh&aacute; tốt c&ugrave;ng bố cục được sắp xếp hợp l&yacute; n&ecirc;n m&igrave;nh sẽ nhanh th&iacute;ch nghi được với vấn đề nhỏ n&agrave;y.</p>\r\n<h3>Hiển thị h&igrave;nh ảnh r&otilde; n&eacute;t, sống động</h3>\r\n<p>Phần m&agrave;n h&igrave;nh của m&aacute;y theo m&igrave;nh l&agrave; ph&ugrave; hợp trong tầm gi&aacute; với k&iacute;ch thước <strong>15.6 inch</strong> c&ugrave;ng <strong>tấm nền IPS</strong> cho g&oacute;c nh&igrave;n rộng r&atilde;i, độ ph&acirc;n giải <strong>Full HD (1920 x 1080)</strong> v&agrave; tần số qu&eacute;t <strong>60 Hz</strong> hiển thị tốt những nội dung tr&ecirc;n m&agrave;n h&igrave;nh từ học tập, l&agrave;m việc cho đến xem phim giải tr&iacute;.&nbsp;</p>\r\n<p>C&aacute;c th&ocirc;ng số được đo đạt bằng <strong>Spyder 5 Pro</strong> lần lượt l&agrave; <strong>66% sRGB</strong>, <strong>50% Adobe RGB</strong> v&agrave; <strong>49% DCI-P3</strong> gi&uacute;p những trải nghiệm của m&igrave;nh khi xem phim, coi video tr&ecirc;n YouTube tương đối tốt, tuy vẫn c&ograve;n hơi &aacute;m v&agrave;ng một ch&uacute;t nhưng việc việc n&agrave;y c&oacute; thể khắc phục được nhờ phần mềm <strong>MyASUS</strong> c&oacute; sẵn tr&ecirc;n m&aacute;y, cho ph&eacute;p bạn t&ugrave;y &yacute; chỉnh phần m&agrave;u sắc của m&agrave;n h&igrave;nh.</p>\r\n<p>Khả năng mở m&aacute;y <strong>180 độ</strong> gi&uacute;p tăng hiệu quả l&agrave;m việc nh&oacute;m của m&igrave;nh l&ecirc;n rất nhiều, những l&uacute;c ngồi qu&aacute;n nước v&agrave; muốn trao đổi b&agrave;i tập th&igrave; chỉ cần gập m&agrave;n h&igrave;nh m&aacute;y xuống, người ngồi đối diện c&oacute; thể dễ d&agrave;ng nh&igrave;n thấy m&agrave; kh&ocirc;ng cần phải dịch chuyển nguy&ecirc;n m&aacute;y qua, tiện lợi hơn rất nhiều so với c&aacute;c sản phẩm laptop kh&aacute;c.&nbsp;&nbsp;</p>\r\n<p>M&igrave;nh đ&atilde; tham gia lớp học trực tuyến tr&ecirc;n Zoom cũng như l&agrave;m c&aacute;c c&ocirc;ng việc cơ bản tr&ecirc;n Google Sheets, Word,... được <strong>hơn 4 giờ</strong> d&ugrave; mở độ s&aacute;ng hết cỡ v&agrave; lu&ocirc;n bật Wifi nhờ vi&ecirc;n pin <strong>42 Wh</strong>.</p>\r\n<p>Đối với m&igrave;nh, laptop Asus Vivobook 15 X1502ZA i5 (EJ120W) hứa hẹn sẽ l&agrave; sản phẩm đ&aacute;ng mua trong tầm gi&aacute; với sức mạnh đến từ con chip mới nhất của nh&agrave; Intel c&ugrave;ng thiết kế đẹp mắt v&agrave; nhiều t&iacute;nh năng, tiện &iacute;ch ấn tượng.&nbsp;&nbsp;</p>', 'Với mức giá chưa đầy 18 triệu mà sở hữu cấu hình mạnh ngang với các dòng máy mỏng nhẹ, cao cấp? Không cần nghi ngờ hay tìm kiếm đâu xa, mình sẽ chứng minh ngay trên chiếc laptop Asus Vivobook 15 X1502ZA i5 (EJ120W) này với con chip Gen 12 mới nhất đến từ nhà Intel.', 'CPU: i51240P1.7GHz\r\nRAM: 8 GBDDR4 2 khe (1 khe 8 GB onboard + 1 khe trống)3200 MHz\r\nỔ cứng: 512 GB SSD NVMe PCIe\r\nMàn hình: 15.6\"Full HD (1920 x 1080)\r\nCard màn hình: Card tích hợpIntel UHD\r\nCổng kết nối: 1 x USB 2.02 x USB 3.2HDMIJack tai nghe 3.5 mmUSB Type-C\r\nHệ điều hành: Windows 11 Home SL\r\nThiết kế: Vỏ nhựa', 100, 17490000, 17990000, '2022-08-05 06:29:59', '2022-08-05 06:29:59', NULL, 'laptop-asus-vivobook-15-x1502za', 0, 0, 'Sản phẩm'),
(28, 9, 17, 'Laptop Dell Gaming Alienware m15 R6 i7', 'https://cdn.tgdd.vn/Products/Images/44/271090/dell-gaming-alienware-m15-r6-i7-11800h-32gb-1tb-ssd-8gb-600x600.jpg', '<h3>Với phong c&aacute;ch thiết kế độc đ&aacute;o c&ugrave;ng cấu h&igrave;nh vượt trội, <a title=\"aptop Dell Gaming Alienware m15 R6 i7 (70272633) đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop/dell-gaming-alienware-m15-r6-i7-70272633\" target=\"_blank\" rel=\"noopener\">laptop Dell Gaming Alienware m15 R6 i7 11800H (70272633)</a> sẵn s&agrave;ng&nbsp;đ&aacute;p ứng ho&agrave;n hảo mọi t&aacute;c vụ của một chiếc <a title=\"Xem th&ecirc;m laptop đồ hoạ - kỹ thuật đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=do-hoa-ky-thuat\" target=\"_blank\" rel=\"noopener\">laptop đồ họa - kỹ thuật</a>&nbsp;như thiết kế hay chiến game.</h3>\r\n<p>&bull; <a title=\"Xem th&ecirc;m laptop Dell Gaming Alienware đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-dell-gaming-alienware\" target=\"_blank\" rel=\"noopener\">Laptop Dell Gaming Alienware</a>&nbsp;sở hữu CPU&nbsp;<strong>Intel Core i7 Tiger Lake 11800H</strong> mang lại hiệu năng xử l&yacute; vượt trội. C&ugrave;ng bạn thoả sức s&aacute;ng tạo h&igrave;nh ảnh, video với card t&iacute;ch hợp <strong>NVIDIA GeForce RTX 3070 8 GB </strong>hay<strong>&nbsp;</strong>giải tr&iacute; với&nbsp;c&aacute;c tựa game hấp dẫn như CS:GO, GTAV,...</p>\r\n<p>&bull;&nbsp;Bộ nhớ <strong>RAM 32 GB</strong> cho khả năng đa nhiệm cực mượt m&agrave;, ổ cứng <strong>SSD 1 TB</strong>&nbsp;cho bạn thoải m&aacute;i lưu trữ dữ liệu với khả năng truy xuất, khởi động m&aacute;y cực nhanh.</p>\r\n<p>&bull; <a title=\"Xem th&ecirc;m laptop Dell đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\" rel=\"noopener\">Laptop Dell</a>&nbsp;sở hữu ngoại h&igrave;nh độc đ&aacute;o mang lại độ nhận diện cao với chiếc logo nổi bật tr&ecirc;n lớp&nbsp;<strong>vỏ nhựa</strong>&nbsp;bền chắc, trọng lượng&nbsp;<strong>2.69 kg</strong>&nbsp;lu&ocirc;n trong tư thế sẵn s&agrave;ng chiến đấu bất cứ khi n&agrave;o bạn cần.</p>\r\n<p>&bull;&nbsp;M&agrave;n h&igrave;nh <strong>15.6 inch</strong> c&oacute; độ s&aacute;ng l&ecirc;n đến <strong>400 nits</strong> v&agrave; <strong>99% DCI-P3</strong> cho h&igrave;nh ảnh hiển thị sống động, rực rỡ với độ tương phản cao.</p>\r\n<p>&bull; C&ocirc;ng nghệ <strong>WVA</strong> mang đến cho m&agrave;n h&igrave;nh <a title=\"Xem th&ecirc;m laptop đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">laptop</a> g&oacute;c nh&igrave;n rộng l&ecirc;n đến <strong>178 độ</strong>, tần số qu&eacute;t <strong>240 Hz</strong> cho độ trễ cực thấp, mang lại trải nghiệm h&igrave;nh ảnh si&ecirc;u mượt m&agrave;, kh&ocirc;ng bị giật, x&eacute; khi chiến những trận game đỉnh cao.</p>\r\n<p>&bull;&nbsp;<strong>Đ&egrave;n b&agrave;n ph&iacute;m chuyển m&agrave;u RGB</strong> độc đ&aacute;o hỗ trợ bạn g&otilde; tốt, chiến game trong mọi điều kiện s&aacute;ng.</p>\r\n<p>&bull; Trang bị c&aacute;c cổng kết nối tiện lợi như USB 3.2, HDMI, LAN (RJ45), USB Type-C.</p>\r\n<p>&bull; C&ocirc;ng nghệ <strong>High Definition (HD)</strong> với t&iacute;nh năng khuếch đại &acirc;m thanh c&ugrave;ng <strong>Audio Nahimic Audio</strong>&nbsp;giả lập hệ thống &acirc;m v&ograve;m 3D sống động, lọc v&agrave; khử tiếng ồn hiệu quả.</p>', 'Với phong cách thiết kế độc đáo cùng cấu hình vượt trội, laptop Dell Gaming Alienware m15 R6 i7 11800H (70272633) sẵn sàng đáp ứng hoàn hảo mọi tác vụ của một chiếc laptop đồ họa - kỹ thuật như thiết kế hay chiến game.', 'CPU: i711800H2.30 GHz\r\nRAM: 32 GBDDR4 2 khe (1 khe 16GB + 1 khe 16GB)3200 MHz\r\nỔ cứng: 1 TB SSD M.2 PCIe (Có thể tháo ra, lắp thanh khác tối đa 2TB)Hỗ trợ thêm 1 khe cắm SSD M.2 PCIe mở rộng (nâng cấp tối đa 2TB)\r\nMàn hình: 15.6\"QHD (2560 x 1440)240Hz\r\nCard màn hình: Card rờiRTX 3070 8GB\r\nCổng kết nối: 3 x USB 3.2HDMIJack tai nghe 3.5 mmLAN (RJ45)USB Type-C\r\nĐặc biệt: Có đèn bàn phím\r\nHệ điều hành: Windows 11 Home SL + Office Home & Student vĩnh viễn\r\nThiết kế: Vỏ nhựa', 100, 61640000, 66490000, '2022-08-05 06:33:57', '2022-08-05 06:33:57', NULL, 'laptop-dell-gaming-alienware-m15-r6-i7', 0, 0, 'Sản phẩm,Dell,Gaming,Alienware'),
(29, 9, 17, 'Laptop Dell Gaming G15 5515', 'https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-291121-114930-600x600.jpg', '<h3>Thiết kế đậm chất ri&ecirc;ng, đầy ấn tượng</h3>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Dell Gaming hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-dell-gaming-g-series\" target=\"_blank\" rel=\"noopener\">Laptop Dell Gaming</a>&nbsp;ho&agrave;n thiện từ chất liệu nhựa cao cấp,<strong>&nbsp;</strong>bền bỉ, phủ gam m&agrave;u&nbsp;<strong>x&aacute;m</strong>&nbsp;thời thượng, thu h&uacute;t bạn từ c&aacute;i nh&igrave;n đầu ti&ecirc;n, th&iacute;ch hợp với đa dạng đối tượng người d&ugrave;ng bởi phong c&aacute;ch gaming hiện đại, kh&ocirc;ng qu&aacute; hầm hố.</p>\r\n<p>Trọng lượng&nbsp;<strong>2.8 kg</strong>&nbsp;v&agrave; độ d&agrave;y&nbsp;<strong>26.9 mm</strong>&nbsp;tạo cảm gi&aacute;c cứng c&aacute;p hơn cho m&aacute;y, nhưng vẫn vừa vặn nằm gọn trong balo, tiện lợi c&ugrave;ng bạn di chuyển mọi l&uacute;c mọi nơi để học tập, l&agrave;m việc cũng như chơi game giải tr&iacute; ho&agrave;n hảo.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-2-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-2-1.jpg\" alt=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-2-1.jpg\" /></a></p>\r\n<p>B&agrave;n ph&iacute;m c&oacute; khoảng c&aacute;ch rộng r&atilde;i, hợp l&yacute; với h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u, n&uacute;t bấm &ecirc;m, độ nảy tốt tạo cảm gi&aacute;c nhạy b&eacute;n, chuẩn x&aacute;c, thoải m&aacute;i khi nhập liệu, soạn thảo. Đặc biệt, trang bị <strong>đ&egrave;n nền</strong> đơn sắc&nbsp;nổi bật t&iacute;ch hợp dưới b&agrave;n ph&iacute;m tạo th&ecirc;m điểm nhấn cho chiếc&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">laptop</a>&nbsp;n&agrave;y vừa gi&uacute;p người d&ugrave;ng thuận tiện l&agrave;m việc hay chiến game ở những nơi thiếu s&aacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-7.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - B&agrave;n ph&iacute;m\" src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-7.jpg\" alt=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - B&agrave;n ph&iacute;m\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-7.jpg\" /></a></p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Dell hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-dell\" target=\"_blank\" rel=\"noopener\">Laptop Dell</a>&nbsp;trang bị đa dạng c&aacute;c cổng kết nối th&ocirc;ng dụng bao gồm: 2 x USB 2.0, HDMI, Jack tai nghe 3.5 mm, LAN (RJ45), USB 3.2, USB Type-C gi&uacute;p li&ecirc;n kết dễ d&agrave;ng v&agrave; truyền tải dữ liệu nhanh ch&oacute;ng đến c&aacute;c thiết bị ngoại vi tương th&iacute;ch như m&agrave;n h&igrave;nh rời, m&aacute;y chiếu, loa, chuột, m&aacute;y in,...&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-12.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - Cổng kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-12.jpg\" alt=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-12.jpg\" /></a></p>\r\n<p>Hai chuẩn kết nối kh&ocirc;ng d&acirc;y<strong>&nbsp;Bluetooth 5.2</strong>,&nbsp;<strong>Wi-Fi 6 (802.11ax)</strong>&nbsp;cho tốc độ truyền tệp nhanh, giảm độ trễ giảm, đảm bảo cho đường truyền internet của bạn lu&ocirc;n trong trạng th&aacute;i ổn định, hạn chế xảy ra hiện tượng ngắt qu&atilde;ng giữa chừng.</p>\r\n<h3>Chơi game cực mượt với hiệu năng vượt trội</h3>\r\n<p><strong>Dell Gaming G15</strong> mang hiệu suất cao nhờ sở hữu bộ vi xử l&yacute;&nbsp;<strong>AMD</strong>&nbsp;<strong>Ryzen 5</strong>&nbsp;<strong>5600H</strong><strong>&nbsp;</strong>c&oacute; cấu tr&uacute;c<strong>&nbsp;6 nh&acirc;n 12 luồng</strong>&nbsp;cho tốc độ xung nhịp cơ bản<strong>&nbsp;3.30 GHz </strong>v&agrave; đạt&nbsp;tối đa l&ecirc;n đến<strong>&nbsp;Turbo Boost 4.2 GHz</strong>, gi&uacute;p giải quyết trơn tru c&aacute;c c&ocirc;ng việc văn ph&ograve;ng cơ bản như Word, Excel, Powerpoint,... đến thiết kế đồ họa, kỹ thuật chuy&ecirc;n dụng tr&ecirc;n c&ocirc;ng cụ Photoshop, Illustrator, Figma, AutoCAD,...&nbsp;</p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop c&oacute; RAM 16 GB hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-16-gb\" target=\"_blank\" rel=\"noopener\">Laptop RAM 16 GB</a>&nbsp;chuẩn&nbsp;<strong>DDR4 2 khe </strong>(1 khe <strong>8 GB</strong> + 1 khe <strong>8 GB</strong>)&nbsp;tốc độ bus RAM<strong> 3200 MHz</strong>, hỗ trợ n&acirc;ng cấp tối đa <strong>32 GB</strong>, đa nhiệm si&ecirc;u mượt m&agrave;, bạn ho&agrave;n to&agrave;n c&oacute; thể mở h&agrave;ng chục cửa sổ vừa l&agrave;m việc, vừa nghe nhạc thư gi&atilde;n m&agrave; kh&ocirc;ng lo giật lag, đứng m&aacute;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-04.jpg\"><img class=\" lazyloaded\" title=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - Cấu h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-04.jpg\" alt=\"Dell Gaming G15 5515 R5 5600H (P105F004DGR) - Cấu h&igrave;nh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/260171/dell-gaming-g15-5515-r5-p105f004dgr-04.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, <a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop AMD Ryzen 5 hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-ryzen-5\" target=\"_blank\" rel=\"noopener\">laptop AMD Ryzen 5</a> c&ograve;n trang bị card đồ họa rời&nbsp;<strong>NVIDIA GeForce RTX3050 4 GB&nbsp;</strong>cải thiện hiệu suất đồ họa tốt hơn, gi&uacute;p bạn thực hiện chỉnh sửa h&igrave;nh ảnh với kết xuất 2D, 3D, render video một c&aacute;ch chuy&ecirc;n nghiệp hay chiến c&aacute;c tựa game thịnh h&agrave;nh ở mức c&agrave;i đặt cao như Fifa Online 4, LOL, Cyberpunk 2077, GTA V,...&nbsp;</p>\r\n<p><a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop c&oacute; ổ cứng SSD 512 GB hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" rel=\"noopener\">Laptop ổ cứng SSD 512 GB</a>&nbsp;<strong>NVMe PCIe</strong>&nbsp;c&oacute; thể th&aacute;o ra, lắp thanh kh&aacute;c tối đa <strong>2 TB (2280) / 1 TB (2230)</strong>&nbsp;tăng tốc độ khởi động m&aacute;y, truy xuất dữ liệu nhanh ch&oacute;ng, r&uacute;t ngắn thời gian phản hồi chỉ trong v&agrave;i gi&acirc;y, đồng thời c&ograve;n cung cấp cho bạn một kh&ocirc;ng gian lưu trữ đủ&nbsp;để bạn lưu lại c&aacute;c phần mềm y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng sợ qu&aacute; nặng m&aacute;y. B&ecirc;n cạnh đ&oacute; c&ograve;n hỗ trợ&nbsp;th&ecirc;m 1 khe cắm <strong>SSD M.2 PCIe</strong> mở rộng dung lượng khi cần thiết.</p>\r\n<h3>Chi tiết, sinh động tr&ecirc;n từng khung h&igrave;nh giải tr&iacute;</h3>\r\n<p>M&agrave;n h&igrave;nh rộng <strong>15.6 inch</strong>&nbsp;c&oacute; độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080) </strong>v&agrave; độ s&aacute;ng<strong> 250 nits&nbsp;</strong>sắc n&eacute;t ngay cả khi ở nơi c&oacute; cường độ &aacute;nh s&aacute;ng cao, c&ugrave;ng c&ocirc;ng nghệ <strong>LED Backlit</strong><strong>&nbsp;</strong>mang đến trải nghiệm xem chơi game, xem phim ch&acirc;n thật, sinh động, đ&atilde; mắt cho bạn h&ograve;a m&igrave;nh v&agrave;o thế giới ảo một c&aacute;ch tuyệt vời nhất.</p>\r\n<p>Tần số qu&eacute;t&nbsp;m&agrave;n h&igrave;nh <strong>120 Hz</strong>&nbsp;m&atilde;n nh&atilde;n với c&aacute;c tựa game h&agrave;nh động hấp dẫn, mọi hoạt cảnh game chuyển động mượt m&agrave;, hạn chế bị x&eacute; hay nh&ograve;e h&igrave;nh, kết hợp với m&agrave;n h&igrave;nh chống ch&oacute;i&nbsp;<strong>Anti Glare</strong>&nbsp;v&agrave;&nbsp;c&ocirc;ng nghệ <strong>WVA</strong>&nbsp;cung cấp tầm nh&igrave;n l&ecirc;n đến&nbsp;<strong>178 độ,&nbsp;</strong>đồng thời đảm bảo an to&agrave;n cho mắt trong c&aacute;c m&ocirc;i trường &aacute;nh s&aacute;ng cao.</p>\r\n<p>C&ocirc;ng nghệ <strong>Nahimic Audio</strong> tạo hiệu ứng &acirc;m thanh v&ograve;m<strong>,&nbsp;</strong>t&aacute;i tạo &acirc;m &acirc;m sắc tốt, cho chất &acirc;m chi tiết, trung thực với &acirc;m bass to r&otilde; v&agrave; độ lọc khử tiếng ồn tốt, bạn c&oacute; thể dễ d&agrave;ng nghe được c&aacute;c chuyển động nhỏ trong game.</p>\r\n<p>Dell Gaming G15 5515 R5 (P105F004DGR) hứa hẹn&nbsp;l&agrave; một trong những sự lựa chọn tuyệt vời trong ph&acirc;n kh&uacute;c&nbsp;<a title=\"Xem th&ecirc;m c&aacute;c mẫu Laptop Gaming hiện đang kinh doanh tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" rel=\"noopener\">laptop gaming</a>&nbsp;bởi thiết kế độc đ&aacute;o, c&aacute; t&iacute;nh c&ugrave;ng những th&ocirc;ng số kỹ thuật ấn tượng.</p>', 'Bộ hiệu năng gây ấn tượng đến từ con chip AMD mạnh mẽ cùng thiết kế cá tính, nổi bật, laptop Dell Gaming G15 5515 R5 (P105F004DGR) sẽ đáp ứng tối ưu mọi nhu cầu từ các tác vụ văn phòng cơ bản đến những ứng dụng đồ họa, chơi game giải trí chuyên nghiệp.', 'CPU: Ryzen 55600H3.3GHz\r\nRAM: 16 GBDDR4 2 khe (1 khe 8 GB + 1 khe 8 GB)3200 MHz\r\nỔ cứng: 512 GB SSD NVMe PCIe (Có thể tháo ra, lắp thanh khác tối đa 2TB (2280) / 1TB (2230))Hỗ trợ thêm 1 khe cắm SSD M.2 PCIe mở rộngKhông hỗ trợ khe cắm HDD\r\nMàn hình: 15.6\"Full HD (1920 x 1080)120Hz\r\nCard màn hình: Card rờiRTX 3050 4GB\r\nCổng kết nối: 1 x USB 3.22 x USB 2.0HDMIJack tai nghe 3.5 mmLAN', 100, 28990000, 31990000, '2022-08-05 13:41:56', '2022-08-05 13:41:56', NULL, 'laptop-dell-gaming-g15-5515', 0, 0, 'Sản phẩm,Dell,Gaming,G15');
INSERT INTO `products` (`id`, `user_id`, `cat_id`, `name`, `thumbnail`, `content`, `summary`, `description`, `qty`, `price`, `old_price`, `created_at`, `updated_at`, `deleted_at`, `slug`, `num_views`, `num_purchases`, `tags`) VALUES
(30, 16, 15, 'Laptop Apple MacBook Pro M2 2022', 'https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-xam-600x600.jpg', '<h3>Hiệu năng vượt trội của d&ograve;ng chip thế hệ mới</h3>\r\n<p><a title=\"Xem th&ecirc;m laptop Apple Macbook đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" rel=\"noopener\">Laptop Apple Macbook</a>&nbsp;sở hữu <strong>CPU Apple M2</strong> với <strong>8 nh&acirc;n CPU</strong> v&agrave; <strong>10 nh&acirc;n GPU </strong>mang lại một sức mạnh đột ph&aacute;, hỗ trợ người d&ugrave;ng chạy mượt m&agrave; c&aacute;c t&aacute;c vụ của một chiếc&nbsp;<a title=\"Xem th&ecirc;m laptop đồ hoạ - kỹ thuật đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=do-hoa-ky-thuat\" target=\"_blank\" rel=\"noopener\">laptop đồ họa - kỹ thuật</a>&nbsp;như thiết kế, chỉnh ảnh hay render video tr&ecirc;n c&aacute;c phần mềm của&nbsp;Adobe như: Photoshop, Premiere,...&nbsp;</p>\r\n<p><strong>RAM 8 GB</strong> đem đến cho người d&ugrave;ng khả năng đa nhiệm mượt m&agrave; c&aacute;c t&aacute;c vụ như lướt web, đọc b&aacute;o, chỉnh sửa ảnh Photoshop c&ugrave;ng l&uacute;c m&agrave; vẫn hạn chế xảy ra t&igrave;nh trạng đứng m&aacute;y hay phản hồi chậm.</p>\r\n<p><strong>Ổ cứng SSD 256 GB</strong>&nbsp;mang đến kh&ocirc;ng gian lưu trữ đủ d&ugrave;ng cho c&aacute;c t&aacute;c vụ văn ph&ograve;ng, thiết kế,... đồng thời tối ưu khả năng khởi động m&aacute;y hay truy cập ứng dụng nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-03-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro M2 2022 - Cấu h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-03-1.jpg\" alt=\"MacBook Pro M2 2022 - Cấu h&igrave;nh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-03-1.jpg\" /></a></p>\r\n<h3>Thao t&aacute;c nhanh hơn với bộ đ&ocirc;i chip M2 v&agrave; MacOS</h3>\r\n<p>Chiếc MacBook M2 được c&aacute;c t&iacute;n đồ c&ocirc;ng nghệ đ&aacute;nh gi&aacute; cao khi sản xuất tr&ecirc;n tiến tr&igrave;nh <strong>5 nm</strong> với <strong>20 tỷ b&oacute;ng b&aacute;n dẫn</strong>, nhiều hơn <strong>25%</strong> so với M1 điều n&agrave;y gi&uacute;p chiếc m&aacute;y vận h&agrave;nh trơn tru nhưng tiết kiệm điện năng hơn. Bộ điều khiển cung cấp băng th&ocirc;ng bộ nhớ hợp nhất <strong>100 GB/gi&acirc;y</strong>, nhiều hơn <strong>50%</strong> so với thế hệ tiền nhiệm gi&uacute;p m&aacute;y truyền ghi dữ liệu nhanh ch&oacute;ng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-ab-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro M2 2022 - Chip Apple M2\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-ab-1.jpg\" alt=\"MacBook Pro M2 2022 - Chip Apple M2\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-ab-1.jpg\" /></a></p>\r\n<p>Hệ điều h&agrave;nh <strong>macOS</strong> ph&aacute;t triển v&agrave; ph&acirc;n phối độc quyền bởi Apple được thiết kế với giao diện đẹp mắt, dễ d&ugrave;ng c&ugrave;ng kho ứng dụng đồ sộ mang đến sự tiện lợi tối đa cho mọi c&ocirc;ng việc, học tập của bạn, đồng thời gi&uacute;p cho khả năng hoạt động mượt m&agrave; hơn đ&aacute;ng kể.</p>\r\n<h3>M&agrave;n h&igrave;nh Retina kết hợp c&ocirc;ng nghệ True Tone mang lại h&igrave;nh ảnh sống động, l&ocirc;i cuốn</h3>\r\n<p><a title=\"Xem th&ecirc;m laptop đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">Laptop</a>&nbsp;sở hữu m&agrave;n h&igrave;nh <strong>13.3 inch</strong> c&ugrave;ng độ ph&acirc;n giải <strong>Retina (2560 x 1600)</strong> sắc n&eacute;t, m&agrave;u sắc trung thực. Độ tương phản cao nhờ độ s&aacute;ng <strong>500 nits</strong>, c&ocirc;ng nghệ m&agrave;n h&igrave;nh <strong>IPS</strong> đem lại khả năng hiển thị r&otilde; r&agrave;ng d&ugrave; thay đổi vị tr&iacute; nh&igrave;n ở bất k&igrave; đ&acirc;u.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-05-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro M2 2022 - M&agrave;n h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-05-1.jpg\" alt=\"MacBook Pro M2 2022 - M&agrave;n h&igrave;nh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-05-1.jpg\" /></a></p>\r\n<p>C&ocirc;ng nghệ <strong>True tone</strong>, <strong>Wide color (P3)</strong> như một lời khẳng định của Apple mang đến m&agrave;u sắc hiển thị ch&iacute;nh x&aacute;c tối ưu khả năng l&agrave;m việc cho người d&ugrave;ng thiết kế s&aacute;ng tạo. C&aacute;c c&ocirc;ng nghệ đ&oacute; được kết hợp với nhau tạo n&ecirc;n một kh&ocirc;ng gian giải tr&iacute; để xem phim Youtube, Netflix,... sống động hơn bao giờ hết.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-190622-101055.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro M2 8GB/256GB - Độ t&aacute;i tạo m&agrave;u\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-190622-101055.jpg\" alt=\"MacBook Pro M2 8GB/256GB - Độ t&aacute;i tạo m&agrave;u\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-190622-101055.jpg\" /></a></p>\r\n<h3>Thiết kế quyến rũ, biểu tượng cho sự ho&agrave;n mỹ thu h&uacute;t mọi &aacute;nh nh&igrave;n</h3>\r\n<p><a title=\"Xem th&ecirc;m laptop Apple  Macbook Pro đang b&aacute;n tại thegioididong.com 1\" href=\"https://www.thegioididong.com/laptop-apple-macbook-pro\" target=\"_blank\" rel=\"noopener\">Macbook Pro</a> được chế t&aacute;c từ lớp vỏ <strong>kim loại</strong> nguy&ecirc;n khối to&aacute;t l&ecirc;n vẻ đẹp của chiếc <a title=\"Xem th&ecirc;m laptop cao cấp - sang trọng đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" rel=\"noopener\">laptop cao cấp - sang trọng</a>. Trọng lượng m&aacute;y chỉ <strong>1.4 kg</strong> v&agrave; mỏng <strong>15.6 mm </strong>đem đến sự cơ động cho bạn dễ d&agrave;ng bỏ v&agrave;o balo để mang theo b&ecirc;n m&igrave;nh.</p>\r\n<p>Ấn tượng trong thiết kế của Apple lu&ocirc;n l&agrave; logo t&aacute;o khuyết được đặt ch&iacute;nh giữa mặt lưng, tạo n&ecirc;n sự nhận diện thương hiệu m&agrave; ai ai cũng phải ngước nh&igrave;n mỗi khi bạn cầm tr&ecirc;n tay hay l&agrave;m việc ngo&agrave;i qu&aacute;n c&agrave; ph&ecirc;.</p>\r\n<p><strong>Magic Keyboard</strong> được thiết kế tinh xảo, đẹp mắt, độ nảy ổn định lu&ocirc;n mang đến sự y&ecirc;n tĩnh v&agrave; thoải m&aacute;i cho người d&ugrave;ng mỗi khi soạn thảo văn bản. <strong>Đ&egrave;n nền b&agrave;n ph&iacute;m</strong> được t&iacute;ch hợp tr&ecirc;n m&aacute;y gi&uacute;p bạn thao t&aacute;c dễ d&agrave;ng trong m&ocirc;i trường thiếu s&aacute;ng, n&acirc;ng cao trải nghiệm cho người d&ugrave;ng.</p>\r\n<p><strong>Touch Bar (thanh cảm ứng)</strong> vẫn được t&iacute;ch hợp tr&ecirc;n m&aacute;y như thế hệ Macbook Pro trước tạo n&ecirc;n sự thuận tiện cho bạn khi cần sử dụng để điều chỉnh với nhiều ứng dụng kh&aacute;c nhau như: k&eacute;o tua video tr&ecirc;n thanh cảm ứng, chụp m&agrave;n h&igrave;nh, điều chỉnh &acirc;m lượng,...</p>\r\n<h3>Macbook Pro M2 một studio thu nhỏ trong tầm tay</h3>\r\n<p>Kh&ocirc;ng gian giải tr&iacute; của c&aacute;c sản phẩm nh&agrave; Apple chưa bao giờ l&agrave;m người d&ugrave;ng thất vọng khi sở hữu <strong>loa k&eacute;p (2 k&ecirc;nh)</strong> c&ugrave;ng c&ocirc;ng nghệ <strong>Wide Stereo</strong> v&agrave; <strong>Dolby Atmos</strong>, mang đến chất lượng &acirc;m thanh tr&ecirc;n cả tuyệt vời, đắm ch&igrave;m người d&ugrave;ng v&agrave;o những bản nhạc du dương lan tỏa khắp căn ph&ograve;ng.&nbsp;</p>\r\n<p>M&aacute;y được t&iacute;ch hợp <strong>720p FaceTime Camera</strong> cho khả năng hiển thị sắc n&eacute;t gi&uacute;p bạn lu&ocirc;n tươi tắn trong mọi buổi họp hay lớp học trực tuyến. <strong>3 microphones</strong> cho ph&eacute;p người d&ugrave;ng thu tiếng tốt hơn, kh&ocirc;ng bị r&egrave;, khử tiếng ồn tốt v&agrave; mang lại sự ổn định khi giao tiếp, hỗ trợ&nbsp;cho qu&aacute; tr&igrave;nh học tập trực tuyến hay gọi điện video tiện lợi hơn.</p>\r\n<h3>Đứng đầu về khả năng bảo mật v&agrave; quyền ri&ecirc;ng tư</h3>\r\n<p>Tự h&agrave;o l&agrave; doanh nghiệp đứng đầu trong khả năng bảo mật v&agrave; ri&ecirc;ng tư với t&iacute;nh năng <strong>bảo mật v&acirc;n tay</strong> t&iacute;ch hợp ph&iacute;m nguồn ngang với thanh cảm ứng Touch Bar tr&ecirc;n m&aacute;y, gi&uacute;p người d&ugrave;ng khởi động m&aacute;y nhanh ch&oacute;ng hơn kiểu nhập mật khẩu truyền thống đồng thời đảm bảo tối đa khả năng bảo mật dữ liệu c&aacute; nh&acirc;n của người d&ugrave;ng.</p>\r\n<h3>C&ocirc;ng nghệ t&acirc;n tiến, tiện &iacute;ch cao</h3>\r\n<p>Laptop trang bị 2 cổng Thunderbolt 3&nbsp;được t&iacute;ch hợp sạc pin, truyền dữ liệu nhanh ch&oacute;ng với băng th&ocirc;ng <strong>40 Gbps</strong>&nbsp;v&agrave; Jack tai nghe 3.5 mm, ph&ugrave; hợp với nhiều loại tai nghe phổ biến tr&ecirc;n thị trường.</p>\r\n<p>Đảm bảo cho tốc độ truyền tải mượt m&agrave; hơn tr&ecirc;n mọi t&aacute;c vụ m&agrave; kh&ocirc;ng lo gi&aacute;n đoạn giữa chừng khi bạn l&agrave;m việc nhờ <strong>Wi-Fi 6 (802.11ax)</strong> c&oacute; băng th&ocirc;ng tăng <strong>40%</strong> so với thế hệ tiền nhiệm.</p>\r\n<h3>Macbook Pro 2022 M2 gi&aacute; bao nhi&ecirc;u?</h3>\r\n<p>Macbook Pro 2022 M2 hiện đang được kinh doanh tại Thế Giới Di Động v&agrave; Điện M&aacute;y Xanh với gi&aacute; <strong>35.990.000</strong> <strong>đồng</strong> ở cả 2 phi&ecirc;n bản m&agrave;u bạc v&agrave; x&aacute;m.</p>\r\n<p>Bạn ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m về chất lượng sản phẩm khi mua h&agrave;ng tại Thế Giới Di Động hay Điện m&aacute;y Xanh, đi k&egrave;m theo đ&oacute; l&agrave; những ưu đ&atilde;i hấp dẫn v&agrave; chế độ bảo h&agrave;nh tốt tr&ecirc;n to&agrave;n quốc.&nbsp;</p>\r\n<h3>Macbook Pro M2 2022 khi n&agrave;o ra mắt?</h3>\r\n<p>Hiện tại m&aacute;y đang được mở b&aacute;n tại c&aacute;c trang web của&nbsp;Thế Giới Di Động, Điện M&aacute;y Xanh&nbsp;nhưng theo dự kiến sẽ được ch&iacute;nh thức l&ecirc;n kệ v&agrave;o khoảng cuối th&aacute;ng <strong>07/2022</strong>. H&atilde;y c&ugrave;ng ch&uacute;ng m&igrave;nh đ&oacute;n chờ si&ecirc;u phẩm n&agrave;y nh&eacute;!</p>\r\n<p>Tổng kết lại, laptop&nbsp;MacBook Pro M2&nbsp;2022 sẽ đem đến cho người d&ugrave;ng sự cộng hưởng trong thiết kế đẳng cấp v&agrave; hiệu năng vượt trội. Tất cả t&iacute;nh năng t&acirc;n tiến sẽ được Apple &aacute;p dụng, phục vụ tối ưu nhất cho c&ocirc;ng việc s&aacute;ng tạo thiết kế đồ họa n&acirc;ng cao trong chiếc laptop mỏng nhẹ.</p>', 'Thiết kế thanh lịch, sang trọng cùng hiệu năng vượt trội đến từ vi xử lý tân tiến Apple M2 có trong Macbook Pro M2, hứa hẹn sẽ mang lại nhiều giá trị cho người dùng sáng tạo, phục vụ tốt nhu cầu thiết kế đồ hoạ nâng cao.', 'CPU: Apple M2100GB/s\r\nRAM: 8 GB\r\nỔ cứng: 256 GB SSD\r\nMàn hình: 13.3\"Retina (2560 x 1600)\r\nCard màn hình: Card tích hợp10 nhân GPU\r\nCổng kết nối: 2 x Thunderbolt 3Jack tai nghe 3.5 mm\r\nĐặc biệt: Có đèn bàn phím\r\nHệ điều hành: Mac OS', 100, 33350000, 35990000, '2022-08-05 13:47:03', '2022-08-05 13:47:03', NULL, 'laptop-apple-macbook-pro-m2-2022', 0, 0, 'Sản phẩm,Macbook,M2'),
(31, 9, 15, 'Laptop Apple MacBook Pro 16 M1', 'https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-600x600.jpg', '<h3>Thật ấn tượng với<a title=\" Apple MacBook Pro 16 M1 Max 2021 b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop/apple-macbook-pro-16-m1-max-2021#top-tskt\" target=\"_blank\" rel=\"noopener\">&nbsp;Apple MacBook Pro 16 M1 Max 2021</a> mang tr&ecirc;n m&igrave;nh \"bộ &aacute;o mới\" độc đ&aacute;o, cuốn h&uacute;t mọi &aacute;nh nh&igrave;n c&ugrave;ng m&agrave;n h&igrave;nh tai thỏ lần đầu ti&ecirc;n xuất hiện ở d&ograve;ng Mac v&agrave; ẩn b&ecirc;n trong l&agrave; bộ cấu h&igrave;nh mạnh mẽ tuyệt vời đến từ con chip&nbsp;M1 Max t&acirc;n tiến.</h3>\r\n<h3>Hiệu năng vượt trội, đỉnh cao, c&acirc;n mọi t&aacute;c vụ</h3>\r\n<p><a title=\"C&aacute;c sản phẩm Macbook Pro 16 inch hiện đang b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-apple-macbook-pro-16-inch\" target=\"_blank\" rel=\"noopener\">MacBook Pro 16 inch</a>&nbsp;với những cải tiến vượt bậc về mặt hiệu năng, hứa hẹn gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm mượt m&agrave; trong c&aacute;c t&aacute;c vụ nặng như chỉnh sửa h&igrave;nh ảnh phức tạp, render video,... hướng đến đối tượng người d&ugrave;ng c&oacute; nhu cầu sản xuất, s&aacute;ng tạo nội dung, kỹ thuật, c&ocirc;n nghệ chuy&ecirc;n nghiệp.</p>\r\n<p>Con chip&nbsp;<a title=\"T&igrave;m hiểu chip M1 Max\" href=\"https://www.thegioididong.com/tin-tuc/apple-m1-max-la-gi-1391498\" target=\"_blank\" rel=\"noopener\">Apple M1 Max</a> mang một sức mạnh si&ecirc;u cấp với cấu tr&uacute;c <strong>10 nh&acirc;n</strong> v&agrave; tốc độ băng trong l&ecirc;n đến&nbsp;<strong>400 GB/s memory bandwidth </strong>cho hiệu&nbsp;suất của Apple nhanh hơn khoảng <strong>70% </strong>so với thế hệ tiền nhiệm Apple M1,&nbsp;từ đ&oacute; mang lại cho bạn một tốc độ xử l&yacute; đ&aacute;ng kinh ngạc gi&uacute;p giải quyết tốt từ c&aacute;c c&ocirc;ng việc văn ph&ograve;ng cơ bản đến phức tạp tr&ecirc;n c&aacute;c phần mềm Office 365 cũng như đồ họa chuy&ecirc;n nghiệp tr&ecirc;n Figma, Photoshop, AI, Pr, AutoCAD,...</p>\r\n<p>T&iacute;ch hợp với bộ CPU hiện đại tr&ecirc;n l&agrave; card đồ họa<strong> GPU&nbsp;32 nh&acirc;n</strong>&nbsp;cho hiệu suất nhanh hơn gấp <strong>4 lần</strong> so với M1, năng cao khả năng xử l&yacute; đồ họa, thoả m&atilde;n niềm đam m&ecirc; s&aacute;ng tạo cho bạn, thiết kế 2D, 3D hiệu quả, render video tuyệt vời, đồng thời tiết kiệm điện năng đ&aacute;ng kể, thấp hơn&nbsp;<strong>70%</strong> so với chip 8 nh&acirc;n của PC/laptop.</p>\r\n<p>Bộ nhớ <a title=\"Xem th&ecirc;m laptop 32 Gb b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-32-gb\" target=\"_blank\" rel=\"noopener\">RAM 32 GB</a> đa nhiệm cực mượt m&agrave;, tăng tốc độ truy xuất dữ liệu, giảm thời gian chờ, mọi thao t&aacute;c được phản hồi tức thời, bạn c&oacute; thể dễ d&agrave;ng mở nhiều phần mềm thiết kế để chỉnh sửa nhiều tệp h&igrave;nh ảnh phức tạp vừa render.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-4-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro 16 M1 Max 2021/32 core-GPU - Cấu h&igrave;nh\" src=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-4-1.jpg\" alt=\"MacBook Pro 16 M1 Max 2021/32 core-GPU - Cấu h&igrave;nh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-4-1.jpg\" /></a></p>\r\n<p>Dung lượng khủng <a title=\"Xem th&ecirc;m laptop SSD 1 TB b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-ssd-1-tb\" target=\"_blank\" rel=\"noopener\">SSD 1 TB</a> mang đến kh&ocirc;ng gian bộ nhớ rộng r&atilde;i, thoải m&aacute;i cho bạn lưu trữ t&agrave;i liệu c&aacute; nh&acirc;n, kho h&igrave;nh ảnh, video s&aacute;ng tạo của bản th&acirc;n v&agrave; h&agrave;ng loạt bộ phim hấp dẫn m&agrave; bạn y&ecirc;u th&iacute;ch, đồng thời&nbsp;mở video 8K ngay lập tức hoặc lưu trữ h&agrave;ng trăm ảnh RAW c&ugrave;ng một l&uacute;c.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-5-3.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro 16 M1 Max 2021/32 core-GPU - SSD\" src=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-5-3.jpg\" alt=\"MacBook Pro 16 M1 Max 2021/32 core-GPU - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-5-3.jpg\" /></a></p>\r\n<p>Hệ điều h&agrave;nh <strong>Mac OS </strong>gi&uacute;p giao diện m&aacute;y t&iacute;nh c&oacute; độ thẩm mỹ cao, tạo cảm hứng l&agrave;m việc ngay cho người d&ugrave;ng mỗi khi bật m&aacute;y l&ecirc;n, tiện thao t&aacute;c, dễ sử dụng v&agrave; kho ứng dụng đồ sộ.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-8.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"MacBook Pro 16 M1 Max 2021/32 core-GPU - macOS Big Sur\" src=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-8.jpg\" alt=\"MacBook Pro 16 M1 Max 2021/32 core-GPU - macOS Big Sur\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/253582/apple-macbook-pro-16-m1-max-2021-8.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, hệ thống tản nhiệt ti&ecirc;n tiến chuyển lưu kh&ocirc;ng kh&iacute; nhiều <strong>hơn 50%</strong>, ngay cả ở tốc độ quạt thấp hơn gi&uacute;p&nbsp;laptop lu&ocirc;n trong trạng th&aacute;i m&aacute;t mẻ, ổn định, hiệu quả l&agrave;m việc cao cả khi hoạt động li&ecirc;n tục suốt ng&agrave;y d&agrave;i.</p>\r\n<h3>Kh&ocirc;ng gian hiển thị rộng cho h&igrave;nh ảnh si&ecirc;u n&eacute;t</h3>\r\n<p><strong>M&agrave;n h&igrave;nh 16.2 inch</strong> viền mỏng, thiết kế tai thỏ độc đ&aacute;o, bắt mắt lần đầu ti&ecirc;n xuất hiện ở MacBook với tấm nền <strong>Liquid Retina XDR display</strong> c&oacute; độ ph&acirc;n giải <strong>(3456 x 2234)</strong> được hiệu chuẩn, tham chiếu chuy&ecirc;n nghiệp cung cấp h&igrave;nh ảnh cực sắc n&eacute;t đến từng chi tiết, đảm bảo mọi hiển thị trở n&ecirc;n ch&acirc;n thực, đ&atilde; mắt.&nbsp;</p>\r\n<p>Tần số qu&eacute;t m&agrave;n h&igrave;nh<strong> l&ecirc;n đến 120 Hz </strong>tăng tốc độ l&agrave;m tươi,&nbsp;tự động điều chỉnh để ph&ugrave; hợp với chuyển động của nội dung hiển thị, mọi hoạt cảnh phim mượt m&agrave;, bắt mắt, đạt hiệu quả r&otilde; rệt khi thực hiện c&aacute;c lệnh t&ugrave;y chỉnh tr&ecirc;n c&aacute;c ứng dụng đồ họa cũng như khả năng&nbsp;chiến game giải tr&iacute; ở mức c&aacute;i đặt trung b&igrave;nh - cao cực đ&atilde;.</p>\r\n<p>Dải m&agrave;u&nbsp;<strong>Wide color (P3)</strong> với <strong>1 tỷ m&agrave;u</strong> t&aacute;i tạo m&agrave;u sắc tốt, độ tương phản cao cho m&agrave;u sắc lu&ocirc;n tươi mới rực rỡ,&nbsp;độ s&aacute;ng m&agrave;n h&igrave;nh lớn mang đến những khung h&igrave;nh trải nghiệm hết sức sinh động, thật tựa như cuộc sống. B&ecirc;n cạnh đ&oacute;,&nbsp;c&ocirc;ng nghệ <strong>True Tone</strong> tự động điều chỉnh m&agrave;u sắc v&agrave; độ s&aacute;ng sao cho ph&ugrave; hợp với m&ocirc;i trường đang sử dụng, đảm bảo chất lượng h&igrave;nh ảnh lu&ocirc;n hiển thị ch&iacute;nh x&aacute;c, ch&acirc;n thực nhất.</p>\r\n<p><strong>Hệ thống &acirc;m thanh 6 loa </strong>bao gồm <strong>4 loa trầm</strong> triệt ti&ecirc;u lực cho &acirc;m sắc s&acirc;u hơn, lấp đầy căn ph&ograve;ng với &acirc;m trầm nhiều hơn tới<strong> 80%</strong> v&agrave; <strong>2 loa tweeter </strong>hiệu suất cao ph&aacute;t ra giọng h&aacute;t r&otilde; r&agrave;ng hơn, đầy đủ, sống động hơn. <strong>Wide stereo sound</strong> kết hợp c&ocirc;ng nghệ&nbsp;<a title=\"T&igrave;m hiểu Dolby Atmos\" href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-dolby-atmos-tren-smartphone-772074#hmenuid3\" target=\"_blank\" rel=\"noopener\">Dolby Atmos</a> gi&uacute;p bạn&nbsp;đắm ch&igrave;m trong &acirc;m nhạc, phim ảnh với &acirc;m thanh r&otilde; r&agrave;ng, chi tiết, đầy đủ chiều rộng v&agrave; chiều s&acirc;u.</p>\r\n<p>Trang bị<strong> 3 micro </strong>chất lượng ph&ograve;ng thu c&oacute; độ ồn thấp hơn tới <strong>60%</strong> ghi lại ngay cả những &acirc;m thanh tinh tế nhất, định hướng ch&ugrave;m tia để giọng n&oacute;i của bạn lu&ocirc;n to v&agrave; r&otilde; r&agrave;ng v&agrave;&nbsp;<strong>FaceTime HD camera</strong> độ ph&acirc;n giải&nbsp;<strong>1080p</strong> với ống k&iacute;nh c&oacute; độ rộng hơn cho ph&eacute;p thu h&igrave;nh ảnh trực tuyến chất lượng khi thực hiện c&aacute;c cuộc gọi video qua Google Meet, Line, Zoom,... hỗ trợ học tập, l&agrave;m việc từ xa hiệu quả tối ưu.</p>\r\n<h3>Thiết kế hiện đại hơn, cuốn h&uacute;t hơn</h3>\r\n<p>Chiếc <a title=\"Xem th&ecirc;m MacBook b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" rel=\"noopener\">Apple MacBook </a><strong>M1 Max 16 </strong>n&agrave;y được chế t&aacute;c ho&agrave;n hảo từ kim loại nguy&ecirc;n khối bằng nh&ocirc;m t&aacute;i chế th&acirc;n thiện với m&ocirc;i trường, bền bỉ theo thời gian, kiểu d&aacute;ng mới lạ so với những d&ograve;ng Mac trước đ&acirc;y, đơn giản m&agrave; đầy tinh tế, t&iacute;nh thẩm mỹ cao, t&ocirc;n l&ecirc;n sự sang trọng v&agrave; cuốn h&uacute;t cho chủ sở hữu.</p>\r\n<p>Tuy&nbsp;<a title=\"Xem th&ecirc;m laptop b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" rel=\"noopener\">laptop</a> c&oacute; độ d&agrave;y <strong>16.8 mm </strong>v&agrave; trọng lượng <strong>2.2 kg </strong>nhưng&nbsp;vẫn gọn g&agrave;ng trong ph&acirc;n kh&uacute;c <a title=\"Xem th&ecirc;m laptop cao cấp b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" rel=\"noopener\">laptop cao cấp </a>chuy&ecirc;n dụng cho d&acirc;n kỹ thuật - đồ họa, bỏ vừa vặn trong balo, t&uacute;i x&aacute;ch, thuận tiện để bạn mang theo mọi l&uacute;c mọi nơi để phục vụ cho c&ocirc;ng việc cũng như giải tr&iacute; h&agrave;ng ng&agrave;y đều ho&agrave;n hảo.</p>\r\n<p><strong>Magic Keyboard</strong>&nbsp;được thiết kế mới v&agrave; loại bỏ được c&aacute;c khuyết điểm so với thế hệ trước, khoảng c&aacute;ch giữ c&aacute;c ph&iacute;m rộng r&atilde;i, b&agrave;n ph&iacute;m c&oacute; độ nảy cao với cơ chế cắt k&eacute;o tinh tế, h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u cho trải nghiệm thao t&aacute;c nhập liệu tốt nhất với tốc độ đ&aacute;nh m&aacute;y nhanh, &ecirc;m v&agrave; chuẩn x&aacute;c. Ngo&agrave;i ra c&ograve;n hỗ trợ <strong>đ&egrave;n nền</strong> để bạn tiện sử dụng trong điều kiện thiếu s&aacute;ng.</p>\r\n<p>Một trong những điểm mới ở đ&acirc;y l&agrave; b&agrave;n ph&iacute;m lần đầu ti&ecirc;n được t&iacute;ch hợp một h&agrave;ng <strong>ph&iacute;m chức năng F</strong> bao gồm:&nbsp;Spotlight, Siri, Dictation v&agrave; Do Not Disturb gi&uacute;p bạn thực hiện c&aacute;c ph&iacute;m tắt/mở nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng hơn bao giờ hết.</p>\r\n<h3>Đa dạng kết nối, bảo mật cao</h3>\r\n<p><a title=\"T&igrave;m hiểu về TouchID\" href=\"https://www.thegioididong.com/hoi-dap/fingerprint-sensor-la-gi-va-co-tac-dung-gi-904389\" target=\"_blank\" rel=\"noopener\">Bảo mật v&acirc;n tay</a><strong>&nbsp;</strong>c&oacute; một v&ograve;ng x&uacute;c gi&aacute;c&nbsp;hướng dẫn ng&oacute;n tay mở kh&oacute;a m&aacute;y Mac của bạn một c&aacute;ch nhanh ch&oacute;ng, dễ d&agrave;ng v&agrave; an to&agrave;n th&ocirc;ng tin chỉ với một chạm cũng như đăng nhập v&agrave;o ứng dụng hay thanh to&aacute;n online,... một c&aacute;ch bảo mật cao, dữ liệu được bảo vệ tối ưu.</p>\r\n<p>Kết nối với TV hoặc m&agrave;n h&igrave;nh rời qua cổng HDMI, Jack cắm taii nghe 3.5 mm ph&aacute;t hiện v&agrave; tự động điều chỉnh cho tai nghe trở kh&aacute;ng cao, kết nối với c&aacute;c thiết bị ngoại vi hoặc m&agrave;n h&igrave;nh tốc độ cao với ba cổng Thunderbolt 4 v&agrave; sạc nhanh bằng cổng <strong>MagSafe 3</strong>,&nbsp;tiết kiệm thời gian chờ.</p>\r\n<p>Hai chuẩn kết nối kh&ocirc;ng d&acirc;y&nbsp;<strong>Bluetooth 5.0</strong> v&agrave; <a title=\"T&igrave;m hiểu Wi-Fi 6\" href=\"https://www.thegioididong.com/hoi-dap/wi-fi-chuan-80211ax-la-gi-tim-hieu-ve-wi-fi-the-he-thu-6-1187524\" target=\"_blank\" rel=\"noopener\">Wi-Fi 6 (802.11ax)</a>&nbsp;cho bạn băng tần cao <strong>9.6 Gbps</strong>, với tốc độ tăng <strong>40% </strong>từ đ&oacute; gi&uacute;p người d&ugrave;ng c&oacute; thể xem được c&aacute;c video 4K một c&aacute;ch mượt m&agrave;.</p>\r\n<p><a title=\"MacBook Pro 16 inch M1 Max 2021 32GB/1TB SSD hiện đang kinh doanh tại Topzone.vn\" href=\"https://www.topzone.vn/mac/macbook-pro-16-inch-m1-max-2021\" target=\"_blank\" rel=\"noopener\">MacBook Pro 16 inch 2021</a> mang bộ cấu h&igrave;nh khủng, đỉnh cao c&ugrave;ng thiết kế được cải tiến, hiện đại v&agrave; đẳng cấp hơn, xứng đ&aacute;ng l&agrave; một chiếc<a title=\"Xem th&ecirc;m laptop đồ họa - kỹ thuật b&aacute;n tại thegioididong.com\" href=\"https://www.thegioididong.com/laptop?g=do-hoa-ky-thuat\" target=\"_blank\" rel=\"noopener\"> laptop đồ họa - kỹ thuật</a> chuy&ecirc;n nghiệp, l&yacute; tưởng d&agrave;nh cho giới c&ocirc;ng nghệ v&agrave; s&aacute;ng tạo nội dung.</p>', 'Thật ấn tượng với Apple MacBook Pro 16 M1 Max 2021 mang trên mình \"bộ áo mới\" độc đáo, cuốn hút mọi ánh nhìn cùng màn hình tai thỏ lần đầu tiên xuất hiện ở dòng Mac và ẩn bên trong là bộ cấu hình mạnh mẽ tuyệt vời đến từ con chip M1 Max tân tiến.', 'CPU: Apple M1 Max400GB/s memory bandwidth\r\nRAM: 32 GB\r\nỔ cứng: 1 TB SSD\r\nMàn hình: 16.2\"Liquid Retina XDR display (3456 x 2234)120Hz\r\nCard màn hình: Card tích hợp32 nhân GPU\r\nCổng kết nối: 3 x Thunderbolt 4 USB-CHDMIJack tai nghe 3.5 mm\r\nĐặc biệt: Có đèn bàn phím\r\nHệ điều hành: Mac OS', 100, 90990000, 90990000, '2022-08-05 13:49:34', '2022-08-05 13:49:34', NULL, 'laptop-apple-macbook-pro-16-m1', 0, 0, 'Sản phẩm');

